mapper.select:
SELECT  /* DeviceMapper.getUsermbr, 사용자 정보(키,아이디) 조회, 황정택/와이즈스톤, 2013-12-09 */
        insd_usermbr_no AS userKey
        , mbr_id AS userID
  FROM  tb_us_usermbr
 WHERE  tenant_id = #{tenantID}

   AND  insd_usermbr_no = #{userKey}


   AND  mbr_id = #{userID}


   AND  usermbr_no = #{imMbrNo}

SELECT  /* DeviceMapper.searchDeviceList, 휴대기기 목록 조회, 황정택/와이즈스톤, 2013-12-05 */
        DISTINCT(l.userKey) AS userKey
     ,  l.userID
FROM (
        SELECT  a.insd_usermbr_no AS userKey
             ,  a.mbr_id AS userID
          FROM  tb_us_usermbr a
         WHERE  a.tenant_id = #{commonRequest.tenantID}
         AND    a.mbr_status_main_cd != 'US010202'
   
       
           a.insd_usermbr_no = #{keySearch.keyString}
       
       
           a.mbr_id = #{keySearch.keyString}
       
       
           a.usermbr_no = #{keySearch.keyString}
       
       
           a.intg_svc_no = #{keySearch.keyString}
                             
   
    ) l

SELECT /* DeviceMapper.searchDeviceListD, 휴대기기 목록 조회(휴대기기 정보로 조회), 황정택/와이즈스톤, 2013-12-05 */ DISTINCT(l.userKey) AS userKey, l.userID AS userID FROM ( SELECT a.insd_usermbr_no AS userKey , a.mbr_id AS userID FROM tb_us_usermbr a LEFT JOIN tb_us_usermbr_device b ON a.insd_usermbr_no = b.insd_usermbr_no AND a.tenant_id = b.tenant_id WHERE a.tenant_id = #{commonRequest.tenantID} AND b.auth_yn = 'Y'
b.device_id = #{keySearch.keyString}
b.insd_device_id = #{keySearch.keyString}
ORDER BY b.auth_dt DESC) l WHERE ROWNUM = 1

SELECT  /* DeviceMapper.searchDeviceList2, 휴대기기 목록 조회, 황정택/와이즈스톤, 2013-12-05 */
        DISTINCT(l.userKey) AS userKey
        , #{commonRequest.systemID} AS userID
        , #{commonRequest.tenantID} AS tenantID
        , #{isMainDevice} AS isMainDevice
  FROM  (
          SELECT  a.insd_usermbr_no AS userKey
            FROM  tb_us_usermbr a
           WHERE  a.tenant_id = #{commonRequest.tenantID}
           AND    a.mbr_status_main_cd != 'US010202'
   
       
           a.insd_usermbr_no = #{keySearch.keyString}
       
       
           a.mbr_id = #{keySearch.keyString}
       
       
           a.usermbr_no = #{keySearch.keyString}
       
       
           a.intg_svc_no = #{keySearch.keyString}
                             
   
    ) l

SELECT /* DeviceMapper.searchDeviceList2D, 휴대기기 목록 조회(휴대기기 정보로 조회), 황정택/와이즈스톤, 2013-12-05 */ DISTINCT(l.userKey) AS userKey , #{commonRequest.systemID} AS userID , #{commonRequest.tenantID} AS tenantID , #{isMainDevice} AS isMainDevice FROM ( SELECT a.insd_usermbr_no AS userKey FROM tb_us_usermbr a LEFT JOIN tb_us_usermbr_device b ON a.insd_usermbr_no = b.insd_usermbr_no AND a.tenant_id = b.tenant_id WHERE a.tenant_id = #{commonRequest.tenantID} AND b.auth_yn = 'Y' AND b.tenant_id = #{commonRequest.tenantID}
b.device_id = #{keySearch.keyString}
b.insd_device_id = #{keySearch.keyString}
ORDER BY b.auth_dt DESC) l WHERE ROWNUM = 1

SELECT /* DeviceMapper.getDevice, 휴대기기 단건 조회, 황정택/와이즈스톤, 2013-12-05 */ tenant_id , insd_usermbr_no , insd_device_id , device_id , device_model_cd , mno_cd , device_alias , rep_device_yn , TO_CHAR( auth_dt,'YYYYMMDDHH24MISS') AS auth_dt , TO_CHAR( upd_dt,'YYYYMMDDHH24MISS') , sms_recv_yn , device_natv_id , device_acct , svc_mang_no , entry_chnl_cd FROM tb_us_usermbr_device WHERE tenant_id = #{tenantID} AND auth_yn = 'Y' AND insd_usermbr_no = #{userKey}
AND rep_device_yn = #{isMainDevice}
AND insd_device_id = #{keyString}
AND device_id = #{keyString}
AND svc_mang_no = #{keyString}

SELECT  /* DeviceMapper.xml, getExtraProfile, SAC(SC), 김경복, 2015-05-27 */
       device_mang_item_cd extraProfile,
       reg_value extraProfileValue,
       TO_CHAR (reg_dt +7, 'YYYYMMDDHH24MISS') AS regDatePlus7,
       TO_CHAR (upd_dt +7, 'YYYYMMDDHH24MISS') AS updateDatePlue7,
       TO_CHAR (SYSDATE, 'YYYYMMDDHH24MISS') AS nowDate
  FROM tb_us_usermbr_device_dtl
 WHERE tenant_id = #{tenantID} 
	 AND insd_usermbr_no = #{userKey}
	 AND insd_device_id = #{deviceKey}

SELECT  /* DeviceMapper.searchDevice, 휴대기기 개별 조회, 황정택/와이즈스톤, 2013-12-05 */
        #{userKey} AS userKey
     , (
            SELECT  mbr_id
              FROM  tb_us_usermbr
             WHERE  insd_usermbr_no = #{userKey}
               AND  tenant_id = #{commonRequest.tenantID}
        ) AS userID
     ,  #{commonRequest.tenantID} AS tenantID
     ,  'INSD_DEVICE_ID' AS keyType
     ,  u.insd_device_id AS keyString
  FROM  
        (
         SELECT  insd_device_id
           FROM  tb_us_usermbr_device
          WHERE  tenant_id = #{commonRequest.tenantID}
            AND  insd_usermbr_no = #{userKey}
            AND  auth_yn = 'Y' 
       
           AND insd_device_id =  #{keySearch.keyString} 
           AND device_id =  #{keySearch.keyString} 
       
        ) u

SELECT /* DeviceMapper.searchDeviceSvcMangNo, (서비스 관리번호), 황정택/와이즈스톤, 2013-12-05 */ l.insd_usermbr_no AS userKey , l.mbr_id AS userID , #{commonRequest.tenantID} AS tenantID , 'SVC_MANG_NO' AS keyType , l.svc_mang_no AS keyString FROM ( SELECT b.insd_device_id AS insd_device_id , a.mbr_id AS mbr_id , a.insd_usermbr_no AS insd_usermbr_no , b.svc_mang_no as svc_mang_no FROM tb_us_usermbr_device b JOIN tb_us_usermbr a ON b.tenant_id = a.tenant_id AND b.insd_usermbr_no = a.insd_usermbr_no WHERE 1=1 AND b.tenant_id = #{commonRequest.tenantID} AND b.auth_yn = 'Y'
AND b.svc_mang_no = #{keySearch.keyString}
ORDER BY b.auth_dt DESC) l WHERE ROWNUM = 1

SELECT /* DeviceMapper.searchDeviceIDOnly, (Device ID only), 황정택/와이즈스톤, 2013-12-05 */ l.insd_usermbr_no AS userKey , l.mbr_id AS userID , #{commonRequest.tenantID} AS tenantID , 'DEVICE_ID' AS keyType , l.device_id AS keyString FROM ( SELECT b.insd_device_id AS insd_device_id , a.mbr_id AS mbr_id , a.insd_usermbr_no AS insd_usermbr_no , b.device_id as device_id FROM tb_us_usermbr a, tb_us_usermbr_device b WHERE a.insd_usermbr_no = b.insd_usermbr_no AND a.tenant_id = #{commonRequest.tenantID} AND b.tenant_id = #{commonRequest.tenantID} AND b.auth_yn = 'Y'
AND b.device_id = #{keySearch.keyString}
ORDER BY b.auth_dt DESC) l WHERE ROWNUM = 1

mapper.update:
UPDATE /* DeviceMapper.unsetDeviceYn, 속성의 rep_device_yn = N, 황정택/와이즈스톤, 2013-12-05 */
tb_us_usermbr_device
   SET rep_device_yn = 'N',
       start_dt = SYSDATE,
       end_dt = TO_DATE('29991231235959', 'YYYYMMDDHH24MISS'),
       upd_dt = SYSDATE
 WHERE tenant_id = #{tenantID}
    AND insd_usermbr_no = #{userKey}
    AND device_id = #{deviceID}
   AND auth_yn = 'Y'

UPDATE /* DeviceMapper.setMainDevice, 속성의 rep_device_yn = Y, 황정택/와이즈스톤, 2013-12-05 */
tb_us_usermbr_device
   SET rep_device_yn = 'Y'
 WHERE tenant_id = #{commonRequest.tenantID}
   AND insd_usermbr_no = #{userKey}
   AND insd_device_id  = #{deviceKey}
   AND auth_yn = 'Y'

UPDATE /* DeviceMapper.removeDotori, 도토리 인증여부 초기화, 황정택/와이즈스톤, 2013-12-20 */
tb_us_usermbr_device_dtl
   SET reg_value = #{extraProfileValue}
     , upd_dt = SYSDATE
 WHERE tenant_id = #{tenantID}
   AND insd_usermbr_no = #{userKey}
   AND insd_device_id = #{deviceKey}
   AND device_mang_item_cd = #{extraProfile}

mapper.select:
SELECT /* DeviceMapper.isRegistered, 사용자 ID 조회, 황정택/와이즈스톤, 2013-12-23 */
       insd_device_id AS deviceKey
FROM tb_us_usermbr_device
WHERE tenant_id = #{commonRequest.tenantID}
       AND device_id = #{userMbrDevice.deviceID}
       AND auth_yn = 'Y'
       AND insd_usermbr_no = #{userKey}

SELECT /* DeviceMapper.isRegisteredKey, 휴대기기 Key 조회(userKey, deviceKey로 조회), 황정택/와이즈스톤, 2013-12-23 */
       insd_device_id AS deviceKey
FROM tb_us_usermbr_device
WHERE tenant_id = #{tenantID}
       AND insd_device_id = #{deviceKey}
       AND auth_yn = 'Y'
AND insd_usermbr_no = #{userKey}

mapper.update:
UPDATE /* DeviceMapper.updateDevice, 휴대기기 등록, 황정택/와이즈스톤, 2013-12-23 */
tb_us_usermbr_device 
       SET upd_dt = SYSDATE,
       start_dt = SYSDATE
       ,mno_cd = #{deviceTelecom} 
       ,device_alias = #{deviceNickName} 
       ,rep_device_yn = #{isPrimary} 
       ,auth_dt = TO_DATE(#{authenticationDate},'YYYYMMDDHH24MISS') 
       ,sms_recv_yn = #{isRecvSMS} 
       ,device_natv_id = #{nativeID} 
       ,device_acct = #{deviceAccount} 
       ,svc_mang_no = #{svcMangNum} 
       ,entry_chnl_cd = #{joinId} 
       ,device_id = #{deviceID} 
       ,device_model_cd = #{deviceModelNo} 
       
       ,chg_case_cd = #{changeCaseCode} 
       ,chg_case_cd = 'US012001' 
       
WHERE tenant_id = #{tenantID}
  AND insd_usermbr_no = #{userKey}
  AND insd_device_id = #{deviceKey}
  AND auth_yn = 'Y'

UPDATE /* DeviceMapper.updateDeviceCode, 휴대기기 등록, 황정택/와이즈스톤, 2013-12-23 */
tb_us_usermbr_device 
       SET upd_dt = SYSDATE,
       start_dt = SYSDATE,
       chg_case_cd = null
       ,mno_cd = #{deviceTelecom} 
       ,device_alias = #{deviceNickName} 
       ,rep_device_yn = #{isPrimary} 
       ,auth_dt = TO_DATE(#{authenticationDate},'YYYYMMDDHH24MISS') 
       ,sms_recv_yn = #{isRecvSMS} 
       ,device_natv_id = #{nativeID} 
       ,device_acct = #{deviceAccount} 
       ,svc_mang_no = #{svcMangNum} 
       ,entry_chnl_cd = #{joinId} 
       ,device_id = #{deviceID} 
       ,device_model_cd = #{deviceModelNo} 
WHERE tenant_id = #{tenantID}
  AND insd_usermbr_no = #{userKey}
  AND insd_device_id = #{deviceKey}
  AND auth_yn = 'Y'

UPDATE /* DeviceMapper.updateCountPlus,  휴대기기 보유 카운트 증가, 황정택/와이즈스톤, 2013-12-26 */
tb_us_usermbr
   SET device_reg_cnt = device_reg_cnt+1,
       upd_dt = SYSDATE
WHERE tenant_id = #{commonRequest.tenantID}
      AND insd_usermbr_no = #{userKey}

UPDATE /* DeviceMapper.updateCountMinus,  휴대기기 보유 카운트 감소, 황정택/와이즈스톤, 2013-12-26 */
tb_us_usermbr
   SET device_reg_cnt = device_reg_cnt-1,
       upd_dt = SYSDATE
WHERE tenant_id = #{tenantID}
      AND insd_usermbr_no = #{userKey}

UPDATE /* DeviceMapper.updateCountDevice,  휴대기기 보유 카운트 증가, 황정택/와이즈스톤, 2013-12-26 */
tb_us_usermbr
   SET device_reg_cnt = (SELECT COUNT(*)
                         FROM tb_us_usermbr_device 
                         WHERE tenant_id = #{commonRequest.tenantID} 
                               AND insd_usermbr_no = #{userKey} 
                               AND auth_yn = 'Y'),
       upd_dt = SYSDATE
WHERE tenant_id = #{commonRequest.tenantID}
      AND insd_usermbr_no = #{userKey}

UPDATE /* DeviceMapper.updateCountDevice2,  휴대기기 보유 카운트 증가, 황정택/와이즈스톤, 2013-12-26 */
tb_us_usermbr
   SET device_reg_cnt = (SELECT COUNT(*)
                         FROM tb_us_usermbr_device 
                         WHERE tenant_id = #{tenantID} 
                               AND insd_usermbr_no = #{userKey} 
                               AND auth_yn = 'Y'),
       upd_dt = SYSDATE
WHERE tenant_id = #{tenantID}
      AND insd_usermbr_no = #{userKey}

UPDATE /* DeviceMapper.doCancelDevice, 속성의 auth_yn = N, 황정택/와이즈스톤, 2013-12-05 */
tb_us_usermbr_device
   SET auth_yn = 'N',
       
       
       end_dt = SYSDATE,
       upd_dt = SYSDATE
 WHERE tenant_id = #{tenantID}
   AND insd_usermbr_no = #{userKey}
   AND device_id = #{deviceID}

UPDATE /* DeviceMapper.doActivateDevice, 속성의 rep_device_yn = N, 황정택/와이즈스톤, 2013-12-05 */
tb_us_usermbr_device
   SET auth_yn = 'Y',
   	  auth_dt = SYSDATE,
       start_dt = SYSDATE,
       end_dt = TO_DATE('29991231235959', 'YYYYMMDDHH24MISS'),
       upd_dt = SYSDATE
       ,rep_device_yn = #{isPrimary} 
       ,mno_cd = #{deviceTelecom} 
        ,device_alias = #{deviceNickName} 
        ,auth_dt = TO_DATE(#{authenticationDate},'YYYYMMDDHH24MISS') 
        ,sms_recv_yn = #{isRecvSMS} 
        ,device_natv_id = #{nativeID} 
        ,device_acct = #{deviceAccount} 
        ,svc_mang_no = #{svcMangNum} 
        ,entry_chnl_cd = #{joinId} 
        ,device_model_cd = #{deviceModelNo} 
 WHERE tenant_id = #{tenantID}
   AND insd_usermbr_no = #{userKey}
   AND insd_device_id = #{deviceKey}

mapper.insert:
        INSERT  /* DeviceMapper.insertUserkeyTrack, 변환추적 등록, 황정택/와이즈스톤, 2014-01-28 */
                INTO tb_us_usermbr_idmbr_trfm_chas (
                seq
                ,pre_tenant_id
                ,pre_insd_usermbr_no
                ,after_tenant_id
                ,after_insd_usermbr_no
                ,enc_device_id
                ,reg_dt
                ,reg_id
        ) VALUES (
                sq_us_usermbr_idmbr_trfm_chas.NEXTVAL
                ,#{preTenantID}
                ,#{preUserKey}
                ,#{afterTenantID}
                ,#{afterUserKey}
                ,PKG_CRYPTO.ENCRYPT(#{deviceID})
                ,SYSDATE
	            ,#{regID}
        )

mapper.update:
UPDATE /* DeviceMapper.updatePrimary, 최신등록기기 대표기기 설정, 황정택/와이즈스톤, 2014-02-03 */
tb_us_usermbr_device
   SET   rep_device_yn = 'Y'
   WHERE tenant_id = #{tenantID}
     AND insd_usermbr_no = #{userKey}
     AND insd_device_id = 
   ( 
   SELECT l.insd_device_id FROM
       ( 
       SELECT insd_device_id 
           FROM tb_us_usermbr_device
          WHERE auth_yn = 'Y'
            AND insd_usermbr_no = #{userKey}
            AND tenant_id = #{tenantID}
       ORDER BY start_dt DESC
       ) l 
   WHERE ROWNUM = 1
   )

mapper.delete:
DELETE  /* DeviceMapper.xml, removeDeviceExtraProfile, SAC(SC), 반범진, 2015-11-24 */
  FROM tb_us_usermbr_device_dtl
 WHERE tenant_id = #{tenantID} 
     AND insd_usermbr_no = #{userKey}
     AND insd_device_id = #{deviceKey}
