mapper.select:
        SELECT /* PurchaseOrderSearchMapper.searchShoppingSpecialPurchaseCount, 쇼핑 특가상품 구매된 건수 조회, 이승택/nTels, 2014-01-22 */
		        NVL(SUM(CEIL(payment_amt / #{specialCouponAmt})), 0) AS cnt
		  FROM  tb_pr_payment
		 WHERE  tenant_id = #{tenantId}
		   AND  prchs_dt >= TRUNC(SYSDATE, 'DD')
		   AND  payment_mtd_cd = 'OR000606'
		   AND  cpn_id = #{specialCouponId}
		   AND  status_cd = 'OR000301'
		UNION ALL
		SELECT
		        NVL(SUM(CEIL(payment_amt / #{specialCouponAmt})), 0) AS cnt
		  FROM  tb_pr_payment
		 WHERE  tenant_id = #{tenantId}
		   AND  insd_usermbr_no = #{userKey}
		   AND  insd_device_id = #{deviceKey}
		   AND  prchs_dt >= TRUNC(SYSDATE, 'DD')
		   AND  payment_mtd_cd = 'OR000606'
		   AND  cpn_id = #{specialCouponId}
		   AND  status_cd = 'OR000301'
		UNION ALL
		SELECT
		        NVL(SUM(CEIL(payment_amt / #{specialCouponAmt})), 0) AS cnt
		  FROM  tb_pr_payment
		 WHERE  tenant_id = #{tenantId}
		   AND  prchs_dt >= TRUNC(SYSDATE, 'MM')
		   AND  payment_mtd_cd = 'OR000606'
		   AND  cpn_id = #{specialCouponId}
		   AND  status_cd = 'OR000301'
		UNION ALL
		SELECT
		        NVL(SUM(CEIL(payment_amt / #{specialCouponAmt})), 0) AS cnt
		  FROM  tb_pr_payment
		 WHERE  tenant_id = #{tenantId}
		   AND  insd_usermbr_no = #{userKey}
		   AND  insd_device_id = #{deviceKey}
		   AND  prchs_dt >= TRUNC(SYSDATE, 'MM')
		   AND  payment_mtd_cd = 'OR000606'
		   AND  cpn_id = #{specialCouponId}
		   AND  status_cd = 'OR000301'

SELECT /* PurchaseOrderSearchMapper.searchPurchaseDtlListByStatus, 구매상세 목록 조회, 이승택/nTels, 2014-01-22 */ tenant_id , prchs_id , prchs_dtl_id , use_tenant_id , use_insd_usermbr_no , use_insd_device_id , tot_amt , currency_cd , prchs_req_path_cd , client_ip , send_insd_usermbr_no , send_insd_device_id , recv_conf_path_cd , prod_id , prod_amt , prod_qty , tenant_prod_grp_cd , status_cd , use_hiding_yn , cancel_req_path_cd , prchs_case_cd , drm_yn , TO_CHAR(prchs_dt, 'YYYYMMDDHH24MISS') AS prchs_dt , TO_CHAR(use_start_dt, 'YYYYMMDDHH24MISS') AS use_start_dt , TO_CHAR(use_expr_dt, 'YYYYMMDDHH24MISS') AS use_expr_dt , TO_CHAR(dwld_start_dt, 'YYYYMMDDHH24MISS') AS dwld_start_dt , TO_CHAR(dwld_expr_dt, 'YYYYMMDDHH24MISS') AS dwld_expr_dt , TO_CHAR(cancel_dt, 'YYYYMMDDHH24MISS') AS cancel_dt , TO_CHAR(recv_dt, 'YYYYMMDDHH24MISS') AS recv_dt , reg_id , upd_id , resv_col_01 , resv_col_02 , resv_col_03 , resv_col_04 , resv_col_05 , prchs_prod_type , part_chrg_prod_nm , prchs_resv_desc , contents_type , use_fixrate_prod_id , cpn_dlv_url FROM tb_pr_prchs_dtl WHERE 1 = 1
AND use_tenant_id = #{useTenantId} AND use_insd_usermbr_no = #{useInsdUsermbrNo}
AND tenant_id = #{tenantId} AND prchs_id = #{prchsId}
AND status_cd = #{statusCd, jdbcType=VARCHAR}
ORDER BY prchs_dtl_id
