mapper.select:
        /* Purchase.purchaseExist, 기구매체크, 조용진/엔텔스, 2013-12-10 */
        SELECT  
	        
                /*+ INDEX(a PK_PR_PRCHS_DTL) */
	        
	        
                /*+ INDEX(a IDX_PR_PRCHS_DTL_01) */
	        
                prod_id  /* 상품ID */
              , prchs_id  /* 구매ID */
              , TO_CHAR(prchs_dt, 'YYYYMMDDHH24MISS') AS prchs_dt  /* 구매일시 */
              , status_cd  /* 구매상태코드 */
              , use_insd_device_id
              , use_insd_usermbr_no
              , tenant_prod_grp_cd
              , prchs_req_path_cd
              , prchs_case_cd
              , prod_amt
              , TO_CHAR(a.use_expr_dt, 'YYYYMMDDHH24MISS') AS use_expr_dt /* 이용종료일시 */
              , PART_CHRG_PROD_NM
          FROM  tb_pr_prchs_dtl a
         WHERE  1=1
        
           AND  tenant_id = #{tenantId} 
           AND  prchs_id = #{prchsId}
        
        
           AND  use_tenant_id = #{tenantId} 
           AND  use_insd_usermbr_no  = #{userKey} 
        
        
           AND  use_insd_device_id  = #{deviceKey} 
        
        
           AND  prod_id  = #{prodId}
        
        
           AND  tenant_prod_grp_cd  LIKE #{tenantProdGrpCd} ||'%'
                
           and  sysdate >= a.dwld_start_dt
           and  sysdate <= a.dwld_expr_dt

select.foreach:
SELECT /* Purchase.purchaseExistV2, 기구매체크V2, 조용진/엔텔스, 2013-12-10 */ tenant_id , prod_id /* 상품ID */ , prchs_id /* 구매ID */ , TO_CHAR(prchs_dt, 'YYYYMMDDHH24MISS') AS prchs_dt /* 구매일시 */ , status_cd /* 구매상태코드 */ , use_insd_device_id , use_insd_usermbr_no , tenant_prod_grp_cd , prchs_req_path_cd , prchs_case_cd , prod_amt , TO_CHAR(a.use_expr_dt, 'YYYYMMDDHH24MISS') AS use_expr_dt /* 이용종료일시 */ , PART_CHRG_PROD_NM FROM tb_pr_prchs_dtl a WHERE 1=1 AND use_tenant_id = #{item.tenantId} AND use_insd_usermbr_no = #{item.userKey}
#{deviceKey, jdbcType=VARCHAR}
#{prodId, jdbcType=VARCHAR}
and sysdate >= a.dwld_start_dt and sysdate <= a.dwld_expr_dt and status_cd = 'OR000301'
