SELECT /* MileageSaveMapper.searchMileageSave, 마일리지 적립 예상 총 금액 조회, 양주원/nTels, 2014-08-08 */ tenant_id , insd_usermbr_no , prom_id , TO_CHAR(save_dt,'yyyyMMdd') AS save_dt , SUM(save_result_amt) AS saveAmt FROM (SELECT tenant_id , insd_usermbr_no , prom_id , save_dt , save_result_amt FROM tb_pr_membership_reserve WHERE tenant_id = #{tenantId} AND insd_usermbr_no = #{userKey} AND proc_status_cd = #{procStatusCd}
AND save_dt >= TO_DATE(#{standardDt, jdbcType=VARCHAR}, 'YYYYMMDDHH24MISS')
) GROUP BY tenant_id,insd_usermbr_no , prom_id, save_dt

        SELECT  TO_CHAR(save_dt,'YYYYMMDD') AS save_dt
			  , type_cd
              , save_result_amt
              , proc_status_cd
          FROM  tb_pr_membership_reserve
         WHERE  tenant_id = #{tenantId}
			
           AND  type_cd = #{typeCd}
			
           AND  prchs_id = #{prchsId}
