mapper.select:
select  /* PurchaseUserInfo.use_insd_device_id, deviceKey조회, 조용진/엔텔스, 2013-12-10 */
        use_insd_device_id 
  from  tb_pr_prchs_dtl
 where  use_tenant_id = #{tenantId}
   and  use_insd_usermbr_no = #{userKey}
   and  rownum=1
