mapper.select:
	SELECT MENU_ID, 
	       UP_MENU_ID, 
	       MENU_DEPTH 
	  FROM TB_DP_TENANT_MENU
	 WHERE MENU_ID IN (
	    SELECT DISTINCT UP_MENU_ID
	      FROM TB_DP_TENANT_MENU
	     WHERE MENU_ID IN (
	        SELECT UP_MENU_ID
	        FROM TB_DP_TENANT_MENU 
	        WHERE MENU_DEPTH = 3
            AND TENANT_ID = #{tenantId}
            AND SYSTEM_ID = #{systemId}
	        AND USE_YN = 'Y'
	    )
	 )
     AND TENANT_ID = #{tenantId}
     AND SYSTEM_ID = #{systemId}
	 AND USE_YN = 'Y'
