mapper.select:
/* BrandCatalogMapper.xml, SELECT_COUNT_CUDTYPE, 브랜드ID 조회, 김형식/SK플래닛 , 2013-12-30 */
  SELECT 
     COUNT(*) CNT
  FROM TB_DP_SHPG_BRAND
  WHERE ORG_BRAND_ID = #{brandId}

mapper.insert:
      /* BrandCatalogMapper.xml, createTbShpgBrand, 브랜드 테이블에 INSERT, 김형식/SK플래닛  , 2013-12-30 */
         INSERT INTO TB_DP_SHPG_BRAND(
	         BRAND_ID
	        ,MENU_ID
	        ,BRAND_NM
	        ,ORG_BRAND_ID
	        ,REG_ID
	        ,REG_DT
	        ,UPD_ID
	        ,UPD_DT         
         )VALUES(
             #{createBrandId}
            ,#{dpCatNo}
            ,#{brandNm}
            ,#{brandId}
            ,'admin'
            ,SYSDATE
            ,'admin'
            ,SYSDATE             
         )

mapper.update:
/* BrandCatalogMapper.xml, updateTbDpShpgBrand, 브랜드 테이블에 UPDATE, 김형식/SK플래닛  , 2013-12-30 */
 UPDATE 
     TB_DP_SHPG_BRAND
 SET 
     BRAND_NM  = #{brandNm}
   , MENU_ID = #{dpCatNo}
   , UPD_DT    = SYSDATE
 WHERE ORG_BRAND_ID = #{brandId}

mapper.select:
/* BrandCatalogMapper.xml, SELECT_CATALOG_COUNT_CUDTYPE,카탈로그ID 조회, 김형식/SK플래닛 , 2013-12-30 */
SELECT 
     COUNT(*) CNT
FROM TB_DP_SHPG_CATALOG
WHERE ORG_CATALOG_ID = #{catalogId}

/* BrandCatalogMapper.xml, SELECT_BRAND_ID,CATALOG 등록시와 BRAND수정시 생성한 BRAND_ID를 조회해감, 김형식/SK플래닛  , 2013-12-30 */
SELECT
    BRAND_ID
FROM TB_DP_SHPG_BRAND
WHERE ORG_BRAND_ID = #{brandId}

/* BrandCatalogMapper.xml, SELECT_CATALOG_ID,CATALOG 수정시 BPPOC에서 생성한 CATALOG_ID를 조회해감, 김형식/SK플래닛 , 2013-12-30 */
SELECT 
    CATALOG_ID
FROM TB_DP_SHPG_CATALOG
WHERE ORG_CATALOG_ID = #{catalogId}

mapper.insert:
        /* BrandCatalogMapper.xml, createTbDpShpgCatalogDesc,카탈로그 상세 테이블에 INSERT, 김형식/SK플래닛 , 2013-12-30 */
        INSERT INTO TB_DP_SHPG_CATALOG_DESC(
	         CATALOG_ID
	        ,LANG_CD
	        ,CATALOG_NM
	        ,CATALOG_DESC
	        ,CATALOG_DTL_DESC
	        ,REG_ID
	        ,REG_DT
	        ,UPD_ID
	        ,UPD_DT
        )
        VALUES(
             #{createCatalogId}
            ,'ko'
            ,#{catalogNm}
            ,#{introText}
            ,#{catalogDesc}
            ,'admin'
            ,SYSDATE
            ,'admin'
            ,SYSDATE 
        )

mapper.update:
/* BrandCatalogMapper.xml, updateTbDpShpgCatalog,카탈로그 정보 테이블에 UPDATE, 김형식/SK플래닛 , 2013-12-30 */
 UPDATE 
     TB_DP_SHPG_CATALOG
 SET 
     BRAND_ID  = #{createBrandId}
   , UPD_DT    = SYSDATE
 WHERE CATALOG_ID = #{createCatalogId}

/* BrandCatalogMapper.xml, updateTbDpShpgCatalogDesc,카탈로그 상세 테이블에 UPDATE, 김형식/SK플래닛 , 2013-12-30 */
 UPDATE 
     TB_DP_SHPG_CATALOG_DESC
 SET 
     CATALOG_NM  = #{catalogNm}
   , CATALOG_DESC = #{introText}
   , CATALOG_DTL_DESC = #{catalogDesc}
   , UPD_DT    = SYSDATE
 WHERE CATALOG_ID = #{createCatalogId}

/* BrandCatalogMapper.xml, updateTbDpMenuShpgMapg,카탈로그_카테고리 매핑 테이블에 UPDATE, 김형식/SK플래닛 , 2013-12-30 */
 UPDATE 
     TB_DP_MENU_SHPG_MAPG
 SET 
     MENU_ID  = #{dpCatNo}
   , UPD_DT    = SYSDATE
 WHERE CATALOG_ID = #{createCatalogId}

mapper.insert:
       /* BrandCatalogMapper.xml, createBrandCatalogTbDpProdImg,브랜드 카탈로그 이미지 테이블, 김형식/SK플래닛 , 2013-12-30 */
        INSERT INTO TB_DP_PROD_IMG(
	         PROD_ID
	        ,IMG_CD
	        ,EXPO_ORD
	        ,LANG_CD
	        ,FILE_PATH
	        ,FILE_NM
	        ,FILE_SIZE
	        ,REG_ID
	        ,REG_DT
	        ,UPD_ID
	        ,UPD_DT            
        )
          VALUES(
             #{prodId}
            ,#{imgCls}
            ,#{seq}
            ,#{langCd}
            ,#{filePos}
            ,#{fileNm}
            ,#{fileSize}
            ,'admin'
	        ,SYSDATE
	        ,'admin'
	        ,SYSDATE 
          )

mapper.delete:
/* BrandCatalogMapper.xml, removeBrandCatalogTbDpProdImg,브랜드 카탈로그 상품 이미지 삭제, 김형식/SK플래닛 , 2013-12-30 */
DELETE
FROM TB_DP_PROD_IMG
WHERE PROD_ID = #{prodId}

mapper.insert:
       /* BrandCatalogMapper.xml, createTbDpProdTag,카탈로그 관련 태그 테이블에 INSERT, 김형식/SK플래닛  , 2013-12-30 */
        INSERT INTO TB_DP_PROD_TAG
            (
                 TAG_SEQ
                ,CID
                ,TAG_TYPE_CD
                ,TAG_CD
                ,TAG_NM
	            ,REG_ID
	            ,REG_DT
	            ,UPD_ID
	            ,UPD_DT   
            )
            VALUES(
	             (SELECT (MAX(TAG_SEQ)+1) FROM TB_DP_PROD_TAG)
	            ,#{cid}
	            ,#{tagTypeCd}         /*DP004507: 쇼핑/쿠폰 태그*/
	            ,#{tagCd}
	            ,#{tagNm}
	            ,#{regId}
	            ,sysdate
	            ,#{updId}
	            ,sysdate
            )

mapper.delete:
/* BrandCatalogMapper.xml, removeTbDpProdTag,TAG 정보 입력 전에 기존의 TAG 정보를 모두 삭, 김형식/SK플래닛  , 2013-12-30 */
DELETE FROM TB_DP_PROD_TAG
WHERE CID = #{cid}
