	        ,UPD_ID
	        ,UPD_DT
        )
        VALUES(
             #{createCatalogId}
            ,#{catalogId}
            ,#{createBrandId}
            ,'admin'
            ,SYSDATE
            ,'admin'
            ,SYSDATE 
        )

        /* BrandCatalogMapper.xml, INSERT_TB_DP_SHPG_CATALOG_DESC, SAC : 김형식 , 2013-12-30 */
        INSERT INTO TB_DP_SHPG_CATALOG_DESC(
	         CATALOG_ID
	        ,LANG_CD
