	        ,CATALOG_DESC
	        ,CATALOG_DTL_DESC
	        ,REG_ID
	        ,REG_DT
	        ,UPD_ID
	        ,UPD_DT
        )
        VALUES(
             #{createCatalogId}
            ,'ko'
            ,#{catalogNm}
            ,#{introText}
            ,#{catalogDesc}
            ,'admin'
            ,SYSDATE
            ,'admin'
            ,SYSDATE 
        )

mapper.update:
/* BrandCatalogMapper.xml, UPDATE_TB_DP_SHPG_CATALOG, SAC : 김형식 , 2013-12-30 */
 UPDATE 
     TB_DP_SHPG_CATALOG
 SET 
     BRAND_ID  = #{createBrandId}
   , UPD_DT    = SYSDATE
 WHERE CATALOG_ID = #{createCatalogId}

/* BrandCatalogMapper.xml, UPDATE_TB_DP_SHPG_CATALOG_DESC, SAC : 김형식 , 2013-12-30 */
 UPDATE 
     TB_DP_SHPG_CATALOG_DESC
 SET 
     CATALOG_NM  = #{catalogNm}
   , CATALOG_DESC = #{introText}
   , CATALOG_DTL_DESC = #{catalogDesc}
   , UPD_DT    = SYSDATE
 WHERE CATALOG_ID = #{createCatalogId}

/* BrandCatalogMapper.xml, UPDATE_TB_DP_CATEGORY_MENU_MAPG, SAC : 김형식 , 2013-12-30 */
 UPDATE 
     TB_DP_MENU_CATALOG_MAPG
 SET 
     MENU_ID  = #{dpCatNo}
   , UPD_DT    = SYSDATE
 WHERE CATALOG_ID = #{createCatalogId}

