mapper.select:
/* CouponMapper.xml, getCouponGenerateId, SAC : 김형식 , 2013-01-08 */
SELECT 
  PROD_ID
 FROM TB_DP_SHPG_PROD A
 WHERE SRC_CONTENT_ID= #{scrContentId}

mapper.insert:
/* CouponMapper.xml, insertTbDpProdInfo, SAC : 김형식 , 2013-01-08 */
  insert into TB_DP_PROD(
       PROD_ID                /* 상품_ID */
      ,PROD_CHRG_YN           /* 상품_유료_여부 */
      ,SELLER_MBR_NO          /* 판매자_회원_번호 */
      ,SVC_GRP_CD             /* 서비스_그룹_코드 */
      ,SVC_TYPE_CD            /* 서비스_타입_코드 */
      ,PROD_GRD_CD            /* 상품_등급_코드 */
      ,CID                    /* CID */
      ,FEE_CASE_CD            /* 요금_유형_코드 */
      ,USE_PERIOD_UNIT_CD     /* 사용_기간_단위_코드 */
      ,USE_PERIOD             /* 사용_기간 */
      ,DRM_YN                 /* DRM_여부 */
      ,META_CLSF_CD           /* 메타_구분_코드(CT28) */
      ,CONTENTS_TYPE_CD       /* 컨텐츠_타입_코드(PD0057, PD0073, PD0093, PD0104) */
      ,TOP_MENU_ID            /* 상위메뉴아이디 (DP28) */
      ,REG_ID                 /* 등록_ID */
      ,REG_DT                 /* 등록_일시 */
      ,UPD_ID                 /* 수정_ID */
      ,UPD_DT                 /* 수정_일시 */
  ) values (
       #{prodId}
      ,#{prodChrgYn}
      ,#{sellerMbrNo}
      ,#{svcGrpCd}
      ,#{svcTypeCd}
      ,#{prodGrdCd}
      ,#{cid}
      ,#{feeCaseCd}
      ,#{usePeriodUnitCd}
      ,#{usePeriod}
      ,#{drmYn}
      ,#{metaClsfCd}
      ,#{contentsTypeCd}
      ,#{topMenuId}
      ,#{regId}
      ,sysdate
      ,#{updId}
      ,sysdate
  )

/* CouponMapper.xml, insertTbDpShpgProdInfo, SAC : 김형식 , 2013-01-08 */
  insert into TB_DP_SHPG_PROD(
