/* CouponMapper.xml, insertTbDpTenantProdPriceInfo, SAC : 김형식 , 2013-01-08 */
  insert into TB_DP_TENANT_PROD_PRICE(
       TENANT_ID           /* 테넌트_ID */
      ,PROD_ID             /* 상품_ID */
      ,APPLY_START_DT      /* 적용_시작_일시 */
