      ,APPLY_END_DT        /* 적용_종료_일시 */
      ,PROD_AMT            /* 상품_금액 */
      ,CHNL_UNLMT_AMT      /* 채널_무제한_금액 */
      ,CHNL_PERIOD_AMT     /* 채널_기간_금액 */
      ,PROD_NET_AMT        /* 상품_정찰_금액 */
      ,DC_RATE             /* 할인_율 */
      ,DC_AMT              /* 할인_금액 */
      ,TAX_CLSF            /* 세금_구분 */
