      ,REG_DT              /* 등록_일시 */
      ,UPD_ID              /* 수정_ID */
      ,UPD_DT              /* 수정_일시 */
  ) values (
       #{tenantId}
      ,#{prodId}
      ,TO_DATE(#{applyStartDt},'yyyymmddhh24miss')
      ,#{seq}
      ,TO_DATE(#{applyEndDt},'yyyymmddhh24miss')
      ,#{prodAmt}
      ,#{chnlUnlmtAmt}
      ,#{chnlPeriodAmt}
      ,#{prodNetAmt}
      ,#{dcRate}
      ,#{dcAmt}
      ,#{taxClsf}
      ,#{regId}
      ,sysdate
      ,#{updId}
      ,sysdate
  )

mapper.update:
/* CouponMapper.xml, updateDPCouponStatus, SAC : 김형식 , 2013-01-08 */
UPDATE 
    TB_DP_TENANT_PROD
SET PROD_STATUS_CD = #{dpStatusCode}
WHERE 1=1

  AND PROD_ID =#{prodId}


  AND PROD_ID =#{itemCode}

        
  AND PROD_ID IN (
       SELECT 
              PROD_ID 
         FROM TB_DP_SHPG_PROD
        WHERE  PROD_ID = #{prodId}
    UNION ALL 
       SELECT 
               B.PART_PROD_ID
        FROM   TB_DP_SHPG_PROD A 
              ,TB_DP_PROD_RSHP B
        WHERE A.PROD_ID = B.PROD_ID
          AND A.PROD_ID = #{prodId}     
  )

mapper.select:
/* CouponMapper.xml, GET_COUPON_INFO, SAC : 김형식 , 2013-01-08 */
 SELECT 
     COUNT(PROD_ID) as CNT
 FROM TB_DP_SHPG_PROD
 WHERE SRC_CONTENT_ID = #{prodId}
