SELECT /* BannerMapper.xml, searchBannerList, tae hee Lee, 2014-02-21 */ JA.TOTAL_COUNT , JA.BNR_MENU_ID , JA.BNR_SEQ , JA.IMG_SIZE_CD , JA.BNR_INFO_TYPE_CD , JA.BNR_INFO , JA.EXPO_ORD , JA.EXPO_STATUS_CD , JA.IMG_PATH , TO_CHAR(JA.REG_DT, 'YYYYMMDDHH24MISS') AS REG_DT , (SELECT TOP_MENU_ID FROM TB_DP_PROD PR WHERE PR.PROD_ID = JA.BNR_INFO AND ROWNUM = 1) AS TOP_MENU_ID , CASE WHEN (BNR_MENU_ID = 'DP010910' OR BNR_MENU_ID = 'DP010911') THEN /* BEST 앱, BEST 컨텐츠 */ DECODE(BNR_INFO_TYPE_CD , 'DP010303' /* 상품 배너 */ , (SELECT PROD_NM FROM TB_DP_PROD_DESC WHERE LANG_CD = 'ko' AND PROD_ID = JA.BNR_INFO) , 'DP010306' /* 카테고리 배너 */ , (SELECT MENU_NM FROM TB_DP_MENU_CATEGORY_DESC WHERE LANG_CD = 'ko' AND MENU_ID = JA.BNR_INFO) , 'DP010307' /* 운영자 임의추천 배너 */ , (SELECT LIST_NM FROM TB_DP_LIST WHERE TENANT_ID = 'S01' AND LIST_ID = JA.BNR_INFO) , 'DP010309' /* 특정 브랜드샵 배너 */ , (SELECT BRAND_SHOP_NM FROM TB_DP_BRAND_SHOP WHERE TENANT_ID = 'S01' AND BRAND_SHOP_NO = JA.BNR_INFO) , 'DP010310' /* 내부URL 배너 */ , BNR_NM) WHEN BNR_MENU_ID = 'DP010926' THEN /* 모바일웹 직사각형 */ DECODE(BNR_INFO_TYPE_CD , 'DP010303' /* 상품 배너 */ , (SELECT PROD_NM FROM TB_DP_PROD_DESC WHERE LANG_CD = 'ko' AND PROD_ID = JA.BNR_INFO) , BNR_NM) WHEN BNR_MENU_ID = 'DP010912' THEN /* 앱가이드 */ DECODE(BNR_INFO_TYPE_CD , 'DP010301' /* 외부URL 배너 */ , (SELECT LIST_NM FROM TB_DP_LIST WHERE TENANT_ID = 'S01' AND LIST_ID = JA.BNR_INFO) , 'DP010303' /* 상품 배너 */ , (SELECT PROD_NM FROM TB_DP_PROD_DESC WHERE LANG_CD = 'ko' AND PROD_ID = JA.BNR_INFO) , 'DP010312' /* 테마추천 배너 */ , (SELECT LIST_NM FROM TB_DP_LIST WHERE TENANT_ID = 'S01' AND LIST_ID = JA.BNR_INFO) , BNR_NM) ELSE BNR_NM END AS BNR_NM FROM (SELECT IA.TOTAL_COUNT , IA.BNR_MENU_ID , IA.BNR_SEQ , IA.IMG_SIZE_CD , IA.BNR_INFO_TYPE_CD , IA.BNR_INFO , IA.BNR_NM , IA.EXPO_ORD , IA.EXPO_ORD_SUB , IA.EXPO_STATUS_CD , IA.IMG_PATH , IA.REG_DT FROM (SELECT COUNT(A.BNR_SEQ) OVER() AS TOTAL_COUNT , A.BNR_MENU_ID , A.BNR_SEQ , A.BNR_INFO_TYPE_CD , A.BNR_INFO , A.REG_DT , B.BNR_NM , B.EXPO_ORD , B.EXPO_ORD_SUB , B.EXPO_STATUS_CD , C.IMG_SIZE_CD , C.IMG_PATH FROM TB_DP_BNR A , TB_DP_BNR_EXPO B , TB_DP_BNR_IMG C
, TB_DP_BNR_DVC D , TB_CM_DEVICE E
WHERE A.TENANT_ID = B.TENANT_ID AND A.BNR_MENU_ID = B.BNR_MENU_ID AND A.BNR_SEQ = B.BNR_SEQ AND A.TENANT_ID = C.TENANT_ID AND A.BNR_MENU_ID = C.BNR_MENU_ID AND A.BNR_SEQ = C.BNR_SEQ
AND A.TENANT_ID = D.TENANT_ID AND A.BNR_MENU_ID = D.BNR_MENU_ID AND A.BNR_SEQ = D.BNR_SEQ AND D.DEVICE_MODEL_CD = E.DEVICE_MODEL_CD AND D.DEVICE_MODEL_CD = #{deviceModelCd} AND E.USE_YN = 'Y' AND (CASE WHEN (A.BNR_INFO_TYPE_CD = 'DP010306' AND SUBSTR(A.BNR_INFO, 1, 4 ) = 'DP13') THEN DECODE(E.EBOOK_SPRT_YN, 'Y', 1, 0) WHEN (A.BNR_INFO_TYPE_CD = 'DP010306' AND SUBSTR(A.BNR_INFO, 1, 4 ) = 'DP14') THEN DECODE(E.COMIC_SPRT_YN, 'Y', 1, 0) WHEN (A.BNR_INFO_TYPE_CD = 'DP010306' AND SUBSTR(A.BNR_INFO, 1, 4 ) = 'DP28') THEN DECODE(E.SCL_SHPG_SPRT_YN, 'Y', 1, 0) ELSE 1 END) = 1
AND A.TENANT_ID = #{tenantId} AND B.EXPO_YN = 'Y' AND B.USE_YN = 'Y' AND B.EXPO_STATUS_CD IN ('DP010201', 'DP010202') AND C.IMG_SIZE_CD = #{imgSizeCd}
AND C.MOBILE_WEB_EXPO_YN = 'Y' AND A.BNR_MENU_ID IN ('DP010915', 'DP010916', 'DP010917', 'DP010918', 'DP010919')
AND A.BNR_MENU_ID IN ('DP010912', 'DP010910')
AND A.BNR_MENU_ID = #{bnrMenuId}
AND B.BNR_EXPO_MENU_ID = #{bnrExpoMenuId}
AND SYSDATE BETWEEN DECODE(B.EXPO_STATUS_CD, 'DP010202', SYSDATE, B.EXPO_START_DT) AND DECODE(B.EXPO_STATUS_CD, 'DP010202', SYSDATE, NVL(B.EXPO_END_DT, SYSDATE)) ) IA ORDER BY IA.BNR_MENU_ID DESC, IA.EXPO_ORD, IA.EXPO_ORD_SUB, IA.EXPO_STATUS_CD ) JA
