SELECT /* ProductInfoMapper.xml, getMusicMetaList, SAC : 정희원 , 2014-04-09 */ CPD.PROD_ID, EPD.PROD_ID AS PART_PROD_ID, TPRC.PROD_AMT, PDESC.PROD_NM,
NVL((CASE WHEN (TS.AVG_EVLU_SCORE > 0 AND TS.AVG_EVLU_SCORE < 1) THEN 1 WHEN TS.AVG_EVLU_SCORE > 5 THEN 5 ELSE ROUND(TS.AVG_EVLU_SCORE, 1) END) , 0) AS AVG_EVLU_SCORE,
NVL(TS.PRCHS_CNT, 0) AS PRCHS_CNT, NVL(TS.PATICPERS_CNT, 0) AS PATICPERS_CNT, PDESC.ARTIST1_NM, PDESC.ARTIST2_NM, PDESC.ARTIST3_NM, 'Y' AS MP3_SPRT_YN, MM.BELL_SPRT_YN, MM.COLORRING_SPRT_YN, MM.AGENCY_NM, MM.CHNL_COMP_NM, MM.OUTSD_CONTENTS_ID, MM.ISSUE_DAY, PI.FILE_PATH || PI.FILE_NM AS FILE_PATH, PI.FILE_NM, PI.FILE_SIZE, EPD.CONTENTS_TYPE_CD, TP.PROD_STATUS_CD, PDESC.PROD_BASE_DESC, PDESC.PROD_DTL_DESC, CPD.PROD_GRD_CD, CPD.META_CLSF_CD, MM.ALBUM_TYPE_NM, MC.MENU_ID, MD.MENU_NM, MD.MENU_DESC, CPD.TOP_MENU_ID, TMD.MENU_NM AS TOP_MENU_NM, TMD.MENU_DESC AS TOP_MENU_DESC, MCH.RANK_CHG_CNT, MCH.RANK_CHG_CLSF_CD, (SELECT DA.ARTIST_ID FROM TB_DP_ARTIST DA WHERE DA.ARTIST_ID = NVL(MCH.ARTIST_ID, PDESC.ARTIST1_ID)) AS ARTIST_ID, TO_CHAR(MCH.REG_DT, 'YYYYMMDDHH24MISS') AS REG_DT FROM TB_DP_PROD CPD INNER JOIN TB_DP_PROD_RSHP RS ON RS.PROD_ID = CPD.PROD_ID INNER JOIN TB_DP_PROD EPD ON EPD.PROD_ID = RS.PART_PROD_ID INNER JOIN TB_DP_MUSIC_PROD MM ON EPD.PROD_ID = MM.PROD_ID INNER JOIN TB_DP_PROD_DESC PDESC ON PDESC.PROD_ID = EPD.PROD_ID INNER JOIN TB_DP_MENU_CATEGORY_PROD_MAPG MCM ON MCM.PROD_ID = CPD.PROD_ID AND MCM.USE_YN = 'Y' AND MCM.BASE_YN = 'Y' INNER JOIN TB_DP_MENU_CATEGORY MC ON MC.MENU_ID = MCM.MENU_ID AND MC.USE_YN = 'Y' INNER JOIN TB_DP_MENU_CATEGORY_DESC MD ON MD.MENU_ID = MCM.MENU_ID INNER JOIN TB_DP_MENU_CATEGORY_DESC TMD ON TMD.MENU_ID = CPD.TOP_MENU_ID INNER JOIN TB_DP_PROD_IMG PI ON PI.PROD_ID = CPD.PROD_ID AND PI.EXPO_ORD = 1 AND PI.LANG_CD = PDESC.LANG_CD AND PI.IMG_CD = #{imageCd} -- 'DP000162' INNER JOIN TB_DP_TENANT_PROD TP ON TP.PROD_ID = EPD.PROD_ID INNER JOIN TB_DP_TENANT_PROD CTP ON CTP.PROD_ID = CPD.PROD_ID INNER JOIN TB_DP_TENANT_PROD_PRICE TPRC ON TPRC.PROD_ID = EPD.PROD_ID AND TPRC.TENANT_ID = TP.TENANT_ID LEFT OUTER JOIN TB_DP_TENANT_PROD_STATS TS ON TS.PROD_ID = CPD.PROD_ID AND TS.TENANT_ID = TP.TENANT_ID LEFT OUTER JOIN TB_DP_MUSIC_CHART MCH ON MCH.PROD_ID = EPD.PROD_ID AND MCH.CHART_CLSF_CD = #{chartClsfCd} AND MCH.RANK_START_DAY = #{rankStartDay} WHERE 1=1 AND CPD.CONTENTS_TYPE_CD = 'PD002501' AND EPD.CONTENTS_TYPE_CD = 'PD002502' AND RS.PROD_RSHP_CD = 'DP010802' AND CPD.SVC_TYPE_CD = #{svcTypeCd} AND EPD.SVC_TYPE_CD = CPD.SVC_TYPE_CD AND CPD.SVC_GRP_CD = 'DP000203' AND EPD.SVC_GRP_CD = 'DP000203' AND MD.LANG_CD = #{langCd} AND TMD.LANG_CD = MD.LANG_CD AND TP.EXPO_YN = 'Y' AND TP.TENANT_ID = #{tenantId} AND PDESC.LANG_CD = MD.LANG_CD AND CPD.PROD_ID IN #{v}

SELECT TB.UP_MENU_ID , (SELECT MENU_NM FROM TB_DP_MENU_CATEGORY_DESC WHERE MENU_ID = TB.TOP_MENU_ID AND LANG_CD = TB.LANG_CD AND ROWNUM = 1) AS TOP_MENU_NM , TB.TOP_MENU_ID , TB.MENU_ID , TB.MENU_NM , TB.MENU_DESC , TB.META_CLSF_CD , TB.PROD_ID , TB.PART_PROD_ID , TB.PROD_NM , TB.CHAPTER , TB.BOOK_CLSF_CD , TB.PROD_BASE_DESC , TB.PROD_GRD_CD , TB.ARTIST1_NM , TB.ARTIST2_NM , TB.ARTIST3_NM , TB.ISSUE_DAY , TB.CHNL_COMP_NM , DECODE( TB.COMPT_YN, 'Y', 'completed', 'continue' ) AS BOOK_STATUS , CASE WHEN (TB.CHNL_PERIOD_AMT >= 0 AND STRM_EPSD_CNT > 0) THEN CHNL_PERIOD_AMT ELSE NVL(CHNL_UNLMT_AMT, 0) END AS CHNL_PROD_AMT -- 채널 대표 가격 , NVL(TB.PROD_AMT, 0) AS EPSD_PROD_AMT , NVL(TB.PATICPERS_CNT, 0) AS PATICPERS_CNT , NVL(TB.PRCHS_CNT, 0) AS DWLD_CNT , NVL((CASE WHEN (TB.AVG_EVLU_SCORE > 0 AND TB.AVG_EVLU_SCORE < 1) THEN 1 WHEN TB.AVG_EVLU_SCORE > 5 THEN 5 ELSE ROUND(TB.AVG_EVLU_SCORE, 1) END), 0) AS AVG_EVLU_SCORE , TB.FILE_PATH || TB.FILE_NM AS FILE_PATH , CASE WHEN (SYSDATE - TB.LAST_DEPLOY_DT) <= 3 THEN 'Y' ELSE 'N' END AS ICON_YN , TB.LAST_DEPLOY_DT AS UPD_DT , TB.PROD_STATUS_CD FROM ( SELECT CMC.UP_MENU_ID, CMC.TOP_MENU_ID, CMC.MENU_ID, CMCM.PROD_ID, RSHP.PART_PROD_ID, CMCD.MENU_NM, CMCD.MENU_DESC, CPROD.PROD_GRD_CD, CPROD.META_CLSF_CD, CPROD.CONTENTS_TYPE_CD, CEBOOK.STRM_EPSD_CNT, CEBOOK.ISSUE_DAY, CEBOOK.CHNL_COMP_NM, CEBOOK.EPSD_CNT, CEBOOK.COMPT_YN, CPDESC.PROD_NM, CPDESC.ARTIST1_NM, CPDESC.ARTIST2_NM, CPDESC.ARTIST3_NM, CPDESC.PROD_BASE_DESC, CPDESC.PROD_DTL_DESC, CTPRC.CHNL_PERIOD_AMT, CTPRC.CHNL_UNLMT_AMT, EPROD.DRM_YN, EPROD.LAST_DEPLOY_DT, EEBOOK.CHAPTER, EEBOOK.BOOK_CLSF_CD, CPI.FILE_PATH, CPI.FILE_NM, CTSTAT.PATICPERS_CNT, CTSTAT.PRCHS_CNT, CTSTAT.AVG_EVLU_SCORE, ETPRC.PROD_AMT, ETPRC.PROD_NET_AMT, CTPROD.PROD_STATUS_CD, CPDESC.LANG_CD FROM TB_DP_PROD_RSHP RSHP INNER JOIN TB_DP_TENANT_PROD CTPROD ON CTPROD.PROD_ID = RSHP.PROD_ID INNER JOIN TB_DP_TENANT_PROD_PRICE CTPRC ON CTPRC.PROD_ID = RSHP.PROD_ID AND CTPRC.TENANT_ID = CTPROD.TENANT_ID INNER JOIN TB_DP_PROD_DESC CPDESC ON CPDESC.PROD_ID = RSHP.PROD_ID INNER JOIN TB_DP_PROD CPROD ON CPROD.PROD_ID = RSHP.PROD_ID INNER JOIN TB_DP_EBOOK_PROD CEBOOK ON CEBOOK.PROD_ID = RSHP.PROD_ID LEFT OUTER JOIN TB_DP_PROD_IMG CPI ON CPI.PROD_ID = CEBOOK.PROD_ID AND CPI.IMG_CD = #{imageCd} AND CPI.LANG_CD = #{langCd} AND CPI.EXPO_ORD = 1 INNER JOIN TB_DP_TENANT_PROD ETPROD ON ETPROD.PROD_ID = RSHP.PART_PROD_ID AND ETPROD.TENANT_ID = CTPROD.TENANT_ID INNER JOIN TB_DP_TENANT_PROD_PRICE ETPRC ON ETPRC.PROD_ID = RSHP.PART_PROD_ID AND ETPRC.TENANT_ID = CTPROD.TENANT_ID INNER JOIN TB_DP_PROD EPROD ON EPROD.PROD_ID = RSHP.PART_PROD_ID INNER JOIN TB_DP_EBOOK_PROD EEBOOK ON EEBOOK.PROD_ID = RSHP.PART_PROD_ID INNER JOIN TB_DP_MENU_CATEGORY_PROD_MAPG CMCM ON CMCM.PROD_ID = RSHP.PROD_ID INNER JOIN TB_DP_MENU_CATEGORY CMC ON CMC.MENU_ID = CMCM.MENU_ID INNER JOIN TB_DP_MENU_CATEGORY_DESC CMCD ON CMCD.MENU_ID = CMCM.MENU_ID AND CMCD.LANG_CD = CPDESC.LANG_CD LEFT OUTER JOIN TB_DP_TENANT_PROD_STATS CTSTAT ON CTSTAT.PROD_ID = RSHP.PROD_ID AND CTSTAT.TENANT_ID = CTPROD.TENANT_ID WHERE 1 = 1 AND RSHP.PROD_RSHP_CD = 'DP010802' AND CTPROD.TENANT_ID = #{tenantId} AND CPDESC.LANG_CD = #{langCd}
AND RSHP.PROD_ID = #{prodId}
AND RSHP.PART_PROD_ID = #{prodId}
AND CPROD.CONTENTS_TYPE_CD = 'PD002501' AND EPROD.CONTENTS_TYPE_CD = 'PD002502' AND CPROD.SVC_GRP_CD = 'DP000203' AND CPROD.TOP_MENU_ID = 'DP26' AND SYSDATE BETWEEN CTPRC.APPLY_START_DT AND CTPRC.APPLY_END_DT AND SYSDATE BETWEEN ETPRC.APPLY_START_DT AND ETPRC.APPLY_END_DT AND CMCM.BASE_YN = 'Y' AND CMCM.USE_YN = 'Y' AND CMC.USE_YN = 'Y' ORDER BY EPROD.LAST_DEPLOY_DT DESC ) TB WHERE ROWNUM = 1

SELECT /* ProductInfoMapper.xml, getMenuInfo, 정희원, 2014-03-05 */ MM.PROD_ID, MC.TOP_MENU_ID, TMD.MENU_NM AS TOP_MENU_NM, TMD.MENU_DESC AS TOP_MENU_DESC, MC.MENU_ID, MD.MENU_NM, MD.MENU_DESC, MC.UP_MENU_ID FROM TB_DP_MENU_CATEGORY_PROD_MAPG MM INNER JOIN TB_DP_MENU_CATEGORY MC ON MC.MENU_ID = MM.MENU_ID INNER JOIN TB_DP_MENU_CATEGORY_DESC MD ON MD.MENU_ID = MM.MENU_ID INNER JOIN TB_DP_MENU_CATEGORY TMC ON TMC.MENU_ID = MC.TOP_MENU_ID INNER JOIN TB_DP_MENU_CATEGORY_DESC TMD ON TMD.MENU_ID = MC.TOP_MENU_ID AND TMD.LANG_CD = MD.LANG_CD WHERE 1=1 AND MM.PROD_ID = #{prodId}
AND MM.MENU_ID = #{menuId}
AND MD.LANG_CD = #{langCd} AND MM.USE_YN = 'Y' AND MC.USE_YN = 'Y'
