SELECT /* CategoryMapper.xml, selectCategoryAppList, SAC 전시, 2014-05-19 */ KA.TOTAL_COUNT , KA.RNUM , KA.TOP_MENU_ID , KA.MENU_ID , KA.PROD_ID FROM (SELECT JA.* FROM (SELECT ROW_NUMBER() OVER(ORDER BY NVL(IB.PRCHS_CNT, 0) DESC, IA.REG_DT DESC) AS RNUM , IA.* , IB.PRCHS_CNT , IB.AVG_EVLU_SCORE , IB.PATICPERS_CNT FROM (SELECT /*+ leading(K A B) */ COUNT(B.PROD_ID) OVER() AS TOTAL_COUNT , A.MENU_ID , A.TOP_MENU_ID , B.PROD_ID , D.PROD_CHRG_YN , D.PROD_GRD_CD , D.DRM_YN , D.REG_DT , G.TENANT_ID , I.SUB_CONTENTS_ID FROM TB_DP_MENU_CATEGORY A , TB_DP_MENU_CATEGORY_PROD_MAPG B , TB_DP_PROD D , TB_DP_TENANT_PROD G , TB_DP_SPRT_DEVICE I , TB_CM_DEVICE K WHERE A.MENU_ID = B.MENU_ID AND B.PROD_ID = D.PROD_ID AND B.PROD_ID = G.PROD_ID AND B.PROD_ID = I.PROD_ID AND I.DEVICE_MODEL_CD = K.DEVICE_MODEL_CD AND A.USE_YN = 'Y' /* Y : 사용 (사용여부) */ AND B.USE_YN = 'Y' /* Y : 사용 (사용여부) */ AND D.SVC_GRP_CD = 'DP000201' /* DP000201 : 애플리캐이션 */ AND G.PROD_STATUS_CD = 'PD000403' /* PD000403 : 판매중 */ AND G.EXPO_YN = 'Y' /* Y : 노출 (노출여부) */ AND A.MENU_ID = #{menuId} /* 메뉴ID */ AND G.TENANT_ID = #{tenantId} /* 테넌트ID */ AND K.DEVICE_MODEL_CD = #{deviceModelCd} /* 단말모델코드 */ AND K.USE_YN = 'Y'
AND D.PROD_CHRG_YN = #{prodCharge} /* 상품유료여부 */
AND D.PROD_GRD_CD IN
#{prodGradeCd}
) IA , TB_DP_TENANT_PROD_STATS IB WHERE IA.TENANT_ID = IB.TENANT_ID(+) AND IA.PROD_ID = IB.PROD_ID(+) ) JA WHERE JA.RNUM BETWEEN #{offset} AND #{count} ) KA
