/* WebtoonMapper.xml.getWebtoonList, 웹툰 상품 리스트 조회, 김형식/SK플래닛 , 2013-12-26 */ SELECT * FROM ( SELECT ROW_NUMBER()OVER(ORDER BY UPD_DT DESC, PROD_NM ) AS RNUM ,COUNT(PROD_ID) OVER() AS totalCount ,(SELECT MENU_DESC FROM TB_DP_MENU_CATEGORY_DESC WHERE MENU_ID = UP_MENU_ID AND ROWNUM=1) as upMenuName ,UP_MENU_ID as upMenuId /* 상위 메뉴ID */ ,MENU_ID as menuId /* 메뉴ID */ ,MENU_DESC as menuNm /* 메뉴명 */ ,CHANNEL_ID as channelId /* 채널ID */ ,PROD_ID as prodId /* 상품ID */ ,PROD_NM as prodNm /* 상품명 */ ,PROD_GRD_CD as prodGrdCd /* 상품등급코드 */ ,AVG_SCORE as avgScore /* 평점 */ ,PROD_AMT as prodAmt /* 가격 */ ,PART_CNT as partCnt /* 부분개수 */ ,SERIALLY_WKDY as seriallyWkdy /* 요일별 */ ,CASE WHEN (SYSDATE - UPD_DT) <= 3 THEN 'Y' ELSE 'N' END AS iconYn /* 아이콘 */ ,UPD_DT as updDt /* 수정일 */ ,DECODE(COMPT_YN,'Y','completed','N','continue',NULL,'continue') as comptYn /* 완결여부 */ ,VOD_TITL_NM as prefix /* prefix */ ,CHAPTER as chapter /* 챕터 */ ,ARTIST1_NM as artist1Nm /* 아티스트1 */ ,ARTIST2_NM as artist2Nm /* 아티스트2 */ ,ARTIST3_NM as artist3Nm /* 아티스트3 */ ,FILE_POS as filePos /* 파일경로 */ FROM ( SELECT A.UP_MENU_ID ,A.MENU_ID ,C.MENU_DESC ,J.PROD_ID AS CHANNEL_ID ,D.PROD_ID ,F.PROD_ALIAS ,D.PROD_GRD_CD ,F.PROD_NM ,TO_CHAR(NVL(H.AVG_EVLU_SCORE,0), '0.0') AS AVG_SCORE ,NVL(H.PATICPERS_CNT,0) AS PART_CNT ,SERIALLY_WKDY ,D.UPD_DT ,E.CHAPTER ,E.COMPT_YN ,E.VOD_TITL_NM ,I.PROD_AMT ,F.ARTIST1_NM ,F.ARTIST2_NM ,F.ARTIST3_NM ,(SELECT FILE_PATH||FILE_NM FROM TB_DP_PROD_IMG WHERE IMG_CD=#{imageCd} AND PROD_ID =D.PROD_ID AND ROWNUM=1) FILE_POS FROM TB_DP_MENU_CATEGORY A , TB_DP_MENU_CATEGORY_PROD_MAPG B , TB_DP_MENU_CATEGORY_DESC C , TB_DP_PROD D , TB_DP_MULTIMDA_PROD E , TB_DP_PROD_DESC F , TB_DP_TENANT_PROD G , TB_DP_TENANT_PROD_STATS H , TB_DP_TENANT_PROD_PRICE I , TB_DP_PROD_RSHP J ,( SELECT PROD_ID,PART_PROD_ID FROM ( SELECT ROW_NUMBER() OVER(PARTITION BY J1.PROD_ID ORDER BY D1.UPD_DT DESC) AS NO ,J1.PROD_ID ,J1.PART_PROD_ID FROM TB_DP_MENU_CATEGORY A1 , TB_DP_MENU_CATEGORY_PROD_MAPG B1 , TB_DP_MENU_CATEGORY_DESC C1 , TB_DP_PROD D1 , TB_DP_MULTIMDA_PROD E1 , TB_DP_PROD_DESC F1 , TB_DP_TENANT_PROD G1 , TB_DP_PROD_RSHP J1 WHERE 1=1 AND A1.MENU_ID = B1.MENU_ID AND A1.MENU_ID = C1.MENU_ID AND B1.PROD_ID = D1.PROD_ID AND D1.PROD_ID = E1.PROD_ID AND D1.PROD_ID = F1.PROD_ID AND D1.PROD_ID = G1.PROD_ID AND D1.PROD_ID = J1.PART_PROD_ID AND G1.TENANT_ID = #{tenantId} /* INPUT 테넌트ID */
AND A1.MENU_ID = #{menuId} /* DP26001 : 웹툰>코믹,DP26002:순정,DP26003:드라마,DP26004:스릴러,DP26005:판타지,DP26006:액션 */
AND A1.UP_MENU_ID ='DP26' /* DP26 : 웹툰 */ AND J1.PROD_RSHP_CD ='DP010802' /*채널-에피소드 상품 관계*/ AND A1.USE_YN = 'Y' /* Y : 사용(시용여부) */ AND B1.USE_YN = 'Y' /* Y : 사용(시용여부) */ AND B1.USE_YN = 'Y' /* Y : 사용(시용여부) */ AND B1.BASE_YN = 'Y' /* Y : 기본(기본여부) */ AND C1.LANG_CD = #{langCd} /* ko : 한국어 */ AND D1.SVC_GRP_CD = 'DP000203' /* DP000203 : 멀티미디어 */ AND G1.PROD_STATUS_CD = 'PD000403' /* PD000403 : 판매중 */ AND G1.EXPO_YN = 'Y' /* Y : 노출(노출여부) */
AND E1.SERIALLY_WKDY LIKE '%' || (SELECT cd_nm FROM tb_cm_cd WHERE cd_id =#{weekDayCd} )|| '%' /* DP010101 ~DP010107 :월~토*/
)WHERE NO =1 ) K WHERE 1=1 AND A.MENU_ID = B.MENU_ID AND A.MENU_ID = C.MENU_ID AND B.PROD_ID = D.PROD_ID AND D.PROD_ID = E.PROD_ID AND D.PROD_ID = F.PROD_ID AND D.PROD_ID = G.PROD_ID AND G.PROD_ID = H.PROD_ID AND G.PROD_ID = I.PROD_ID AND G.TENANT_ID = I.TENANT_ID AND D.PROD_ID = J.PART_PROD_ID AND D.PROD_ID = K.PART_PROD_ID(+) AND K.PART_PROD_ID IS NOT NULL AND G.TENANT_ID = #{tenantId} /* INPUT 테넌트ID */
AND A.MENU_ID = #{menuId} /* DP26001 : 웹툰>코믹,DP26002:순정,DP26003:드라마,DP26004:스릴러,DP26005:판타지,DP26006:액션 */
AND A.UP_MENU_ID = 'DP26' /* DP26 : 웹툰 */ AND J.PROD_RSHP_CD ='DP010802' /*채널-에피소드 상품 관계*/ AND A.USE_YN = 'Y' /* Y : 사용(시용여부) */ AND B.USE_YN = 'Y' /* Y : 사용(시용여부) */ AND B.USE_YN = 'Y' /* Y : 사용(시용여부) */ AND B.BASE_YN = 'Y' /* Y : 기본(기본여부) */ AND C.LANG_CD = #{langCd} /* ko : 한국어 */ AND D.SVC_GRP_CD = 'DP000203' /* DP000203 : 멀티미디어 */ AND G.PROD_STATUS_CD = 'PD000403' /* PD000403 : 판매중 */ AND G.EXPO_YN = 'Y' /* Y : 노출(노출여부) */ AND SYSDATE BETWEEN I.APPLY_START_DT AND I.APPLY_END_DT
AND E.SERIALLY_WKDY LIKE '%' || (SELECT cd_nm FROM tb_cm_cd WHERE cd_id =#{weekDayCd} )|| '%' /* DP010101 ~DP010107 :월~토*/
AND NOT EXISTS (SELECT 1 FROM TB_DP_SPRT_DEVICE WHERE DEVICE_MODEL_CD = #{deviceModelCd} AND PROD_ID = D.PROD_ID ) ) ) ka WHERE RNUM BETWEEN #{offset} AND #{count}
