SELECT /* WebtoonMapper.xml, getWebtoonList, SAC 전시, 2014-04-03 */ LA.TOTAL_COUNT , LA.RNUM , LA.TOP_MENU_ID , LA.MENU_ID , LA.PROD_ID , LA.PART_PROD_ID , LA.META_CLSF_CD , LA.CONTENTS_TYPE_CD FROM ( SELECT KA.* FROM ( SELECT JA.*, ROW_NUMBER() OVER(ORDER BY JA.UPD_DT DESC, JA.PROD_NM) AS RNUM FROM ( SELECT IA.*, COUNT(IA.PROD_ID) OVER() AS TOTAL_COUNT FROM ( SELECT ROW_NUMBER( ) OVER ( PARTITION BY D.PROD_ID ORDER BY J.UPD_DT DESC ) AS RN , A.TOP_MENU_ID , A.MENU_ID , D.PROD_ID , I.PART_PROD_ID , D.META_CLSF_CD , D.CONTENTS_TYPE_CD , F.PROD_NM , J.UPD_DT , K.SERIALLY_WKDY FROM TB_DP_MENU_CATEGORY A , TB_DP_MENU_CATEGORY_PROD_MAPG B , TB_DP_PROD D /* 채널 상품 */ , TB_DP_EBOOK_PROD E /* 채널 상품 */ , TB_DP_PROD_DESC F , TB_DP_TENANT_PROD G , TB_DP_PROD_RSHP I , TB_DP_PROD J /* 에피소드 상품 */ , TB_DP_EBOOK_PROD K /* 에피소드 상품 */ , TB_DP_TENANT_PROD L /* 에피소드 상품 */ WHERE A.MENU_ID = B.MENU_ID AND B.PROD_ID = D.PROD_ID AND D.PROD_ID = E.PROD_ID AND D.PROD_ID = F.PROD_ID AND D.PROD_ID = G.PROD_ID AND G.TENANT_ID = #{tenantId} /* header param : tenantId */ AND D.PROD_ID = I.PROD_ID AND I.PROD_RSHP_CD = 'DP010802' /* DP010802 : 채널-에피소드 상품 관계 */ AND I.PART_PROD_ID = J.PROD_ID AND I.PART_PROD_ID = K.PROD_ID AND I.PART_PROD_ID = L.PROD_ID AND A.USE_YN = 'Y' /* Y : 사용 (사용여부) */ AND B.USE_YN = 'Y' /* Y : 사용 (사용여부) */ AND D.SVC_GRP_CD = 'DP000203' /* DP000203 : 멀티미디어 */ AND D.CONTENTS_TYPE_CD = 'PD002501' /* PD002501 : 채널타입 */ AND E.EPSD_CNT > 0 AND F.LANG_CD = #{langCd} /* 언어 코드 */ AND G.PROD_STATUS_CD = 'PD000403' /* PD000403 : 판매중 */ AND G.EXPO_YN = 'Y' /* Y : 노출 (노출여부) */ AND J.CONTENTS_TYPE_CD = 'PD002502' /* PD002502 : 에피소드 */ AND L.PROD_STATUS_CD = 'PD000403' /* PD000403 : 판매중 */ AND A.TOP_MENU_ID = #{topMenuId} /* 탑메뉴ID */
AND A.MENU_ID = #{menuId} /* 메뉴ID */
AND A.MENU_ID LIKE 'DP26%' /* 메뉴ID */
) IA WHERE IA.RN = 1
AND IA.SERIALLY_WKDY LIKE CONCAT('%', CONCAT((SELECT cd_nm FROM tb_cm_cd WHERE cd_id = #{weekDayCd} AND LANG_CD = #{langCd}), '%')) /* DP010101 ~ DP010107 : 월~토*/
) JA ) KA WHERE KA.RNUM BETWEEN #{offset} AND #{count} ) LA

SELECT /* WebtoonMapper.xml, getAdminWebtoonList, SAC 전시, 2014-03-17 */ B1.TENANT_ID , B1.MENU_ID , B1.PROD_ID , B1.PART_PROD_ID , B1.TOP_MENU_ID , B1.CONTENTS_TYPE_CD , B1.TOTAL_COUNT FROM (SELECT A1.TENANT_ID , A1.MENU_ID , A1.PROD_ID , A1.PART_PROD_ID , A1.TOP_MENU_ID , A1.CONTENTS_TYPE_CD , COUNT(A1.PROD_ID) OVER () AS TOTAL_COUNT , ROW_NUMBER() OVER(ORDER BY A1.EXPO_ORD_ALWAYS_YN DESC, A1.EXPO_ORD, A1.EXPO_ORD_SUB) AS RNUM FROM (SELECT A.TENANT_ID , D.MENU_ID , C.PROD_ID , C.PART_PROD_ID , E.TOP_MENU_ID , A.EXPO_ORD_ALWAYS_YN , A.EXPO_ORD , A.EXPO_ORD_SUB , F.CONTENTS_TYPE_CD , ROW_NUMBER() OVER(PARTITION BY C.PROD_ID ORDER BY F.UPD_DT DESC) AS NO FROM (SELECT LP.TENANT_ID , LP.PROD_ID , LP.EXPO_ORD_ALWAYS_YN , LP.EXPO_ORD , LP.EXPO_ORD_SUB FROM TB_DP_LIST_PROD LP , TB_DP_LIST LT WHERE LT.TENANT_ID = LP.TENANT_ID AND LT.LIST_ID = LP.LIST_ID AND LT.EXPO_YN = 'Y' AND LP.EXPO_YN = 'Y' AND LT.TENANT_ID = #{tenantId} AND LT.LIST_ID = #{listId}
AND LP.MENU_ID = #{menuId}
AND LP.MENU_ID LIKE 'DP26%'
AND LP.STD_DT = TO_DATE(#{stdDt}, 'YYYYMMDDHH24MISS') AND SYSDATE BETWEEN LP.EXPO_START_DT AND LP.EXPO_END_DT AND LP.TENANT_ID = #{tenantId} AND LP.LIST_ID = #{listId} AND LT.EXPO_YN = LP.EXPO_YN ) A /* 채널 */ , UV_TB_DP_MULTIMDA_PROD B /* 채널 */ , TB_DP_PROD_RSHP C , TB_DP_MENU_CATEGORY_PROD_MAPG D , TB_DP_MENU_CATEGORY E , TB_DP_PROD F , TB_DP_TENANT_PROD G , UV_TB_DP_MULTIMDA_PROD H /* 에피소드 */ WHERE A.PROD_ID = B.PROD_ID AND 0 < NVL(B.EPSD_CNT, 0) AND C.PROD_ID = B.PROD_ID AND D.PROD_ID = C.PART_PROD_ID AND 'DP010802' = C.PROD_RSHP_CD AND 'Y' = D.USE_YN AND E.MENU_ID = D.MENU_ID AND 'Y' = E.USE_YN AND 'DP26' = E.TOP_MENU_ID AND F.PROD_ID = D.PROD_ID AND 'DP000203' = F.SVC_GRP_CD AND 'PD002502' = F.CONTENTS_TYPE_CD AND G.PROD_ID = F.PROD_ID AND #{tenantId} = G.TENANT_ID AND 'PD000403' = G.PROD_STATUS_CD AND 'Y' = G.EXPO_YN AND H.PROD_ID = C.PART_PROD_ID
AND H.SERIALLY_WKDY LIKE CONCAT('%', CONCAT((SELECT cd_nm FROM tb_cm_cd WHERE cd_id = #{weekDayCd}), '%')) /* DP010101 ~DP010107 :월~토*/
AND A.PROD_ID = C.PROD_ID AND C.PART_PROD_ID = F.PROD_ID AND H.PROD_ID = D.PROD_ID AND D.USE_YN = E.USE_YN AND D.USE_YN = G.EXPO_YN AND E.USE_YN = G.EXPO_YN AND D.PROD_ID = G.PROD_ID AND H.PROD_ID = F.PROD_ID AND G.PROD_ID = C.PART_PROD_ID ) A1 WHERE NO = 1 ) B1 WHERE RNUM BETWEEN #{offset} AND (#{offset}+#{count}-1) ORDER BY RNUM
