mapper.select:
SELECT /* DeviceModelListMapper.selectDeviceModelList, 2015-09-10 */ TOTAL_COUNT , RNUM , DEVICE_MODEL_CD , MODEL_NM AS DEVICE_MODEL_NM , MNFT_COMP_CD , MAKE_COMP_NM AS MNFT_COMP_NM , CMNT_COMP_CD , CMNT_COMP_NM , LIST_IMAGE_PATH , DTL_IMAGE_PATH FROM ( SELECT COUNT(*) OVER() AS TOTAL_COUNT , ROWNUM AS RNUM , A.DEVICE_MODEL_CD , A.MODEL_NM , A.MNFT_COMP_CD , SPSAC.FC_GET_CM_CD_NM(A.MNFT_COMP_CD, #{langCd}) AS MAKE_COMP_NM , A.CMNT_COMP_CD , SPSAC.FC_GET_CM_CD_NM(A.CMNT_COMP_CD, #{langCd}) AS CMNT_COMP_NM , REPLACE( REPLACE( REPLACE( NVL(A.LIST_IMG_PATH, '/images/no_imgage/phone_68_68.gif') , '/data/img', '/images'), '/data/wipi', '/wipiimages'), '/data/common', '/common') AS LIST_IMAGE_PATH , REPLACE( REPLACE( REPLACE( NVL(A.DTL_IMG_PATH, '/images/no_imgage/phone_68_68.gif') , '/data/img', '/images'), '/data/wipi', '/wipiimages'), '/data/common', '/common') AS DTL_IMAGE_PATH FROM TB_CM_DEVICE A WHERE 1=1 AND A.USE_YN = 'Y' AND NVL(A.VERIFY_DVC_YN, 'N') = 'N' AND DP_YN = 'Y' AND A.REP_DEVICE_YN = 'Y'
AND UPPER(A.MODEL_NM) NOT LIKE '%'||UPPER('테스트')||'%'
AND (UPPER(A.DEVICE_MODEL_CD) LIKE '%'||UPPER(#{deviceModelCd})||'%' OR UPPER(A.MODEL_NM) LIKE '%'||UPPER(#{deviceModelCd})||'%')
AND (UPPER(A.MODEL_NM) LIKE '%'||UPPER(#{deviceModelNm})||'%' OR UPPER(A.DEVICE_MODEL_CD) LIKE '%'||UPPER(#{deviceModelNm})||'%')
AND CMNT_COMP_CD = #{cmntCompCd}
AND MNFT_COMP_CD = #{mnftCompCd}
ORDER BY MNFT_COMP_CD, CMNT_COMP_CD, REG_DT DESC ) WHERE RNUM BETWEEN #{offset} AND #{count}
