SELECT /* BestContentsMapper.xml, selectBestContentsBookList, SAC 전시, 2015-04-13 */ LA.total_count , LA.rnum , LA.top_menu_id , LA.menu_id , LA.prod_id , LA.part_prod_id , LA.meta_clsf_cd , LA.expo_ord , LA.contents_type_cd FROM ( SELECT KA.* FROM ( SELECT JA.*, ROW_NUMBER() OVER(ORDER BY JA.expo_ord_always_yn DESC, JA.expo_ord, JA.expo_ord_sub) AS rnum FROM ( SELECT IA.*, COUNT(IA.prod_id) OVER() AS total_count FROM ( SELECT ROW_NUMBER() OVER ( PARTITION BY A.prod_id ORDER BY DECODE( L.book_clsf_cd, E.book_clsf_cd, 1, 2 ), L.chapter DESC, NVL( K.use_period_unit_cd, 'PD00310' ) DESC, J.last_deploy_dt DESC ) AS RN , B.top_menu_id , A.menu_id , A.prod_id , I.part_prod_id , A.expo_ord_always_yn , A.expo_ord , A.expo_ord_sub , D.meta_clsf_cd , D.contents_type_cd FROM ( SELECT tenant_id , prod_id , menu_id , expo_ord_always_yn , expo_ord , expo_ord_sub FROM TB_DP_LIST_PROD WHERE tenant_id = #{tenantId} /* header param : tenantId */ AND list_id = #{listId} /* param : listId */ AND expo_yn = 'Y' /* 전시여부 */ AND std_dt = TO_DATE(#{stdDt}, 'YYYYMMDDHH24MISS') /* 기준일시 */ AND SYSDATE BETWEEN expo_start_dt AND expo_end_dt ) A JOIN TB_DP_MENU_CATEGORY B ON( A.menu_id = B.menu_id AND B.use_yn = 'Y' ) JOIN TB_DP_PROD D /* 채널상품 */ ON( A.prod_id = D.prod_id AND D.contents_type_cd = 'PD002501' ) JOIN TB_DP_EBOOK_PROD E ON( A.prod_id = E.prod_id ) JOIN TB_DP_TENANT_PROD G ON( A.tenant_id = G.tenant_id AND A.prod_id = G.prod_id AND G.expo_yn = 'Y' ) JOIN TB_DP_PROD_RSHP I ON( A.prod_id = I.prod_id AND I.prod_rshp_cd = 'DP010802' ) /* DP010802 : 채널-에피소드 상품 관계 */ JOIN TB_DP_PROD J /* 에피소드상품 */ ON( I.part_prod_id = J.prod_id AND J.contents_type_cd = 'PD002502' ) JOIN TB_DP_USE_POLICY K /* 에피소드 상품에 대해서만 정책을 가질 수 있음 */ ON( J.use_policy_id = K.use_policy_id ) JOIN TB_DP_EBOOK_PROD L ON( I.PART_PROD_ID = L.PROD_ID ) WHERE 1 = 1 AND D.svc_grp_cd = 'DP000203' /* DP000203 : 멀티미디어 */ AND G.prod_status_cd = 'PD000403' /* PD000403 : 판매중 */ AND EXISTS ( SELECT 1 FROM TB_DP_SPRT_DEVICE SP1 JOIN TB_CM_DEVICE SP2 ON( SP1.device_model_cd = SP2.device_model_cd ) WHERE SP1.prod_id = I.part_prod_id AND SP2.device_model_cd IN ( 'ANY-PHONE-4MM', #{deviceModelCd} ) AND SP2.use_yn = 'Y' )
AND B.top_menu_id IN ('DP13', 'DP14') /* 탑메뉴ID */
AND B.top_menu_id = 'DP13' /* 탑메뉴ID */
AND B.top_menu_id = 'DP14' /* 탑메뉴ID */
AND D.prod_grd_cd IN #{prodGradeCd}
) IA WHERE IA.RN = 1 ) JA ) KA WHERE KA.RNUM BETWEEN #{offset} AND #{count} ) LA
