SELECT /* BestDownloadMapper.xml, selectBestDownloadAppList, SAC : 홍지호 , 2014-01-25 */ KA.TOTAL_COUNT /* 조회 건수 */ ,KA.TOP_MENU_ID /* TOP MENU ID */ ,KA.MENU_ID /* MENU_ID */ ,KA.PROD_ID /* 상품 ID */ FROM ( SELECT JA.* FROM ( SELECT ROWNUM AS RNUM ,IA.* FROM ( SELECT COUNT(*) OVER () AS TOTAL_COUNT ,A.EXPO_ORD ,A.TENANT_ID ,A.PROD_ID ,A.MENU_ID ,F.TOP_MENU_ID ,H.SUB_CONTENTS_ID FROM ( SELECT A.TENANT_ID ,A.PROD_ID ,A.MENU_ID ,A.EXPO_ORD FROM TB_DP_LIST_PROD A WHERE A.TENANT_ID = #{tenantId} /* header param : tenantId */ AND A.LIST_ID = #{listId} /* param : listId */ AND A.EXPO_YN = 'Y' /* 전시여부 */ AND TO_CHAR(A.STD_DT, 'YYYYMMDDHH24MISS') = #{stdDt} /* 기준일시 */ ) A ,TB_DP_PROD B ,TB_DP_MENU_CATEGORY_PROD_MAPG E ,TB_DP_MENU_CATEGORY F ,TB_DP_SPRT_DEVICE H ,TB_DP_TENANT_PROD I ,TB_CM_DEVICE K WHERE A.PROD_ID = B.PROD_ID AND B.SVC_GRP_CD ='DP000201' /* 서비스 그룹코드 : 앱 */
AND B.PROD_GRD_CD IN
#{prodGradeCd}
AND A.PROD_ID = E.PROD_ID AND A.MENU_ID = E.MENU_ID AND E.MENU_ID = F.MENU_ID AND F.USE_YN ='Y' AND A.PROD_ID = H.PROD_ID AND A.TENANT_ID = I.TENANT_ID AND A.PROD_ID = I.PROD_ID AND I.PROD_STATUS_CD = 'PD000403' /* PD000403 : 판매중 */ AND F.TOP_MENU_ID != 'DP02' /* DP02 : 폰꾸미기 */
AND F.TOP_MENU_ID = #{topMenuId}
AND H.DEVICE_MODEL_CD = K.DEVICE_MODEL_CD AND K.DEVICE_MODEL_CD = #{deviceModelCd} /* header param : 단말모델코드 */ AND K.USE_YN = 'Y' AND I.EXPO_YN = 'Y' /* 노출여부 */ ORDER BY A.EXPO_ORD ) IA ) JA WHERE JA.RNUM BETWEEN #{offset} AND #{count} /* param : offset, count */ ) KA
