SELECT /* FeatureCategoryMapper.xml, selectFeatureNewVodist, SAC 전시, 2014-02-10 */ B.TENANT_ID , B.MENU_ID , B.PROD_ID , B.TOP_MENU_ID , B.CONTENTS_TYPE_CD , B.TOTAL_COUNT FROM (SELECT A.TENANT_ID , A.MENU_ID , A.PROD_ID , A.TOP_MENU_ID , A.CONTENTS_TYPE_CD , COUNT(A.PROD_ID) OVER () AS TOTAL_COUNT --,ROW_NUMBER() OVER(ORDER BY A.EXPO_ORD_ALWAYS_YN DESC, A.EXPO_ORD, A.EXPO_ORD_SUB) AS RNUM
, ROW_NUMBER() OVER(ORDER BY A.REG_DT DESC, A.PROD_NM) AS RNUM /* 최신순 */
, ROW_NUMBER() OVER(ORDER BY NVL(A.PRCHS_CNT, 0) DESC, A.REG_DT DESC, A.PROD_NM) AS RNUM /* 다운로드순 */
, ROW_NUMBER() OVER(ORDER BY NVL(A.AVG_EVLU_SCORE, 0) DESC, A.REG_DT DESC, A.PROD_NM) AS RNUM /* 평점순 */
, ROW_NUMBER() OVER(ORDER BY A.REG_DT DESC, A.PROD_NM) AS RNUM /* 최신순 */
FROM (SELECT A.TENANT_ID , D.MENU_ID , A.PROD_ID , D.TOP_MENU_ID , E.META_CLSF_CD , 'PD002501' AS CONTENTS_TYPE_CD , A.EXPO_ORD_ALWAYS_YN , A.EXPO_ORD , A.EXPO_ORD_SUB , E.REG_DT , H.PRCHS_CNT , H.AVG_EVLU_SCORE , I.PROD_NM , ROW_NUMBER() OVER(PARTITION BY A.PROD_ID ORDER BY E.REG_DT DESC) AS RN FROM (SELECT LP.TENANT_ID , LP.PROD_ID , LP.MENU_ID , LP.EXPO_ORD_ALWAYS_YN , LP.EXPO_ORD , LP.EXPO_ORD_SUB , LP.ETC_CD FROM TB_DP_LIST_PROD LP /* 채널 */ , TB_DP_LIST LT WHERE LP.TENANT_ID = #{tenantId} /* 테넌트ID */ AND LP.LIST_ID = #{listId} /* TGR000000002 : 운영자신규 멀티미디어 */ AND LP.EXPO_YN = 'Y' /* Y : 노출 (노출여부) */ AND LP.STD_DT = TO_DATE(#{stdDt}, 'YYYYMMDDHH24MISS') AND SYSDATE BETWEEN LP.EXPO_START_DT AND LP.EXPO_END_DT AND LP.TENANT_ID = LT.TENANT_ID AND LP.LIST_ID = LT.LIST_ID AND LT.EXPO_YN = 'Y' ) A , TB_DP_PROD_RSHP B , TB_DP_MENU_CATEGORY_PROD_MAPG C , TB_DP_MENU_CATEGORY D , TB_DP_PROD E /* 에피소드 */ , TB_DP_TENANT_PROD F , TB_DP_SPRT_DEVICE G , TB_DP_TENANT_PROD_STATS H /* 채널 */ , TB_DP_PROD_DESC I /* 채널 */ WHERE A.PROD_ID = B.PROD_ID AND B.PROD_RSHP_CD = 'DP010802' AND A.MENU_ID = C.MENU_ID AND B.PART_PROD_ID = C.PROD_ID AND C.USE_YN = 'Y' AND C.MENU_ID = D.MENU_ID AND D.USE_YN = 'Y' AND D.TOP_MENU_ID = #{topMenuId}
/* 프리미엄 영화 */ AND A.ETC_CD IN ( 'DP003700', 'DP003701' )
/* 프리미엄 영화 외 */ AND A.ETC_CD NOT IN ( 'DP003700', 'DP003701' ) AND D.MENU_ID = #{menuId}
AND D.MENU_ID = #{menuId}
AND C.PROD_ID = E.PROD_ID AND E.SVC_GRP_CD = 'DP000203' AND E.CONTENTS_TYPE_CD = 'PD002502'
AND E.PROD_GRD_CD IN
#{prodGradeCd} /* 상품등급코드 */
AND E.PROD_ID = F.PROD_ID AND A.TENANT_ID = F.TENANT_ID AND F.PROD_STATUS_CD = 'PD000403' AND F.EXPO_YN = 'Y' AND F.PROD_ID = G.PROD_ID AND (G.DEVICE_MODEL_CD = #{deviceModelCd} OR G.DEVICE_MODEL_CD = #{anyDeviceModelCd}) /* 단말기 모델 코드 */ AND (CASE WHEN (SELECT VIDEO_DRM_SPRT_YN FROM TB_CM_DEVICE WHERE DEVICE_MODEL_CD = #{deviceModelCd} AND USE_YN = 'Y' ) = 'N' AND NVL(E.DRM_YN, 'N') = 'Y' THEN 'NOT SUPPORT' /* DRM 미지원 단말이고 에피소드 상품이 DRM 상품일 경우 노출 안함 */ ELSE CASE WHEN (SELECT SD_VIDEO_SPRT_YN FROM TB_CM_DEVICE WHERE DEVICE_MODEL_CD = #{deviceModelCd} AND USE_YN = 'Y' ) = 'Y' THEN 'SUPPORT' ELSE 'NOT SUPPORT' END END) = 'SUPPORT' AND A.PROD_ID = H.PROD_ID(+) AND A.TENANT_ID = H.TENANT_ID(+) AND B.PROD_ID = I.PROD_ID AND I.LANG_CD = #{langCd} ) A WHERE RN = 1 ) B WHERE RNUM BETWEEN #{offset} AND (#{offset}+#{count}-1) ORDER BY RNUM
