/* FeatureCategoryMapper.xml, selectTopMenuAppListByRecent, SAC : 서영배 , 2014-01-03 */ SELECT F_PROD.* , ST.DWLD_CNT , ST.PRCHS_CNT , NVL( ( CASE WHEN ( ST.AVG_EVLU_SCORE > 0 AND ST.AVG_EVLU_SCORE < 1 ) THEN 1 WHEN ST.AVG_EVLU_SCORE > 5 THEN 5 ELSE ROUND( ST.AVG_EVLU_SCORE , 1 ) END ) , 0 ) AS AVG_EVLU_SCORE FROM ( SELECT R_PROD.* FROM ( SELECT T_PROD.* , ROWNUM AS RNUM , COUNT(*) OVER () AS TOTAL_COUNT FROM ( SELECT ROW_NUMBER() OVER ( PARTITION BY P1.SELLER_MBR_NO ORDER BY LP.UPD_DT DESC ) AS RN , TP.TENANT_ID , MN.TOP_MENU_ID , (SELECT MENU_NM FROM TB_DP_MENU_CATEGORY_DESC WHERE MENU_ID = MN.UP_MENU_ID AND LANG_CD = #{langCd}) TOP_MENU_NM , MN.MENU_ID , (SELECT MENU_NM FROM TB_DP_MENU_CATEGORY_DESC WHERE MENU_ID = MN.MENU_ID AND LANG_CD = #{langCd}) MENU_NM , P1.PROD_ID , AP.AID , PS.PROD_NM , PS.PROD_BASE_DESC , TR.PROD_AMT , P1.PROD_GRD_CD , AP.PART_PARENT_CLSF_CD , P1.DRM_YN , SC.APK_PKG_NM , SC.APK_VER , SC.FILE_SIZE , AP.VER_MAJOR , AP.VER_MINOR , PI.FILE_PATH , PI.FILE_NM , P1.UPD_DT , LP.EXPO_ORD , LP.EXPO_ORD_ALWAYS_YN , LP.EXPO_ORD_SUB FROM TB_DP_LIST_PROD LP , TB_DP_MENU_CATEGORY_PROD_MAPG MP , TB_DP_MENU_CATEGORY MN , TB_DP_PROD P1 , TB_DP_PROD_DESC PS , TB_DP_TENANT_PROD TP , TB_DP_TENANT_PROD_PRICE TR , TB_DP_SUB_CONTENTS SC , TB_DP_APP_PROD AP , TB_DP_PROD_IMG PI , TB_DP_SPRT_DEVICE DV , TB_CM_DEVICE CD WHERE LP.TENANT_ID = #{tenantId} AND LP.LIST_ID = #{listId} AND LP.PROD_ID = P1.PROD_ID AND P1.PROD_ID = PS.PROD_ID AND PS.LANG_CD = #{langCd}
AND MN.TOP_MENU_ID = #{menuId}
AND MN.MENU_ID = MP.MENU_ID AND MP.PROD_ID = P1.PROD_ID AND TP.PROD_ID = P1.PROD_ID AND TP.TENANT_ID = LP.TENANT_ID AND TP.PROD_ID = LP.PROD_ID AND TP.PROD_STATUS_CD = 'PD000403' AND TP.EXPO_YN = 'Y' AND P1.PROD_ID = SC.PROD_ID AND P1.PROD_ID = AP.PROD_ID AND TP.PROD_ID = TR.PROD_ID AND TP.TENANT_ID = TR.TENANT_ID AND SYSDATE BETWEEN TR.APPLY_START_DT AND TR.APPLY_END_DT AND PI.PROD_ID = P1.PROD_ID AND PI.IMG_CD = 'DP000103' AND SC.PROD_ID = DV.PROD_ID AND SC.SUB_CONTENTS_ID = DV.SUB_CONTENTS_ID AND DV.DEVICE_MODEL_CD = CD.DEVICE_MODEL_CD AND CD.DEVICE_MODEL_CD = #{deviceModelCd} AND TO_CHAR(LP.STD_DT, 'YYYYMMDDHH24MISS') = #{stdDt} AND SYSDATE BETWEEN LP.EXPO_START_DT AND LP.EXPO_END_DT
AND P1.PROD_GRD_CD = #{prodGradeCd} /* 상품등급코드 */
AND P1.PROD_CHRG_YN = 'Y' /* 유료 */
AND P1.PROD_CHRG_YN = 'N' /* 무료 */
) T_PROD WHERE T_PROD.RN <= 2 ORDER BY T_PROD.EXPO_ORD_ALWAYS_YN DESC, T_PROD.EXPO_ORD, T_PROD.EXPO_ORD_SUB ) R_PROD WHERE RNUM BETWEEN #{offset} AND (#{offset}+#{count}-1) ) F_PROD LEFT OUTER JOIN TB_DP_TENANT_PROD_STATS ST ON F_PROD.PROD_ID = ST.PROD_ID AND F_PROD.TENANT_ID = ST.TENANT_ID

