SELECT /* eatureCategoryMapper.xml, selectCategoryEpubBestList, SAC 전시, 2014-02-10 */ A.TENANT_ID , A.MENU_ID , A.PROD_ID , A.PART_PROD_ID , A.TOP_MENU_ID , A.CONTENTS_TYPE_CD , A.TOTAL_COUNT FROM (SELECT A.TENANT_ID , D.MENU_ID , H.PROD_ID , H.PART_PROD_ID , D.TOP_MENU_ID , E.CONTENTS_TYPE_CD , COUNT(H.PART_PROD_ID) OVER () AS TOTAL_COUNT , ROW_NUMBER() OVER(ORDER BY B.EXPO_ORD_ALWAYS_YN DESC, B.EXPO_ORD, B.EXPO_ORD_SUB) AS RNUM FROM TB_DP_LIST A , TB_DP_LIST_PROD B , TB_DP_MENU_CATEGORY_PROD_MAPG C , TB_DP_MENU_CATEGORY D , TB_DP_PROD E , TB_DP_TENANT_PROD F , (SELECT SD.PROD_ID FROM TB_DP_SPRT_DEVICE SD , TB_CM_DEVICE CD WHERE SD.DEVICE_MODEL_CD = CD.DEVICE_MODEL_CD AND (SD.DEVICE_MODEL_CD = #{deviceModelCd} OR SD.DEVICE_MODEL_CD = #{anyDeviceModelCd}) AND CD.USE_YN = 'Y'
AND CD.EBOOK_SPRT_YN = 'Y'
AND CD.COMIC_SPRT_YN = 'Y'
) G , TB_DP_PROD_RSHP H WHERE A.LIST_ID = B.LIST_ID AND A.TENANT_ID = B.TENANT_ID AND A.EXPO_YN = 'Y' AND B.EXPO_YN = 'Y' AND SYSDATE BETWEEN B.EXPO_START_DT AND B.EXPO_END_DT AND A.TENANT_ID = #{tenantId} AND A.LIST_ID = #{listId} AND B.STD_DT = TO_DATE(#{stdDt}, 'YYYYMMDDHH24MISS') AND B.PROD_ID = C.PROD_ID AND B.MENU_ID = C.MENU_ID AND C.USE_YN = 'Y' AND C.MENU_ID = D.MENU_ID AND D.USE_YN = 'Y' AND D.TOP_MENU_ID = #{topMenuId}
/* 인기도서 일반 - 장르소설 / 작가출판>장르소설을 제외한 나머지 */ AND D.MENU_ID NOT IN (SELECT MENU_ID FROM TB_DP_MENU_CATEGORY WHERE (MENU_ID LIKE 'DP13004%' /* 장르소설 */ OR MENU_ID IN ('DP13027002', 'DP13027008', 'DP13027009', 'DP13027010' ) /* 작가출판 장르소설 */ ) )
/* 인기도서 판타지 - 장르소설>SF/판타지, 작가출판>장르소설-SF/판타지 */ AND D.MENU_ID IN ('DP13004003', 'DP13027010')
/* 인기도서 로맨스 - 장르소설>로맨스, 작가출판>장르소설-로맨스 */ AND D.MENU_ID IN ( 'DP13004001', 'DP13027008' )
/* 인기도서 무협 - 장르소설>무협, 작가출판>장르소설-무협 */ AND D.MENU_ID IN ( 'DP13004002', 'DP13027009' )
AND D.MENU_ID = 'DP14001' /* 인기코믹 무협 */
AND D.MENU_ID = 'DP14002' /* 인기코믹 순정 */
AND D.MENU_ID = 'DP14003' /* 인기코믹 액션 */
AND D.MENU_ID = 'DP14006' /* 인기코믹 드라마 */
AND C.PROD_ID = E.PROD_ID AND E.CONTENTS_TYPE_CD = 'PD002502'
AND E.PROD_CHRG_YN = 'Y' /* 유료 */
AND E.PROD_CHRG_YN = 'N' /* 무료 */
AND E.PROD_GRD_CD IN
#{prodGradeCd} /* 상품등급코드 */
AND E.META_CLSF_CD IN ('CT19', 'CT20') /* EBOOK */
AND E.PROD_ID = F.PROD_ID AND F.EXPO_YN = 'Y' AND F.PROD_STATUS_CD = 'PD000403' AND B.TENANT_ID = F.TENANT_ID AND F.PROD_ID = G.PROD_ID AND G.PROD_ID = H.PART_PROD_ID AND H.PROD_RSHP_CD = 'DP010802' ) A WHERE RNUM BETWEEN #{offset} AND (#{offset}+#{count}-1) ORDER BY RNUM
