mapper.select:
SELECT /* FeatureRecommendMapper.xml, selectRecommendOnedayList, SAC 전시 , 2014-02-28 */ aa.tenant_id /* 테넌트 ID */ , aa.one_seq /* 일련번호 */ , aa.expo_dt /* 노출 일자 */ , aa.expo_start_dt /* 노출 시작 일시 */ , aa.expo_end_dt /* 노출 종료 일시 */ , aa.part_chrgmony_app_yn /* 부분 유료화 앱 여부 */ , aa.free_item_amt /* 무료 아이템 금액 */ , aa.prod_realre_amt /* 상품 실재 금액 */ , aa.prod_offr_amt /* 상품 제공 금액 */ , aa.menu_id /* 메뉴 ID */ , aa.prod_id /* 상품 ID */ , aa.part_prod_id /* 부분 상품 ID */ , aa.top_menu_id /* 탑 메뉴 ID */ , aa.meta_clsf_cd /* 메타 구분 코드 */ , aa.svc_grp_cd /* 서비스 그룹 코드 */ , aa.svc_grp_nm /* 서비스 그룹 명 */ , aa.contents_type_cd /* 컨텐츠 타입 코드 */ FROM ( SELECT a.tenant_id , a.seq as one_seq , a.expo_dt , to_char(a.expo_start_dt,'YYYYMMDDHHmiss') AS expo_start_dt , to_char(a.expo_end_dt,'YYYYMMDDHHmiss') AS expo_end_dt , a.part_chrgmony_app_yn , NVL(a.free_item_amt, 0) AS free_item_amt , NVL(a.prod_realre_amt, 0) AS prod_realre_amt , NVL(a.prod_offr_amt, 0) AS prod_offr_amt , d.menu_id , b.prod_id , e.part_prod_id , d.top_menu_id , b.meta_clsf_cd , b.svc_grp_cd , FC_GET_CM_CD_NM(b.svc_grp_cd, #{langCd}) AS svc_grp_nm , b.contents_type_cd , ROW_NUMBER() OVER ( PARTITION BY a.seq ORDER BY e.reg_dt DESC) AS rn FROM tb_dp_oneday_one_app a /* 하루하나앱 */ , tb_dp_prod b /* 상품 */ , tb_dp_menu_category_prod_mapg c /* 메뉴 카탈로그 상품 매핑 */ , tb_dp_menu_category d /* 메뉴 카탈로그 */ , tb_dp_prod_rshp e /* 상품 관계 */ , tb_dp_tenant_prod g /* 테넌트 상품 */ WHERE a.prod_id = b.prod_id AND a.prod_id = g .prod_id AND a.prod_id = e.prod_id AND e.prod_rshp_cd = 'DP010802' AND b.prod_id = c.prod_id AND c.use_yn = 'Y' AND c.menu_id = d.menu_id AND d.use_yn = 'Y' AND a.tenant_id = #{tenantId} AND g .tenant_id = #{tenantId} AND a.use_yn = 'Y' AND g .expo_yn = 'Y'
AND c.menu_id = #{menuId}
AND b.prod_grd_cd in
#{prodGradeCd}
AND a.seq = #{seq}
AND a.expo_dt >= CASE WHEN TO_NUMBER(TO_CHAR(SYSDATE, 'HH24')) < 11 THEN TO_CHAR(SYSDATE -8, 'YYYYMMDD') ELSE TO_CHAR(SYSDATE -7, 'YYYYMMDD') END AND a.expo_dt <= CASE WHEN TO_NUMBER(TO_CHAR(SYSDATE, 'HH24')) < 11 then TO_CHAR(SYSDATE +3, 'YYYYMMDD') ELSE TO_CHAR(SYSDATE +4, 'YYYYMMDD') END
AND a.expo_dt >= #{periodStart}
AND a.expo_dt <= #{periodEnd}
) aa WHERE aa.rn = 1 ORDER BY aa.expo_dt
