mapper.select:
SELECT /* IsfAppCodiMapper.xml, getCategoryInfoList, 선호 카테고리 정보 조회(앱코디), 이태희/부르칸, 2014-11-18 */ A.MENU_ID, CASE WHEN A.MENU_ID = 'DP30' AND B.MENU_NM IS NULL THEN '앱' ELSE B.MENU_NM END MENU_NM FROM ( SELECT MENU_ID, MENU_ORD FROM ( SELECT 'DUMMY' AS MENU_ID, 0 AS MENU_ORD FROM DUAL
UNION ALL SELECT #{menu.menuId} AS MENU_ID, #{menu.menuOrd} AS MENU_ORD FROM DUAL
) WHERE MENU_ID != 'DUMMY' ) A, TB_DP_MENU_CATEGORY_DESC B WHERE A.MENU_ID = B.MENU_ID(+) AND B.LANG_CD(+) = #{tenantHeader.langCd} ORDER BY A.MENU_ORD

SELECT /* IsfAppCodiMapper.xml, getCategoryInfoList, 선호 카테고리 정보 조회(앱코디), 이태희/부르칸, 2014-11-18 */ PROD_ID, TOP_MENU_ID, 'PD002501' AS CONTENTS_TYPE_CD, PROD_ID AS PART_PROD_ID, CASE WHEN (RCD != '903001' AND RCD != '909001') AND (R_PROD_NM IS NULL OR R_MENU_NM IS NULL) THEN '요즘 HOT! Power User의 BEST 상품을 소개합니다.' ELSE CASE WHEN RCD = '901001' THEN '따끈따끈 ' || R_PROD_NM || '의 신작이 나왔어요!' WHEN RCD = '901002' THEN R_PROD_NM || '의 다음 이야기가 궁금하시다면? 지금 확인하세요!' WHEN RCD = '903001' THEN DECODE(APP_YN, 'Y', '앱', MENU_NM) || ' 매니아들이 선택한 오늘의 ' || DECODE(APP_YN, 'Y', '앱', MENU_NM) WHEN RCD = '904001' THEN R_PROD_NM || '를 좋아하셨다면, 이 ' || DECODE(APP_YN, 'Y', '앱', MENU_NM) || '도 좋아하실거에요.' WHEN RCD = '904002' THEN '나의 ' || DECODE(R_APP_YN, 'Y', '앱', R_MENU_NM) || ' MATE들이 추천하는 ' || DECODE(APP_YN, 'Y', '앱', MENU_NM) WHEN RCD = '909001' THEN 'Everyone''s Pick! 모두의 주목을 받은 BEST ' || DECODE(APP_YN, 'Y', '앱', MENU_NM) ELSE '요즘 HOT! Power User의 BEST 상품을 소개합니다.' END END AS RECM_REASON FROM ( SELECT A.RK, A.RID, A.RCD, B.PROD_ID, C.TOP_MENU_ID, D.MENU_NM, (SELECT PROD_NM FROM TB_DP_PROD_DESC WHERE PROD_ID = A.RID AND LANG_CD = #{tenantHeader.langCd} AND ROWNUM = 1) AS R_PROD_NM, (SELECT MENU_NM FROM TB_DP_PROD X, TB_DP_MENU_CATEGORY_DESC Y WHERE X.PROD_ID = A.RID AND X.TOP_MENU_ID = Y.MENU_ID AND Y.LANG_CD = #{tenantHeader.langCd} AND ROWNUM = 1) AS R_MENU_NM, CASE WHEN C.TOP_MENU_ID IN ('DP03', 'DP04', 'DP08') THEN 'Y' ELSE 'N' END AS APP_YN, (SELECT CASE WHEN C.TOP_MENU_ID IN ('DP03', 'DP04', 'DP08') THEN 'Y' ELSE 'N' END FROM TB_DP_PROD Z WHERE Z.PROD_ID = A.RID) AS R_APP_YN, ROW_NUMBER() OVER(PARTITION BY B.PROD_ID ORDER BY C.REG_DT DESC) AS RNUM FROM ( SELECT ID, RK, RID, RCD FROM ( /* ID : 추천상품ID, RK : 추천상품 순위, RID : 추천근거 상품ID, RCD : 추천근거 코드*/ SELECT 'DUMMY' AS ID, 0 AS RK, '' AS RID, '' AS RCD FROM DUAL
UNION ALL SELECT #{item.itemId} AS ID, #{item.itemOrd} AS RK, #{item.recmItemId} AS RID, #{item.recmReasonCd} AS RCD FROM DUAL
) WHERE ID != 'DUMMY' ) A, TB_DP_PROD_RSHP B, TB_DP_PROD C, TB_DP_MENU_CATEGORY_DESC D WHERE A.ID = B.PROD_ID AND B.PART_PROD_ID = C.PROD_ID AND C.TOP_MENU_ID = D.MENU_ID AND B.PROD_RSHP_CD = 'DP010802' AND D.LANG_CD = #{tenantHeader.langCd} ) WHERE RNUM = 1 ORDER BY RK
