mapper.select:
SELECT B.MENU_ID, B.MENU_NM FROM ( SELECT MENU_ID, MENU_ORD FROM ( SELECT 'DUMMY' AS MENU_ID, 0 AS MENU_ORD FROM DUAL
UNION ALL SELECT #{menu.menuId} AS MENU_ID, #{menu.menuOrd} AS MENU_ORD FROM DUAL
) WHERE MENU_ID != 'DUMMY' ) A, TB_DP_MENU_CATEGORY_DESC B WHERE A.MENU_ID = B.MENU_ID AND B.LANG_CD = #{tenantHeader.langCd} ORDER BY A.MENU_ORD

SELECT PROD_ID, TOP_MENU_ID, CONTENTS_TYPE_CD, PROD_ID AS PART_PROD_ID, CASE WHEN (RCD != '903001' AND RCD != '909001') AND (R_PROD_NM IS NULL OR R_MENU_NM IS NULL) THEN '요즘 HOT! Power User의 BEST 상품을 소개합니다.' ELSE CASE WHEN RCD = '901001' THEN '따끈따끈 ' || R_PROD_NM || '의 신작이 나왔어요!' WHEN RCD = '901002' THEN R_PROD_NM || '의 다음 이야기가 궁금하시다면? 지금 확인하세요!' WHEN RCD = '903001' THEN MENU_NM || ' 매니아들이 선택한 오늘의 ' || MENU_NM WHEN RCD = '904001' THEN R_PROD_NM || '를 좋아하셨다면, 이 ' || MENU_NM || '도 좋아하실거에요.' WHEN RCD = '904002' THEN '나의 ' || R_MENU_NM || ' MATE들이 추천하는 ' || MENU_NM WHEN RCD = '909001' THEN 'Everyone''s Pick! 모두의 주목을 받은 BEST ' || MENU_NM ELSE '요즘 HOT! Power User의 BEST 상품을 소개합니다.' END END AS RECM_REASON FROM ( SELECT A.RK, A.RID, A.RCD, B.PROD_ID, B.TOP_MENU_ID, B.CONTENTS_TYPE_CD, C.MENU_NM, (SELECT PROD_NM FROM TB_DP_PROD_DESC WHERE PROD_ID = RID AND LANG_CD = #{tenantHeader.langCd} AND ROWNUM = 1) AS R_PROD_NM, (SELECT MENU_NM FROM TB_DP_PROD X, TB_DP_MENU_CATEGORY_DESC Y WHERE X.PROD_ID = RID AND X.TOP_MENU_ID = Y.MENU_ID AND Y.LANG_CD = #{tenantHeader.langCd} AND ROWNUM = 1) AS R_MENU_NM FROM ( SELECT ID, RK, RID, RCD FROM ( /* ID : 추천상품ID, RK : 추천상품 순위, RID : 추천근거 상품ID, RCD : 추천근거 코드*/ SELECT 'DUMMY' AS ID, 0 AS RK, '' AS RID, '' AS RCD FROM DUAL
UNION ALL SELECT #{item.itemId} AS ID, #{item.itemOrd} AS RK, #{item.recmItemId} AS RID, #{item.recmReasonCd} AS RCD FROM DUAL
) WHERE ID != 'DUMMY' ) A, TB_DP_PROD B, TB_DP_MENU_CATEGORY_DESC C WHERE A.ID = B.PROD_ID AND B.TOP_MENU_ID = C.MENU_ID AND C.LANG_CD = #{tenantHeader.langCd} ) ORDER BY RK
