SELECT /* IsfThemeRecommendMapper.xml, getRecomendPkgList, Yun JuYoung, 2014-02-11 */ PKG.* , COUNT(*) OVER () AS TOTAL_COUNT FROM ( SELECT PKG_ID , PKG_NM , IMG2 AS PKG_IMG_POS FROM TB_DP_RECOM_PKG UP
, (
SELECT #{multiValue_id.id} ISF_ID, #{multiValue_id.order} ISF_ORDER FROM DUAL
) ISF
WHERE EXPO_YN = 'Y' AND ( SYSDATE BETWEEN EXPO_START_DT AND EXPO_END_DT OR ALWAYS_EXPO_YN = 'Y' )
AND PKG_ID = #{THEME_RECOMM_ID}
AND PKG_ID = ISF_ID ORDER BY ISF_ORDER
ORDER BY ATTR_PRTY_EXPO_YN DESC, EXPO_ORD, REG_DT
) PKG WHERE ROWNUM BETWEEN #{START_ROW} AND #{END_ROW}
