SELECT /* IsfThemeRecommendMapper.xml, getRecomendPkgList, 테마추천 목록 조회 , 윤주영/SK플래닛, 2014-02-11 */ PKG.* FROM ( SELECT ROWNUM AS RNN , PKG_ID , PKG_NM
, SET_IMG1 , SET_IMG2 , SET_IMG3
, IMG2 AS PKG_IMG_POS
, COUNT(*) OVER () AS TOTAL_COUNT FROM TB_DP_RECOM_PKG UP
, (
SELECT #{multiValueId.id} ISF_ID, #{multiValueId.order} ISF_ORDER FROM DUAL
) ISF
WHERE EXPO_YN = 'Y' AND ( SYSDATE BETWEEN EXPO_START_DT AND EXPO_END_DT OR ALWAYS_EXPO_YN = 'Y' )
AND PKG_ID = #{recommendId}
AND PKG_ID = ISF_ID ORDER BY ISF_ORDER
ORDER BY ATTR_PRTY_EXPO_YN DESC, EXPO_ORD, REG_DT
) PKG
WHERE RNN BETWEEN #{START_ROW} AND #{END_ROW}
