SELECT /* ProductInfoMapper.xml, getShoppingMetaInfo,에피소드ID 이용 상품조회(쇼핑) , 김형식/SK플래닛, 2014-01-07 */ KK.* FROM ( SELECT H.PROD_ID ,H.PART_PROD_ID ,CASE WHEN N.PROD_ID IS NOT NULL THEN M.PROD_NM ELSE C.CATALOG_NM END PROD_NM ,'thumbnail' AS fileType ,PI.FILE_PATH || PI.FILE_NM AS FILE_PATH ,J.TOP_MENU_ID ,(SELECT MENU_NM FROM TB_DP_MENU_CATEGORY_DESC WHERE MENU_ID = J.TOP_MENU_ID AND LANG_CD = #{lang} AND ROWNUM = 1 ) AS TOP_MENU_NM ,D.MENU_ID ,F.MENU_NM ,J.META_CLSF_CD ,J.REG_DT ,J.SELLER_MBR_NO ,J.EXPO_SELLER_NM ,J.EXPO_SELLER_TELNO ,J.EXPO_SELLER_EMAIL ,'' AS prodClsfCd ,J.PROD_GRD_CD ,J.USE_PERIOD_UNIT_CD ,J.USE_PERIOD ,J.DRM_YN ,CASE WHEN (SELECT COUNT(*) FROM TB_DP_SPRT_DEVICE WHERE PROD_ID = I.PROD_ID AND DEVICE_MODEL_CD = #{deviceModelNo}) > 0 THEN 'Y' ELSE 'N' END AS PHONE_SPRT_YN ,B.CATALOG_ID ,A.BRAND_ID ,A.BRAND_NM ,CASE WHEN N.PROD_ID IS NOT NULL THEN 'special:Tstore' ELSE 'shoppingStore' END couponType ,N.CPN_ID AS couponId ,(CASE WHEN I.PROD_CASE_CD='DP006301' THEN 'giftcard' WHEN I.PROD_CASE_CD='DP006302' THEN 'exchange' WHEN I.PROD_CASE_CD='DP006303' THEN 'delivery' END) AS PROD_CASE_CD ,J.CID ,(SELECT PROD_STATUS_CD FROM TB_DP_TENANT_PROD WHERE PROD_ID = H.PROD_ID AND EXPO_YN ='Y' AND TENANT_ID = #{tenantId} AND ROWNUM =1) chnlProdStatusCd ,K.PROD_STATUS_CD ,OPT.OPT1_NM ,OPT.OPT2_NM FROM TB_DP_SHPG_BRAND A ,TB_DP_SHPG_CATALOG B ,TB_DP_SHPG_CATALOG_DESC C ,TB_DP_MENU_SHPG_MAPG D ,TB_DP_MENU_CATEGORY E ,TB_DP_MENU_CATEGORY_DESC F ,TB_DP_PROD_CATALOG_MAPG G ,TB_DP_PROD_RSHP H ,TB_DP_SHPG_PROD I ,TB_DP_PROD J ,TB_DP_TENANT_PROD K ,TB_DP_TENANT_PROD_PRICE L ,TB_DP_PROD_DESC M ,(SELECT B1.PROD_ID , B1.CPN_ID FROM TB_DP_PROD_RSHP A1 ,TB_DP_SPRC_PROD B1 WHERE 1=1 AND A1.PART_PROD_ID = B1.PROD_ID
AND B1.PROD_ID = #{productBasicInfo.partProdId}
AND B1.EXPO_YN ='Y' AND B1.CPN_ID IS NOT NULL AND SYSDATE BETWEEN B1.SPRC_APPLY_START_DT AND B1.SPRC_APPLY_END_DT )N ,TB_DP_PROD_IMG PI ,TB_DP_PROD_OPT OPT WHERE 1=1 AND A.BRAND_ID = B.BRAND_ID AND B.CATALOG_ID = C.CATALOG_ID AND C.CATALOG_ID = D.CATALOG_ID AND D.MENU_ID = E.MENU_ID AND D.MENU_ID = F.MENU_ID AND B.CATALOG_ID = G.CATALOG_ID AND G.PROD_ID = H.PROD_ID AND H.PROD_ID <> H.PART_PROD_ID AND H.PART_PROD_ID = I.PROD_ID AND I.PROD_ID = J.PROD_ID AND I.PROD_ID = K.PROD_ID AND I.PROD_ID = L.PROD_ID AND I.PROD_ID = M.PROD_ID AND K.TENANT_ID = L.TENANT_ID AND C.CATALOG_ID = PI.PROD_ID AND PI.IMG_CD = #{imageCd} AND K.TENANT_ID = #{tenantId} /* INPUT 테넌트ID */ AND PI.EXPO_ORD ='1' AND I.PROD_ID = N.PROD_ID(+) AND I.PROD_ID = OPT.EPSD_PROD_ID(+) AND H.PROD_RSHP_CD =#{prodRshpCd} /*채널-에피소드 상품 관계*/ AND NVL(B.CHNL_CNT, 0) > 0 AND C.LANG_CD= #{lang} /* ko : 한국어 */ AND F.LANG_CD= #{lang} /* ko : 한국어 */ AND M.LANG_CD =#{lang} /* ko : 한국어 */ AND D.BASE_YN='Y' /* Y : 기본(기본여부) */ AND D.USE_YN ='Y' /* Y : 기본(기본여부) */ AND E.USE_YN='Y' /* Y : 기본(기본여부) */ AND G.BASE_YN='Y' /* Y : 기본(기본여부) */ AND G.USE_YN ='Y' /* Y : 기본(기본여부) */ AND NVL(I.B2B_PROD_YN, 'N') <> 'Y' AND K.EXPO_YN = 'Y' /* Y : 노출(노출여부) */ AND SYSDATE BETWEEN L.APPLY_START_DT AND L.APPLY_END_DT AND I.PROD_ID = #{productBasicInfo.partProdId} /* 상품ID */ ) KK
