SELECT /* MenuCategoryMapper.xml, selectShoppingSubCategoryList, SAC 전시 , 2014-03-07 */ COUNT(A.MENU_ID) OVER() AS TOTAL_COUNT , A.MENU_PROD_CNT , A.TENANT_ID , A.MENU_ID , A.SYSTEM_ID , A.MENU_NM , A.MENU_ENG_NM , A.MENU_DESC , A.MENU_DEPTH , A.INFR_MENU_YN , A.UP_MENU_ID , A.EXPO_ORD , A.TARGET_URL , A.SEARCH_FILE_PATH , A.SEARCH_FILE_NM , A.BODY_FILE_PATH , A.BODY_FILE_NM , A.BODY_FILE_SIZE , A.MAIN_ON_FILE_PATH , A.MAIN_ON_FILE_NM , A.MAIN_OFF_FILE_PATH , A.MAIN_OFF_FILE_NM , A.RANK_FILE_PATH , A.RANK_FILE_NM , A.USE_YN , A.MENU_ID_TYPE , A.LNB_FILE_PATH , A.LNB_FILE_NM , A.LNB_FILE_SIZE , A.PRE_CATEGORY_INFO , A.CATEGORY_MENU_YN , A.REG_ID , A.REG_DT , A.UPD_ID , A.UPD_DT FROM ( SELECT NVL(C.MENU_PROD_CNT, 0) MENU_PROD_CNT , A.TENANT_ID , A.TENANT_MENU_ID AS MENU_ID , A.SYSTEM_ID , A.MENU_NM , A.MENU_ENG_NM , A.MENU_DESC , A.MENU_DEPTH , A.INFR_MENU_YN , A.UP_MENU_ID , A.EXPO_ORD , A.TARGET_URL , A.SEARCH_FILE_PATH , A.SEARCH_FILE_NM , A.BODY_FILE_PATH , A.BODY_FILE_NM , NVL(A.BODY_FILE_SIZE, 0) AS BODY_FILE_SIZE , A.MAIN_ON_FILE_PATH , A.MAIN_ON_FILE_NM , A.MAIN_OFF_FILE_PATH , A.MAIN_OFF_FILE_NM , A.RANK_FILE_PATH , A.RANK_FILE_NM , A.USE_YN , A.MENU_ID_TYPE , A.LNB_FILE_PATH , A.LNB_FILE_NM , A.LNB_FILE_SIZE , A.PRE_CATEGORY_INFO , A.CATEGORY_MENU_YN , A.REG_ID , A.REG_DT , A.UPD_ID , A.UPD_DT , ( SELECT CASE WHEN MENU_DEPTH = 1 THEN 0 ELSE EXPO_ORD END EXPO_ORD FROM TB_DP_TENANT_MENU WHERE TENANT_MENU_ID = SUBSTR(A.TENANT_MENU_ID, 0, 7 ) AND SYSTEM_ID = A.SYSTEM_ID) AS RNK1 , ROW_NUMBER() OVER ( PARTITION BY SUBSTR( A.TENANT_MENU_ID, 0, 7 ) ORDER BY A.MENU_DEPTH , A.EXPO_ORD , A.MENU_NM ) AS RNK2 FROM TB_DP_TENANT_MENU A , TB_DP_MENU_CATEGORY B , ( SELECT A.MENU_ID , COUNT(A.MENU_ID) MENU_PROD_CNT FROM ( SELECT DISTINCT A.MENU_ID, B.CATALOG_ID FROM TB_DP_MENU_CATEGORY A , TB_DP_SHPG_CATALOG B , TB_DP_MENU_SHPG_MAPG C , TB_DP_PROD_CATALOG_MAPG D /* 채널 */ , TB_DP_PROD_RSHP E , TB_DP_SHPG_PROD F /* 에피 */ , TB_DP_TENANT_PROD G /* 에피 */ , TB_DP_TENANT_PROD_PRICE H /* 에피 */ /* 추가 */ , TB_DP_SPRT_DEVICE I , TB_CM_DEVICE J WHERE 1=1 AND A.MENU_ID = C.MENU_ID AND B.CATALOG_ID = C.CATALOG_ID AND B.CATALOG_ID = D.CATALOG_ID AND D.PROD_ID = E.PROD_ID AND E.PART_PROD_ID = F.PROD_ID AND E.PART_PROD_ID = G.PROD_ID AND E.PART_PROD_ID = H.PROD_ID AND E.PART_PROD_ID = I.PROD_ID AND G.TENANT_ID = H.TENANT_ID /* 추가 */ AND NVL(B.CHNL_CNT, 0) > 0 AND A.USE_YN ='Y' AND C.USE_YN ='Y' AND D.USE_YN ='Y' AND G.EXPO_YN = 'Y' AND NVL(F.B2B_PROD_YN, 'N') != 'Y' AND G.PROD_STATUS_CD = 'PD000403' AND SYSDATE BETWEEN H.APPLY_START_DT AND H.APPLY_END_DT /* 추가 */ AND J.SCL_SHPG_SPRT_YN = 'Y' AND (I.DEVICE_MODEL_CD = #{deviceModelCd} OR I.DEVICE_MODEL_CD = #{mmDeviceModelCd}) /* 단말모델코드 */ AND J.DEVICE_MODEL_CD = #{deviceModelCd} /* 단말모델코드 */ AND G.TENANT_ID = #{tenantId} /* 테넌트ID */ AND A.TOP_MENU_ID = #{topMenuId} /* 메뉴ID */ AND C.MENU_ID LIKE #{topMenuId}||'%' /* 메뉴ID */ ) A GROUP BY A.MENU_ID ) C WHERE A.TENANT_MENU_ID = B.MENU_ID AND A.TENANT_MENU_ID = C.MENU_ID(+) AND A.USE_YN = 'Y' AND B.USE_YN = 'Y' AND B.TOP_MENU_ID = #{topMenuId} AND A.TENANT_ID = #{tenantId} /* 테넌트ID */ AND A.SYSTEM_ID = #{systemId} /* 시스템ID */
AND NVL(A.CATEGORY_MENU_YN, 'Y') = 'Y'
) A ORDER BY A.RNK1, A.RNK2
