mapper.select:
SELECT /* MusicMainMapper.xml, getMusicMainList, SAC 전시, 2014-02-05 */ A.TENANT_ID , A.MENU_ID , A.PROD_ID , A.PART_PROD_ID , A.TOP_MENU_ID , A.META_CLSF_CD , A.CONTENTS_TYPE_CD , A.TOTAL_COUNT FROM (SELECT F.TENANT_ID , C.MENU_ID , I.PROD_ID , I.PART_PROD_ID , C.TOP_MENU_ID , D.META_CLSF_CD , D.CONTENTS_TYPE_CD , COUNT(I.PART_PROD_ID) OVER () AS TOTAL_COUNT
/* TOP Music */ , ROW_NUMBER() OVER(ORDER BY A.NOW_RANK ASC, E.PROD_NM) AS RNUM
/* 최신음악, 장르별 */ /* 인기도 */
, ROW_NUMBER() OVER(ORDER BY A.NOW_RANK ASC, G.ISSUE_DAY, E.PROD_NM) AS RNUM
/* 아티스트명 */
, ROW_NUMBER() OVER(ORDER BY E.ARTIST1_NM, E.PROD_NM) AS RNUM
/* 곡명 */
, ROW_NUMBER() OVER(ORDER BY E.PROD_NM, E.ARTIST1_NM) AS RNUM
/* Default */
, ROW_NUMBER() OVER(ORDER BY A.NOW_RANK ASC, G.ISSUE_DAY, E.PROD_NM) AS RNUM
/* 컬러링, 벨소리 */ , ROW_NUMBER() OVER(ORDER BY A.RANK_CHG_CNT ASC) AS RNUM
FROM TB_DP_MUSIC_CHART A /* 에피소드 */ , TB_DP_MENU_CATEGORY_PROD_MAPG B , TB_DP_MENU_CATEGORY C , TB_DP_PROD D , TB_DP_PROD_DESC E , TB_DP_TENANT_PROD F , TB_DP_MUSIC_PROD G , (SELECT DISTINCT SD.PROD_ID FROM TB_DP_SPRT_DEVICE SD , TB_CM_DEVICE CD WHERE SD.DEVICE_MODEL_CD = CD.DEVICE_MODEL_CD AND (SD.DEVICE_MODEL_CD = #{deviceModelCd} OR SD.DEVICE_MODEL_CD = #{anyDeviceModelCd}) AND CD.USE_YN = 'Y' AND CD.MUSIC_SPRT_YN = 'Y' ) H , TB_DP_PROD_RSHP I WHERE A.CHART_CLSF_CD = #{chartClsfCd} AND A.RANK_START_DAY = #{stdDt} AND A.PROD_ID = B.PROD_ID AND B.USE_YN = 'Y' AND B.MENU_ID = C.MENU_ID AND C.USE_YN = 'Y'
AND C.MENU_ID = #{menuId}
AND C.TOP_MENU_ID = 'DP16'
AND B.PROD_ID = D.PROD_ID AND D.CONTENTS_TYPE_CD = 'PD002502' AND D.META_CLSF_CD = 'CT25' AND D.PROD_ID = E.PROD_ID AND E.LANG_CD = #{langCd} AND E.PROD_ID = F.PROD_ID AND F.TENANT_ID = #{tenantId} AND F.EXPO_YN = 'Y' AND F.PROD_STATUS_CD = 'PD000403' AND A.PROD_ID = G.PROD_ID AND A.PROD_ID = H.PROD_ID AND H.PROD_ID = I.PART_PROD_ID AND I.PROD_RSHP_CD = 'DP010802' ) A WHERE RNUM BETWEEN #{offset} AND (#{offset}+#{count}-1) ORDER BY RNUM
