SELECT /* OpenApiMapper.xml, searchSupportGameCenter, SAC 전시, 2014-03-03 */ P.PROD_ID ,DAP.AID ,DPD.PROD_NM ,DECODE(DTP.PROD_STATUS_CD,'PD000403',NVL(( SELECT 'Y' FROM TB_DP_SPRT_DEVICE SH WHERE SH.DEVICE_MODEL_CD = #{deviceModelNo} AND SH.PROD_ID = P.PROD_ID AND ROWNUM = 1 ), 'N') ,'N') AS DEVICE_SUPPORT ,NVL(DTPP.PROD_AMT , 0) AS PROD_AMT ,NVL(PA.AVG_EVLU_SCORE, 0) AS AVG_EVLU_SCORE ,NVL(PA.PATICPERS_CNT, 0) AS PATICPERS_CNT ,P.PROD_GRD_CD AS PROD_GRD_CD ,(SELECT APK_PKG_NM FROM TB_DP_SUB_CONTENTS SF WHERE SF.PROD_ID = P.PROD_ID AND SF.SUB_CONTENTS_ID = SH.SUB_CONTENTS_ID ) AS APK_PKG_NM ,NVL(PA.PRCHS_CNT, 0) AS PRCHS_CNT /*다운로드 수*/ ,P.TOP_MENU_ID AS TOP_MENU_ID ,(SELECT MENU_NM FROM TB_DP_MENU_CATEGORY_DESC WHERE MENU_ID = P.TOP_MENU_ID AND LANG_CD = #{langCd} AND ROWNUM = 1) AS TOP_MENU_NM ,CP.MENU_ID AS MENU_ID /* 카테고리 코드*/ ,DMCD.MENU_NM AS MENU_NM /* 카테코리 명*/ FROM TB_DP_PROD P ,TB_DP_APP_PROD DAP ,TB_DP_PROD_DESC DPD ,TB_DP_TENANT_PROD_STATS PA ,TB_DP_TENANT_PROD DTP ,TB_DP_TENANT_PROD_PRICE DTPP ,TB_DP_SPRT_DEVICE SH ,TB_DP_MENU_CATEGORY_PROD_MAPG CP ,TB_DP_MENU_CATEGORY DMC ,TB_DP_MENU_CATEGORY_DESC DMCD ,TB_DP_PROD_RSHP C WHERE P.PROD_ID = C.PART_PROD_ID AND P.PROD_ID = DAP.PROD_ID AND P.PROD_ID = DPD.PROD_ID AND C.PROD_ID = CP.PROD_ID AND CP.MENU_ID = DMC.MENU_ID AND CP.MENU_ID = DMCD.MENU_ID AND P.PROD_ID = DTP.PROD_ID AND DTP.PROD_ID = DTPP.PROD_ID AND C.PROD_RSHP_CD = 'DP010802' AND PA.TENANT_ID = #{tenantId} AND DTP.TENANT_ID = #{tenantId} AND DTP.TENANT_ID = DTPP.TENANT_ID AND PA.PROD_ID(+) = P.PROD_ID AND SH.PROD_ID(+) = P.PROD_ID AND SH.DEVICE_MODEL_CD(+) = #{deviceModelNo} AND CP.USE_YN = 'Y' AND DMCD.LANG_CD = #{langCd} AND DPD.LANG_CD = #{langCd}
AND P.PROD_ID IN
#{productId}
AND (DAP.PART_PARENT_CLSF_CD <> 'PD012302' /* 부분유료화 모상품만 */ OR DAP.PART_PARENT_CLSF_CD IS NULL )
ORDER BY INSTR
