SELECT /* OpenApi.xml, searchDownloadBestList, 이석희, 2014-02-10 */ KA.* , DECODE(INSTR(KB.FILE_PATH, '.', 1, 1), 0, KB.FILE_PATH || KB.FILE_NM, KB.FILE_PATH) AS IMAGE_PATH , NVL(KB.FILE_SIZE, 0) AS IMAGE_SIZE FROM ( SELECT JA.* FROM ( SELECT ROWNUM AS RNUM ,IA.* FROM ( SELECT COUNT(*) OVER () AS TOTAL_COUNT , A.PROD_ID AS PART_PROD_ID , NVL(A.DWLD_QTY, 0) AS PRCHS_CNT , B.EXPO_SELLER_NM , B.CONTENTS_TYPE_CD , C.PROD_NM , C.PROD_BASE_DESC , D.AID , D.PKG_NM AS APK_PKG_NM , NVL(G.PROD_AMT, 0) AS PROD_AMT , NVL(F.AVG_EVLU_SCORE, 0) AS AVG_EVLU_SCORE , NVL(F.PATICPERS_CNT, 0) AS PATICPERS_CNT FROM ( SELECT PROD_ID , TENANT_ID , DWLD_QTY FROM TB_DP_LIST_PROD WHERE LIST_ID = #{listId} AND TENANT_ID =#{tenantId} AND TO_CHAR(STD_DT, 'YYYYMMDDHH24MISS') = #{stdDt} /* 기준일시 */ AND EXPO_YN ='Y' ) A , TB_DP_PROD B , TB_DP_PROD_DESC C , TB_DP_APP_PROD D , TB_DP_TENANT_PROD E , TB_DP_TENANT_PROD_STATS F , TB_DP_TENANT_PROD_PRICE G WHERE A.PROD_ID = B.PROD_ID AND A.PROD_ID = C.PROD_ID AND A.PROD_ID = D.PROD_ID AND A.PROD_ID = E.PROD_ID AND A.PROD_ID = F.PROD_ID AND A.PROD_ID = G.PROD_ID AND A.TENANT_ID = E.TENANT_ID AND A.TENANT_ID = F.TENANT_ID AND A.TENANT_ID = G.TENANT_ID AND B.TOP_MENU_ID != 'DP02' AND B.SVC_GRP_CD = 'DP000201' /* 서비스 그룹코드 : 앱 */ AND D.PLAT_CLSF_CD = 'PD005606' /* 안드로이드용 앱 */ AND E.PROD_STATUS_CD ='PD000403' /* ======================================= INPUT PARAMS ======================================= */
AND B.REG_ID = #{inquiryValue} -- 개발자 ID
AND B.SELLER_MBR_NO IN
#{sellerKey}
AND B.PROD_CHRG_YN = #{prodCharge} --유무료 구분 ORDER BY A.DWLD_QTY , C.PROD_NM ) IA ) JA WHERE JA.RNUM BETWEEN #{offset} AND #{count} /* param : offset, count */ ) KA , TB_DP_PROD_IMG KB WHERE KA.PART_PROD_ID = KB.PROD_ID AND KB.EXPO_ORD = '1' AND KB.LANG_CD = #{langCd} AND KB.IMG_CD = #{imageCd}

SELECT /* OpenApiMapper.xml, getPreviewImageList, 이석희, 2012-08-22 */ PROD_ID , COUNT(*) OVER() AS CUR_CNT , FILE_PATH AS PREVIEW_IMAGE_PATH , IMAGE_SIZE AS PREVIEW_IMAGE_SIZE , IMAGE_CODE FROM ( /* Game/App 미리보기 */ SELECT KA.FILE_PATH , KA.IMAGE_SIZE , KA.PROD_ID , KA.IMG_CD AS IMAGE_CODE FROM ( SELECT JA.AID , JA.PROD_ID , JA.IMG_CD , NVL(JB.FILE_PATH, '/data4/android/noimage_158_211_20131112163000.png') AS FILE_PATH , NVL(JB.FILE_SIZE, 0) AS IMAGE_SIZE , ROW_NUMBER() OVER (PARTITION BY JA.AID ORDER BY JA.AID, DECODE(JB.FILE_PATH, NULL, 1, 0), JA.IMG_CD) RNUM FROM ( SELECT IA.AID, IA.PROD_ID, IB.IMG_CD FROM ( SELECT A.PROD_ID, B.AID FROM TB_DP_PROD A ,TB_DP_APP_PROD B WHERE A.TOP_MENU_ID NOT IN( 'DP02', 'DP09', 'DP14', 'DP17', 'DP18' ) AND A.PROD_ID = B.PROD_ID
AND A.PROD_ID =#{prodId}
) IA , ( SELECT 'DP000116' AS IMG_CD FROM DUAL UNION ALL SELECT 'DP0001D7' AS IMG_CD FROM DUAL UNION ALL SELECT 'DP000117' AS IMG_CD FROM DUAL UNION ALL SELECT 'DP0001D8' AS IMG_CD FROM DUAL UNION ALL SELECT 'DP000118' AS IMG_CD FROM DUAL UNION ALL SELECT 'DP0001D9' AS IMG_CD FROM DUAL UNION ALL SELECT 'DP000119' AS IMG_CD FROM DUAL UNION ALL SELECT 'DP0001E0' AS IMG_CD FROM DUAL UNION ALL SELECT 'DP0001C5' AS IMG_CD FROM DUAL UNION ALL SELECT 'DP0001E1' AS IMG_CD FROM DUAL UNION ALL SELECT 'DP0001C8' AS IMG_CD FROM DUAL UNION ALL SELECT 'DP0001E2' AS IMG_CD FROM DUAL UNION ALL SELECT 'DP0001D1' AS IMG_CD FROM DUAL UNION ALL SELECT 'DP0001E3' AS IMG_CD FROM DUAL UNION ALL SELECT 'DP0001D4' AS IMG_CD FROM DUAL UNION ALL SELECT 'DP0001E4' AS IMG_CD FROM DUAL ) IB ) JA , TB_DP_PROD_IMG JB WHERE JA.PROD_ID = JB.PROD_ID(+) AND JA.IMG_CD = JB.IMG_CD(+) ) KA )

SELECT TOTAL_COUNT AS TOTAL_COUNT ,PP.PROD_ID AS PROD_ID ,PP.PROD_NM AS PROD_NM ,PP.PROD_BASE_DESC AS PROD_BASE_DESC ,PP.TOP_MENU_ID AS TOP_MENU_ID ,(SELECT MENU_NM FROM TB_DP_MENU_CATEGORY_DESC WHERE MENU_ID = PP.TOP_MENU_ID AND LANG_CD = #{langCd} AND ROWNUM = 1) AS TOP_MENU_NM ,(SELECT MENU_NM FROM TB_DP_MENU_CATEGORY_DESC WHERE MENU_ID = PP.MENU_ID AND LANG_CD = #{langCd} AND ROWNUM = 1) AS MENU_NM ,NVL(PP.PROD_AMT, 0) AS PROD_AMT ,NVL(PP.AVG_EVLU_SCORE, 0) AS AVG_EVLU_SCORE ,NVL(PP.PATICPERS_CNT, 0) AS PATICPERS_CNT ,NVL(PP.PRCHS_CNT, 0) AS PRCHS_CNT ,PP.PROD_GRD_CD AS PROD_GRD_CD ,(SELECT DECODE(INSTR(PI.FILE_PATH, '.', 1, 1), 0, PI.FILE_PATH || PI.FILE_NM, PI.FILE_PATH) FROM TB_DP_PROD_IMG PI WHERE PP.PROD_ID = PI.PROD_ID AND PI.IMG_CD = 'DP000125' AND PI.LANG_CD = #{langCd} AND ROWNUM = 1) AS IMAGE_PATH FROM ( SELECT COUNT(*) OVER() AS TOTAL_COUNT ,ROW_NUMBER() OVER(ORDER BY DECODE(#{orderedBy}, 'popular', Z.AVG_EVLU_SCORE, Z.REG_DT) DESC) AS RONUM ,Z.* FROM ( SELECT ROW_NUMBER( ) OVER( PARTITION BY PR.PROD_ID ORDER BY P.REG_DT DESC ) AS RNUM ,P.PROD_ID ,PD.PROD_NM ,PD.PROD_BASE_DESC ,P.TOP_MENU_ID ,MCP.MENU_ID ,TPP.PROD_AMT ,P.PROD_GRD_CD ,TO_CHAR(P.REG_DT, 'YYYYMMDD') AS REG_DT ,NVL(TV.AVG_EVLU_SCORE, 0) AS AVG_EVLU_SCORE ,NVL(TV.PATICPERS_CNT, 0) AS PATICPERS_CNT ,NVL(TV.PRCHS_CNT, 0) AS PRCHS_CNT FROM TB_DP_PROD P , TB_DP_APP_PROD APD , TB_DP_PROD_DESC PD , TB_DP_PROD_RSHP PR , TB_DP_MENU_CATEGORY_PROD_MAPG MCPM , TB_DP_MENU_CATEGORY MCP , TB_DP_TENANT_PROD TP , TB_DP_TENANT_PROD_PRICE TPP , TB_DP_TENANT_PROD_STATS TV WHERE P.PROD_ID = PR.PROD_ID AND P.PROD_ID = APD.PROD_ID AND P.PROD_ID = MCPM.PROD_ID AND MCPM.MENU_ID = MCP.MENU_ID AND P.PROD_ID = PR.PART_PROD_ID AND P.PROD_ID = PD.PROD_ID AND P.PROD_ID = TP.PROD_ID AND P.PROD_ID = TPP.PROD_ID AND TP.TENANT_ID = TPP.TENANT_ID AND TP.TENANT_ID = TV.TENANT_ID AND P.PROD_ID = TV.PROD_ID(+) AND PR.PROD_RSHP_CD = 'DP010802' AND TP.PROD_STATUS_CD = 'PD000403' AND P.SVC_GRP_CD = 'DP000201' AND PD.LANG_CD = #{langCd} AND P.TOP_MENU_ID != 'DP02'
AND P.TOP_MENU_ID IN
#{topMenuId}
AND TP.EXPO_YN = 'Y' AND SYSDATE BETWEEN TPP.APPLY_START_DT AND TPP.APPLY_END_DT AND MCPM.USE_YN = 'Y' AND UPPER( PD.PROD_NM ) like '%'||UPPER( RTRIM(#{searchKeyword}) )||'%' AND TP.TENANT_ID = #{tenantId} ) Z WHERE RNUM = 1 ) PP WHERE RONUM BETWEEN #{offset} AND #{count}
AND ROWNUM <= #{count}

       SELECT  
                NVL(APPPROD.AID, '') AS AID
		       ,PRODDESC.PROD_NM
		       ,PROD.REG_DT
		       ,TENANTPROD.PROD_STATUS_CD
         FROM  TB_DP_PROD PROD 
		      ,TB_DP_APP_PROD APPPROD
		      ,TB_DP_PROD_DESC PRODDESC
		      ,TB_DP_TENANT_PROD TENANTPROD
		WHERE PROD.PROD_ID = APPPROD.PROD_ID
		 AND  PROD.PROD_ID = PRODDESC.PROD_ID
		 AND  PRODDESC.LANG_CD = #{tenantHeader.langCd}
		 AND  PROD.PROD_ID = TENANTPROD.PROD_ID
		 AND  TENANTPROD.TENANT_ID = #{tenantHeader.tenantId}
		  
		 AND  PROD.REG_ID = #{req.sellerId}
		 AND  PROD.SELLER_MBR_NO = #{req.sellerKey}
		 
		 AND  (APPPROD.PART_PARENT_CLSF_CD = #{partClsfCd} OR APPPROD.PART_PARENT_CLSF_CD is null)
		 
		 AND  APPPROD.AID = #{req.searchValue}
		 
		 
		 AND  PRODDESC.PROD_NM = #{req.searchValue}

SELECT PRODDESC.PROD_NM ,PROD.EXPO_SELLER_NM ,PRODDESC.PROD_BASE_DESC ,( #{appUrl} || PROD.TOP_MENU_ID || CHR (38) || 'insProdId=' || PROD.PROD_ID || CHR (38) || 'prodGrdCd=' || PROD.PROD_GRD_CD || CHR (38) || 't_top=' || PROD.TOP_MENU_ID) AS WEB_URL ,PROD.REG_DT ,APPPROD.AID ,IMG.FILE_PATH AS IMAGE_PATH FROM TB_DP_PROD PROD ,TB_DP_PROD_DESC PRODDESC ,TB_DP_PROD_IMG IMG ,TB_DP_APP_PROD APPPROD WHERE PROD.PROD_ID = PRODDESC.PROD_ID AND PROD.PROD_ID = IMG.PROD_ID AND PROD.PROD_ID = APPPROD.PROD_ID AND IMG.IMG_CD = #{imageCd} AND IMG.EXPO_ORD = '1' AND IMG.LANG_CD = #{tenantHeader.langCd} AND (APPPROD.PART_PARENT_CLSF_CD = 'PD012301' OR APPPROD.PART_PARENT_CLSF_CD is null)
AND PROD.REG_ID = #{req.sellerId} AND PROD.SELLER_MBR_NO = #{req.sellerKey}
AND APPPROD.AID = #{req.aid}

SELECT  
         NVL(APPPROD.AID, '') AS AID
        ,PRODDESC.PROD_NM
        ,PROD.REG_DT
        ,TENANTPROD.PROD_STATUS_CD
  FROM  TB_DP_PROD PROD 
       ,TB_DP_APP_PROD APPPROD
       ,TB_DP_PROD_DESC PRODDESC
       ,TB_DP_TENANT_PROD TENANTPROD
 WHERE PROD.PROD_ID = APPPROD.PROD_ID
  AND  PROD.PROD_ID = PRODDESC.PROD_ID
  AND  PRODDESC.LANG_CD = #{tenantHeader.langCd}
  AND  PROD.PROD_ID = TENANTPROD.PROD_ID
  AND  TENANTPROD.TENANT_ID = #{tenantHeader.tenantId}
  AND  PROD.REG_ID = #{req.sellerId}
  AND  PROD.SELLER_MBR_NO = #{req.sellerKey}
  AND  (APPPROD.PART_PARENT_CLSF_CD = #{partClsfCd} OR APPPROD.PART_PARENT_CLSF_CD is null)
  
  AND  APPPROD.AID = #{req.searchValue}
  
  
  AND  PRODDESC.PROD_NM = #{req.searchValue}

SELECT P.PROD_ID ,DAP.AID ,DPD.PROD_NM ,DECODE(DTP.PROD_STATUS_CD,'PD000403',NVL(( SELECT 'Y' FROM TB_DP_SPRT_DEVICE SH WHERE SH.DEVICE_MODEL_CD = #{deviceModelNo} AND SH.PROD_ID = P.PROD_ID AND ROWNUM = 1 ), 'N') ,'N') AS DEVICE_SUPPORT ,NVL(DTPP.PROD_AMT , 0) AS PROD_AMT ,NVL(PA.AVG_EVLU_SCORE, 0) AS AVG_EVLU_SCORE ,NVL(PA.PATICPERS_CNT, 0) AS PATICPERS_CNT ,P.PROD_GRD_CD AS PROD_GRD_CD ,(SELECT APK_PKG_NM FROM TB_DP_SUB_CONTENTS SF WHERE SF.PROD_ID = P.PROD_ID AND SF.SUB_CONTENTS_ID = SH.SUB_CONTENTS_ID ) AS APK_PKG_NM ,NVL(PA.PRCHS_CNT, 0) AS PRCHS_CNT /*다운로드 수*/ ,P.TOP_MENU_ID AS TOP_MENU_ID ,(SELECT MENU_NM FROM TB_DP_MENU_CATEGORY_DESC WHERE MENU_ID = P.TOP_MENU_ID AND LANG_CD = #{langCd} AND ROWNUM = 1) AS TOP_MENU_NM ,CP.MENU_ID AS MENU_ID /* 카테고리 코드*/ ,DMCD.MENU_NM AS MENU_NM /* 카테코리 명*/ FROM TB_DP_PROD P ,TB_DP_APP_PROD DAP ,TB_DP_PROD_DESC DPD ,TB_DP_TENANT_PROD_STATS PA ,TB_DP_TENANT_PROD DTP ,TB_DP_TENANT_PROD_PRICE DTPP ,TB_DP_SPRT_DEVICE SH ,TB_DP_MENU_CATEGORY_PROD_MAPG CP ,TB_DP_MENU_CATEGORY DMC ,TB_DP_MENU_CATEGORY_DESC DMCD ,TB_DP_PROD_RSHP C WHERE P.PROD_ID = C.PART_PROD_ID AND P.PROD_ID = DAP.PROD_ID AND P.PROD_ID = DPD.PROD_ID AND C.PROD_ID = CP.PROD_ID AND CP.MENU_ID = DMC.MENU_ID AND CP.MENU_ID = DMCD.MENU_ID AND P.PROD_ID = DTP.PROD_ID AND DTP.PROD_ID = DTPP.PROD_ID AND C.PROD_RSHP_CD = 'DP010802' AND PA.TENANT_ID = #{tenantId} AND DTP.TENANT_ID = #{tenantId} AND DTP.TENANT_ID = DTPP.TENANT_ID AND PA.PROD_ID(+) = P.PROD_ID AND SH.PROD_ID(+) = P.PROD_ID AND SH.DEVICE_MODEL_CD(+) = #{deviceModelNo} AND CP.USE_YN = 'Y' AND DMCD.LANG_CD = #{langCd} AND DPD.LANG_CD = #{langCd}
AND P.PROD_ID IN
#{productId}
AND (DAP.PART_PARENT_CLSF_CD != 'PD012302' /* 부분유료화 모상품만 */ OR DAP.PART_PARENT_CLSF_CD IS NULL )
