SELECT /* OtherServiceGroupMapper.XML, selectOtherServiceGroupList, SAC 전시, 2014-01-27 */ COUNT(JA.PROD_ID) OVER () AS TOTAL_COUNT , JA.PROD_ID , JA.CONTENTS_TYPE_CD , JA.CONTENTS_TYPE_NM , JA.TOP_MENU_ID , JA.TOP_MENU_NM , JA.SVC_GRP_CD , JA.META_CLSF_CD , JA.PROD_GRD_CD , JA.PROD_CHRG_YN FROM ( SELECT TDP.PROD_ID , TDP.CONTENTS_TYPE_CD , DECODE(TDP.CONTENTS_TYPE_CD,'PD002501','채널','PD002502','에피소드') AS CONTENTS_TYPE_NM , TDP.TOP_MENU_ID , (SELECT MENU_NM FROM TB_DP_MENU_CATEGORY_DESC WHERE MENU_ID = TDP.TOP_MENU_ID AND LANG_CD = 'ko') TOP_MENU_NM , TDP.SVC_GRP_CD , TDP.META_CLSF_CD , TDP.PROD_GRD_CD , TDP.PROD_CHRG_YN , ROWNUM FROM TB_DP_PROD TDP WHERE 1=1
AND TDP.PROD_ID IN
#{item}
UNION ALL SELECT * FROM( SELECT A.CATALOG_ID AS PROD_ID , C.CONTENTS_TYPE_CD , DECODE(C.CONTENTS_TYPE_CD,'PD002501','채널','PD002502','에피소드') AS CONTENTS_TYPE_NM , C.TOP_MENU_ID , (SELECT MENU_NM FROM TB_DP_MENU_CATEGORY_DESC WHERE MENU_ID = C.TOP_MENU_ID AND LANG_CD = 'ko') TOP_MENU_NM , C.SVC_GRP_CD , C.META_CLSF_CD , C.PROD_GRD_CD , C.PROD_CHRG_YN , ROW_NUMBER() OVER ( PARTITION BY A.CATALOG_ID ORDER BY B.PROD_ID) AS RNUM FROM TB_DP_PROD_CATALOG_MAPG A, TB_DP_PROD_RSHP B, TB_DP_PROD C WHERE 1=1
AND A.CATALOG_ID IN
#{item}
AND A.PROD_ID = B.PROD_ID AND B.PROD_ID = C.PROD_ID ) LA WHERE LA.RNUM = 1 ) JA
ORDER BY instr
#{item}

SELECT /* OtherServiceGroupMapper.XML, selectPartProdId, 정희원/SKP, 2014-07-08 */
    RSHP.PART_PROD_ID AS EPSD_ID
FROM TB_DP_PROD_RSHP RSHP
  WHERE 1=1
    AND RSHP.PROD_ID = #{chnlId}
    AND RSHP.PROD_RSHP_CD = #{rshpCd}
