SELECT /* RelatedProductMapper.xml, selectMusicMetaInfo, SAC 전시 , 2014-03-08 */ F.ARTIST1_ID AS ARTIST_ID , E.PROD_ID , E.PART_PROD_ID , D.MENU_ID , B.MENU_NM , B.MENU_DESC , A.TOP_MENU_ID , C.MENU_NM AS TOP_MENU_NM , C.MENU_DESC AS TOP_MENU_DESC , G.OUTSD_CONTENTS_ID , F.PROD_NM , F.PROD_BASE_DESC , F.PROD_DTL_DESC , H.PROD_AMT , H.PROD_NET_AMT , I.META_CLSF_CD , I.PROD_GRD_CD , F.ARTIST1_NM , F.ARTIST2_NM , F.ARTIST3_NM , G.CHNL_COMP_NM , G.AGENCY_NM , G.ISSUE_DAY , 'Y' AS MP3_SPRT_YN , NVL(G.BELL_SPRT_YN, 'N') AS BELL_SPRT_YN , NVL(G.COLORRING_SPRT_YN, 'N') AS COLORRING_SPRT_YN , CONCAT(J.FILE_PATH, J.FILE_NM) AS FILE_PATH , J.FILE_NM , J.FILE_SIZE , #{productBasicInfo.contentsTypeCd} as CONTENTS_TYPE_CD , K.PROD_STATUS_CD , I.REG_DT , NVL(L.PATICPERS_CNT, 0) AS PATICPERS_CNT , NVL(L.PRCHS_CNT, 0) AS DWLD_CNT , NVL(L.PRCHS_CNT, 0) AS PRCHS_CNT
, NVL((CASE WHEN (L.AVG_EVLU_SCORE > 0 AND L.AVG_EVLU_SCORE < 1) THEN 1 WHEN L.AVG_EVLU_SCORE > 5 THEN 5 ELSE ROUND(L.AVG_EVLU_SCORE, 1) END), 0) AS AVG_EVLU_SCORE
FROM TB_DP_MENU_CATEGORY A , TB_DP_MENU_CATEGORY_DESC B /* 서브메뉴 */ , TB_DP_MENU_CATEGORY_DESC C /* 탑메뉴 */ , TB_DP_MENU_CATEGORY_PROD_MAPG D , TB_DP_PROD_RSHP E , TB_DP_PROD_DESC F /* 에피 */ , TB_DP_MUSIC_PROD G /* 에피 */ , TB_DP_TENANT_PROD_PRICE H /* 에피 */ , TB_DP_PROD I /* 에피 */ , TB_DP_PROD_IMG J /* 에피 */ , TB_DP_TENANT_PROD K /* 에피 */ , TB_DP_TENANT_PROD_STATS L /* 채널 */ WHERE 1=1 AND A.MENU_ID = B.MENU_ID AND A.TOP_MENU_ID = C.MENU_ID AND A.MENU_ID = D.MENU_ID AND D.PROD_ID = E.PART_PROD_ID AND E.PART_PROD_ID = F.PROD_ID AND E.PART_PROD_ID = G.PROD_ID AND E.PART_PROD_ID = H.PROD_ID AND E.PART_PROD_ID = I.PROD_ID AND E.PROD_ID = J.PROD_ID(+) AND E.PART_PROD_ID = K.PROD_ID AND E.PROD_ID = L.PROD_ID(+)
AND B.LANG_CD = C.LANG_CD AND B.LANG_CD = F.LANG_CD AND H.TENANT_ID = K.TENANT_ID AND SYSDATE BETWEEN H.APPLY_START_DT AND H.APPLY_END_DT AND A.USE_YN = 'Y' AND D.USE_YN = 'Y' AND K.EXPO_YN = 'Y' AND E.PROD_RSHP_CD = 'DP010802' AND I.CONTENTS_TYPE_CD = 'PD002502' AND A.TOP_MENU_ID = #{productBasicInfo.topMenuId} AND B.LANG_CD = #{tenantHeader.langCd} AND j.LANG_CD(+) = #{tenantHeader.langCd}
AND E.PROD_ID = #{productBasicInfo.prodId}
AND E.PART_PROD_ID = #{productBasicInfo.partProdId}
AND J.IMG_CD(+) = #{imageCd} AND H.TENANT_ID = #{tenantHeader.tenantId} AND L.TENANT_ID(+) = #{tenantHeader.tenantId} AND ROWNUM = 1
