SELECT /* RelatedProductMapper.xml, selectMusicMetaInfo, SAC : 유시혁 , 2014-03-06 */ CP.PROD_ID , CP.PART_PROD_ID , PD.ARTIST1_ID AS ARTIST_ID , C.MENU_ID , CD.MENU_NM , CD.MENU_DESC , C.TOP_MENU_ID , CD2.MENU_NM AS TOP_MENU_NM , CD2.MENU_DESC AS TOP_MENU_DESC , MP.OUTSD_CONTENTS_ID -- 외부_컨텐츠_ID , PD.PROD_NM , PD.PROD_BASE_DESC , PD.PROD_DTL_DESC , TPP.PROD_AMT , TPP.PROD_NET_AMT , P.PROD_GRD_CD , P.META_CLSF_CD , PD.ARTIST1_NM , PD.ARTIST2_NM , PD.ARTIST3_NM , MP.CHNL_COMP_NM , MP.AGENCY_NM , MP.ISSUE_DAY , 'Y' AS MP3_SPRT_YN , MP.BELL_SPRT_YN , MP.COLORRING_SPRT_YN , CONCAT(PI.FILE_PATH, PI.FILE_NM) AS FILE_PATH , PI.FILE_NM , PI.FILE_SIZE , P.CONTENTS_TYPE_CD , TP.PROD_STATUS_CD , P.REG_DT , NVL(TPS.PATICPERS_CNT, 0) AS PATICPERS_CNT , NVL(TPS.PRCHS_CNT, 0) AS DWLD_CNT , NVL(TPS.PRCHS_CNT, 0) AS PRCHS_CNT
, NVL((CASE WHEN (TPS.AVG_EVLU_SCORE > 0 AND TPS.AVG_EVLU_SCORE < 1) THEN 1 WHEN TPS.AVG_EVLU_SCORE > 5 THEN 5 ELSE ROUND(TPS.AVG_EVLU_SCORE, 1) END), 0) AS AVG_EVLU_SCORE
FROM UV_TB_DP_MULTIMDA_PROD MP , TB_DP_PROD P , TB_DP_PROD_DESC PD -- 채널 , TB_DP_PROD_RSHP CP , TB_DP_TENANT_PROD TP , TB_DP_TENANT_PROD_PRICE TPP , TB_DP_MENU_CATEGORY_PROD_MAPG CPM , TB_DP_MENU_CATEGORY C , TB_DP_MENU_CATEGORY_DESC CD , TB_DP_MENU_CATEGORY_DESC CD2 , UV_TB_DP_MULTIMDA_PROD MP2 , TB_DP_PROD P2 , TB_DP_TENANT_PROD TP2 , TB_DP_PROD_IMG PI , TB_DP_TENANT_PROD_STATS TPS WHERE MP.PROD_ID = CP.PART_PROD_ID AND P.PROD_ID = CP.PART_PROD_ID AND CPM.PROD_ID = CP.PART_PROD_ID AND C.MENU_ID = CPM.MENU_ID AND CD.MENU_ID = C.MENU_ID AND CD2.MENU_ID = C.TOP_MENU_ID AND CD2.LANG_CD = CD.LANG_CD AND P2.PROD_ID = CP.PROD_ID -- CHANNEL PROD ID AND P2.META_CLSF_CD = P.META_CLSF_CD AND TP2.PROD_ID = P2.PROD_ID AND TP2.TENANT_ID = TP.TENANT_ID AND TP2.PROD_STATUS_CD = TP.PROD_STATUS_CD AND TP2.EXPO_YN = TP.EXPO_YN AND MP2.PROD_ID = P2.PROD_ID AND TP.PROD_ID = P.PROD_ID AND TPP.PROD_ID = TP.PROD_ID AND TPP.TENANT_ID = TP.TENANT_ID AND SYSDATE BETWEEN TPP.APPLY_START_DT AND TPP.APPLY_END_DT AND PD.PROD_ID = P.PROD_ID AND PI.PROD_ID = CP.PROD_ID AND TP2.PROD_ID = TPS.PROD_ID(+) AND TP2.TENANT_ID = TPS.TENANT_ID(+) AND CP.PROD_RSHP_CD = 'DP010802' AND TP.EXPO_YN = 'Y' -- 노출 여부 AND P2.CONTENTS_TYPE_CD = 'PD002501' -- 채널 AND P.CONTENTS_TYPE_CD = 'PD002502' -- 에피소드 AND CPM.USE_YN = 'Y' AND C.USE_YN = 'Y' ----------------------------------------------------------------------------- -- INPUT PARAMETER -----------------------------------------------------------------------------
AND CP.PROD_ID = #{productBasicInfo.prodId}
AND CP.PART_PROD_ID = #{productBasicInfo.partProdId}
AND P.TOP_MENU_ID = #{productBasicInfo.topMenuId} AND PI.IMG_CD = #{imageCd} AND TP.TENANT_ID = #{tenantHeader.tenantId} AND PD.LANG_CD = #{tenantHeader.langCd} AND CD.LANG_CD = #{tenantHeader.langCd} AND ROWNUM = 1
