/* ShoppingMapper.xml.getFeatureProductList,쇼핑/인기 추천 상품 리스트 조회, 김형식/SK플래닛 , 2014-01-07 */ SELECT * FROM ( SELECT ROW_NUMBER()OVER(ORDER BY REG_DT DESC, CATALOG_NM ) AS RNUM ,COUNT(CATALOG_ID) OVER() AS TOTALCOUNT ,CATALOG_ID AS catagoryId ,CATALOG_NM AS catagoryName ,PROD_NET_AMT AS prodNetAmt ,PROD_AMT AS prodAmt ,DC_RATE AS dcRate ,DC_AMT AS dcAmt ,DLV_PROD_YN AS dlvProdYn ,BRAND_ID AS brandId ,BRAND_NM AS brandName ,MENU_ID AS menuId ,MENU_NM AS menuName ,TOP_MENU_ID as upMenuId ,(SELECT MENU_NM FROM TB_DP_MENU_CATEGORY_DESC WHERE MENU_ID = TOP_MENU_ID AND LANG_CD = 'ko' AND ROWNUM = 1 ) AS upMenuName ,NO AS no ,NEW_YN AS newYn ,REG_DT AS regDt ,TENANT_ID AS tenentId ,PROD_ID as prodId ,PART_PROD_ID as partProdId ,PRCHS_QTY as prchsQty ,DWLD_QTY as dwldQty ,EXPO_ORD as expoOrd ,PROD_GRD_CD as prodGrdCd ,APPLY_START_DT as applyStartDt ,APPLY_END_DT as applyEndDt ,DECODE(PROD_CASE_CD , 'DP006301', 'giftcard' , 'DP006302', 'exchange' , 'DP006303', 'delivery' ) AS prodCaseCd ,FILE_PATH || FILE_NM as filePos FROM ( SELECT * FROM ( SELECT B.CATALOG_ID ,C.CATALOG_NM ,NVL(L.PROD_NET_AMT, 0) AS PROD_NET_AMT ,NVL(L.PROD_AMT, 0) AS PROD_AMT ,NVL(N.SPRC_DC_RATE, L.DC_RATE) AS DC_RATE ,NVL(N.SPRC_PRICE, L.PROD_AMT) AS DC_AMT ,A.BRAND_ID ,A.BRAND_NM ,D.MENU_ID ,F.MENU_NM ,ROW_NUMBER() OVER(PARTITION BY B.CATALOG_ID ORDER BY NVL(N.SPRC_PRICE, L.PROD_AMT) ASC) as NO ,CASE WHEN (SYSDATE - 7) <= I.REG_DT THEN 'Y' ELSE 'N' END AS NEW_YN ,I.REG_DT ,K.TENANT_ID ,H.PROD_ID ,H.PART_PROD_ID ,I.DLV_PROD_YN ,NVL(M.PRCHS_QTY, 0) PRCHS_QTY ,NVL(M.DWLD_QTY, 0) DWLD_QTY ,M.EXPO_ORD ,J.PROD_GRD_CD ,L.APPLY_START_DT ,L.APPLY_END_DT ,I.PROD_CASE_CD ,E.TOP_MENU_ID ,PI.FILE_PATH ,PI.FILE_NM FROM TB_DP_SHPG_BRAND A ,TB_DP_SHPG_CATALOG B ,TB_DP_SHPG_CATALOG_DESC C ,TB_DP_MENU_SHPG_MAPG D ,TB_DP_MENU_CATEGORY E ,TB_DP_MENU_CATEGORY_DESC F ,TB_DP_PROD_CATALOG_MAPG G ,TB_DP_PROD_RSHP H ,TB_DP_SHPG_PROD I ,TB_DP_PROD J ,TB_DP_TENANT_PROD K ,TB_DP_TENANT_PROD_PRICE L ,TB_DP_PROD_IMG PI ,( SELECT TDLP.TENANT_ID , TDLP.LIST_ID , TDLP.MENU_ID , TDLP.PROD_ID , TDLP.EXPO_ORD , TDLP.PRCHS_QTY , TDLP.DWLD_QTY FROM TB_DP_LIST_PROD TDLP ,TB_DP_LIST TDL WHERE 1=1 AND TDLP.TENANT_ID = TDL.TENANT_ID AND TDLP.LIST_ID = TDL.LIST_ID AND TDLP.TENANT_ID = #{tenantId} /* 테넌트ID */ AND TDLP.LIST_ID = #{listId} /* ADM000000014 : 쇼핑 추천, RNK000000006 쇼핑 인기 */ AND TDLP.EXPO_YN = 'Y' /* Y : 노출 (노출여부) */ AND TDL.EXPO_YN = 'Y' /* Y : 노출 (노출여부) */ AND TO_CHAR(TDLP.STD_DT, 'YYYYMMDDHH24MISS') = #{stdDt} AND SYSDATE BETWEEN TDLP.EXPO_START_DT AND TDLP.EXPO_END_DT ) M ,(SELECT B1.PROD_ID , B1.SPRC_DC_RATE , B1.SPRC_PRICE FROM TB_DP_PROD_CATALOG_MAPG A1 ,TB_DP_SPRC_PROD B1 WHERE 1=1 AND A1.PROD_ID = B1.PROD_ID AND B1.EXPO_YN ='Y' AND B1.CPN_ID IS NOT NULL AND SYSDATE BETWEEN B1.SPRC_APPLY_START_DT AND B1.SPRC_APPLY_END_DT )N WHERE 1=1 AND A.BRAND_ID = B.BRAND_ID AND B.CATALOG_ID = C.CATALOG_ID AND C.CATALOG_ID = D.CATALOG_ID AND D.MENU_ID = E.MENU_ID AND D.MENU_ID = F.MENU_ID AND B.CATALOG_ID = G.CATALOG_ID AND G.PROD_ID = H.PROD_ID AND H.PART_PROD_ID = I.PROD_ID AND I.PROD_ID = J.PROD_ID AND I.PROD_ID = K.PROD_ID AND I.PROD_ID = L.PROD_ID AND K.TENANT_ID = M.TENANT_ID
AND B.CATALOG_ID = M.PROD_ID
AND I.PROD_ID = M.PROD_ID
AND B.CATALOG_ID = PI.PROD_ID AND PI.IMG_CD=#{imageCd} AND I.PROD_ID = N.PROD_ID(+) AND H.PROD_RSHP_CD ='DP010802' /*채널-에피소드 상품 관계*/ AND C.LANG_CD= #{langCd} /* ko : 한국어 */ AND F.LANG_CD= #{langCd} /* ko : 한국어 */ AND D.BASE_YN='Y' /* Y : 기본(기본여부) */ AND D.USE_YN ='Y' /* Y : 기본(기본여부) */ AND E.USE_YN='Y' /* Y : 기본(기본여부) */ AND G.BASE_YN='Y' /* Y : 기본(기본여부) */ AND G.USE_YN ='Y' /* Y : 기본(기본여부) */ AND NVL(I.B2B_PROD_YN, 'N') != 'Y' AND K.PROD_STATUS_CD = 'PD000403' /* PD000403 : 판매중 */ AND K.EXPO_YN = 'Y' /* Y : 노출(노출여부) */ AND SYSDATE BETWEEN L.APPLY_START_DT AND L.APPLY_END_DT AND K.TENANT_ID = #{tenantId} /* INPUT 테넌트ID */
AND J.PROD_CHRG_YN =#{prodCharge}
AND J.PROD_GRD_CD =#{prodGradeCd}
AND NOT EXISTS ( SELECT 1 FROM TB_DP_SPRT_DEVICE DV ,TB_CM_DEVICE CM WHERE 1=1 AND DV.DEVICE_MODEL_CD = CM.DEVICE_MODEL_CD AND CM.SCL_SHPG_SPRT_YN = 'Y' /*쇼핑 상품 단말 지원 여부*/ AND DV.DEVICE_MODEL_CD = #{deviceModelCd} AND PROD_ID = G.PROD_ID ) ) WHERE NO =1 ) ORDER BY EXPO_ORD ASC, CATALOG_NM ASC ) WHERE RNUM BETWEEN #{offset} AND #{count}

/* ShoppingMapper.xml.getNewProductList,최신 상품 리스트 조회, 김형식/SK플래닛 , 2014-01-07 */ SELECT * FROM ( SELECT ROW_NUMBER()OVER(ORDER BY REG_DT DESC, CATALOG_NM ) AS RNUM ,COUNT(CATALOG_ID) OVER() AS TOTALCOUNT ,CATALOG_ID AS catagoryId ,CATALOG_NM AS catagoryName ,PROD_NET_AMT AS prodNetAmt ,PROD_AMT AS prodAmt ,DC_RATE AS dcRate ,DC_AMT AS dcAmt ,DLV_PROD_YN AS dlvProdYn ,BRAND_ID AS brandId ,BRAND_NM AS brandName ,MENU_ID AS menuId ,MENU_NM AS menuName ,TOP_MENU_ID as upMenuId ,(SELECT MENU_NM FROM TB_DP_MENU_CATEGORY_DESC WHERE MENU_ID = TOP_MENU_ID AND LANG_CD = 'ko' AND ROWNUM = 1 ) AS upMenuName ,NO AS no ,NEW_YN AS newYn ,REG_DT AS regDt ,TENANT_ID AS tenentId ,PROD_ID as prodId ,PART_PROD_ID as partProdId ,PRCHS_CNT as prchsQty ,PROD_GRD_CD as prodGrdCd ,APPLY_START_DT as applyStartDt ,APPLY_END_DT as applyEndDt ,DECODE(PROD_CASE_CD , 'DP006301', 'giftcard' , 'DP006302', 'exchange' , 'DP006303', 'delivery' ) AS prodCaseCd ,FILE_PATH || FILE_NM as filePos FROM ( SELECT * FROM ( SELECT B.CATALOG_ID ,C.CATALOG_NM ,NVL(L.PROD_NET_AMT, 0) AS PROD_NET_AMT ,NVL(L.PROD_AMT, 0) AS PROD_AMT ,NVL(N.SPRC_DC_RATE, L.DC_RATE) AS DC_RATE ,NVL(N.SPRC_PRICE, L.PROD_AMT) AS DC_AMT ,A.BRAND_ID ,A.BRAND_NM ,D.MENU_ID ,F.MENU_NM ,ROW_NUMBER() OVER(PARTITION BY B.CATALOG_ID ORDER BY NVL(N.SPRC_PRICE, L.PROD_AMT) ASC) as NO ,CASE WHEN (SYSDATE - 7) <= I.REG_DT THEN 'Y' ELSE 'N' END AS NEW_YN ,I.REG_DT ,K.TENANT_ID ,H.PROD_ID ,H.PART_PROD_ID ,I.DLV_PROD_YN ,NVL(M.PRCHS_CNT, 0) PRCHS_CNT ,J.PROD_GRD_CD ,L.APPLY_START_DT ,L.APPLY_END_DT ,I.PROD_CASE_CD ,E.TOP_MENU_ID ,PI.FILE_PATH ,PI.FILE_NM FROM TB_DP_SHPG_BRAND A ,TB_DP_SHPG_CATALOG B ,TB_DP_SHPG_CATALOG_DESC C ,TB_DP_MENU_SHPG_MAPG D ,TB_DP_MENU_CATEGORY E ,TB_DP_MENU_CATEGORY_DESC F ,TB_DP_PROD_CATALOG_MAPG G ,TB_DP_PROD_RSHP H ,TB_DP_SHPG_PROD I ,TB_DP_PROD J ,TB_DP_TENANT_PROD K ,TB_DP_TENANT_PROD_PRICE L ,TB_DP_PROD_IMG PI ,(SELECT PROD_ID ,PRCHS_CNT FROM TB_DP_TENANT_PROD_STATS WHERE TENANT_ID = #{tenantId} /* INPUT 테넌트ID */ ) M ,(SELECT B1.PROD_ID , B1.SPRC_DC_RATE , B1.SPRC_PRICE FROM TB_DP_PROD_CATALOG_MAPG A1 ,TB_DP_SPRC_PROD B1 WHERE 1=1 AND A1.PROD_ID = B1.PROD_ID AND B1.EXPO_YN ='Y' AND B1.CPN_ID IS NOT NULL AND SYSDATE BETWEEN B1.SPRC_APPLY_START_DT AND B1.SPRC_APPLY_END_DT )N WHERE 1=1 AND A.BRAND_ID = B.BRAND_ID AND B.CATALOG_ID = C.CATALOG_ID AND C.CATALOG_ID = D.CATALOG_ID AND D.MENU_ID = E.MENU_ID AND D.MENU_ID = F.MENU_ID AND B.CATALOG_ID = G.CATALOG_ID AND G.PROD_ID = H.PROD_ID AND H.PART_PROD_ID = I.PROD_ID AND I.PROD_ID = J.PROD_ID AND I.PROD_ID = K.PROD_ID AND I.PROD_ID = L.PROD_ID AND I.PROD_ID = M.PROD_ID(+) AND I.PROD_ID = N.PROD_ID(+) AND B.CATALOG_ID = PI.PROD_ID AND PI.IMG_CD=#{imageCd} AND H.PROD_RSHP_CD ='DP010802' /*채널-에피소드 상품 관계*/ AND C.LANG_CD= #{langCd} /* ko : 한국어 */ AND F.LANG_CD= #{langCd} /* ko : 한국어 */ AND D.BASE_YN='Y' /* Y : 기본(기본여부) */ AND D.USE_YN ='Y' /* Y : 기본(기본여부) */ AND E.USE_YN='Y' /* Y : 기본(기본여부) */ AND G.BASE_YN='Y' /* Y : 기본(기본여부) */ AND G.USE_YN ='Y' /* Y : 기본(기본여부) */ AND NVL(I.B2B_PROD_YN, 'N') != 'Y' AND K.PROD_STATUS_CD = 'PD000403' /* PD000403 : 판매중 */ AND K.EXPO_YN = 'Y' /* Y : 노출(노출여부) */ AND SYSDATE BETWEEN L.APPLY_START_DT AND L.APPLY_END_DT AND K.TENANT_ID = #{tenantId} /* INPUT 테넌트ID */
AND J.PROD_CHRG_YN =#{prodCharge}
AND J.PROD_GRD_CD =#{prodGradeCd}
AND NOT EXISTS ( SELECT 1 FROM TB_DP_SPRT_DEVICE DV ,TB_CM_DEVICE CM WHERE 1=1 AND DV.DEVICE_MODEL_CD = CM.DEVICE_MODEL_CD AND CM.SCL_SHPG_SPRT_YN = 'Y' /*쇼핑 상품 단말 지원 여부*/ AND DV.DEVICE_MODEL_CD = #{deviceModelCd} AND PROD_ID = G.PROD_ID ) ) WHERE NO =1 ) ORDER BY REG_DT DESC, CATALOG_NM ASC ) WHERE RNUM BETWEEN #{offset} AND #{count}

/* ShoppingMapper.xml.getSubProductList,쇼핑 세부카테고리 상품 조회, 김형식/SK플래닛 , 2014-01-07 */ SELECT * FROM ( SELECT ROW_NUMBER()OVER(ORDER BY REG_DT DESC, CATALOG_NM ) AS RNUM ,COUNT(CATALOG_ID) OVER() AS TOTALCOUNT ,CATALOG_ID AS catagoryId ,CATALOG_NM AS catagoryName ,PROD_NET_AMT AS prodNetAmt ,PROD_AMT AS prodAmt ,DC_RATE AS dcRate ,DC_AMT AS dcAmt ,DLV_PROD_YN AS dlvProdYn ,BRAND_ID AS brandId ,BRAND_NM AS brandName ,MENU_ID AS menuId ,MENU_NM AS menuName ,TOP_MENU_ID as upMenuId ,(SELECT MENU_NM FROM TB_DP_MENU_CATEGORY_DESC WHERE MENU_ID = TOP_MENU_ID AND LANG_CD = 'ko' AND ROWNUM = 1 ) AS upMenuName ,NO AS no ,NEW_YN AS newYn ,REG_DT AS regDt ,TENANT_ID AS tenentId ,PROD_ID as prodId ,PART_PROD_ID as partProdId ,PRCHS_CNT as prchsQty ,EXPO_ORD as expoOrd ,PROD_GRD_CD as prodGrdCd ,APPLY_START_DT as applyStartDt ,APPLY_END_DT as applyEndDt ,DECODE(PROD_CASE_CD , 'DP006301', 'giftcard' , 'DP006302', 'exchange' , 'DP006303', 'delivery' ) AS prodCaseCd ,FILE_PATH || FILE_NM as filePos
FROM ( SELECT * FROM ( SELECT B.CATALOG_ID ,C.CATALOG_NM ,NVL(L.PROD_NET_AMT, 0) AS PROD_NET_AMT ,NVL(L.PROD_AMT, 0) AS PROD_AMT ,NVL(N.SPRC_DC_RATE, L.DC_RATE) AS DC_RATE ,NVL(N.SPRC_PRICE, L.PROD_AMT) AS DC_AMT ,A.BRAND_ID ,A.BRAND_NM ,D.MENU_ID ,F.MENU_NM ,ROW_NUMBER() OVER(PARTITION BY B.CATALOG_ID ORDER BY NVL(N.SPRC_PRICE, L.PROD_AMT) ASC) as NO ,CASE WHEN (SYSDATE - 7) <= I.REG_DT THEN 'Y' ELSE 'N' END AS NEW_YN ,I.REG_DT ,K.TENANT_ID ,H.PROD_ID ,H.PART_PROD_ID ,I.DLV_PROD_YN
,NVL(M.PRCHS_CNT, 0) PRCHS_CNT ,'1' as EXPO_ORD
,NVL(M.PRCHS_QTY, 0) PRCHS_CNT ,M.EXPO_ORD
,J.PROD_GRD_CD ,L.APPLY_START_DT ,L.APPLY_END_DT ,I.PROD_CASE_CD ,E.TOP_MENU_ID ,PI.FILE_PATH ,PI.FILE_NM FROM TB_DP_SHPG_BRAND A ,TB_DP_SHPG_CATALOG B ,TB_DP_SHPG_CATALOG_DESC C ,TB_DP_MENU_SHPG_MAPG D ,TB_DP_MENU_CATEGORY E ,TB_DP_MENU_CATEGORY_DESC F ,TB_DP_PROD_CATALOG_MAPG G ,TB_DP_PROD_RSHP H ,TB_DP_SHPG_PROD I ,TB_DP_PROD J ,TB_DP_TENANT_PROD K ,TB_DP_TENANT_PROD_PRICE L ,TB_DP_PROD_IMG PI
,(SELECT PROD_ID ,PRCHS_CNT FROM TB_DP_TENANT_PROD_STATS WHERE TENANT_ID = #{tenantId} /* INPUT 테넌트ID */ ) M
,( SELECT TDLP.TENANT_ID , TDLP.LIST_ID , TDLP.MENU_ID , TDLP.PROD_ID , TDLP.EXPO_ORD , TDLP.PRCHS_QTY , TDLP.DWLD_QTY FROM TB_DP_LIST_PROD TDLP ,TB_DP_LIST TDL WHERE 1=1 AND TDLP.TENANT_ID = TDL.TENANT_ID AND TDLP.LIST_ID = TDL.LIST_ID AND TDLP.TENANT_ID = #{tenantId} /* 테넌트ID */ AND TDLP.LIST_ID = #{listId} /* RNK000000006 쇼핑 인기 */ AND TDLP.EXPO_YN = 'Y' /* Y : 노출 (노출여부) */ AND TDL.EXPO_YN = 'Y' /* Y : 노출 (노출여부) */ AND TO_CHAR(TDLP.STD_DT, 'YYYYMMDDHH24MISS') = #{stdDt} AND SYSDATE BETWEEN TDLP.EXPO_START_DT AND TDLP.EXPO_END_DT ) M
,(SELECT B1.PROD_ID , B1.SPRC_DC_RATE , B1.SPRC_PRICE FROM TB_DP_PROD_CATALOG_MAPG A1 ,TB_DP_SPRC_PROD B1 WHERE 1=1 AND A1.PROD_ID = B1.PROD_ID AND B1.EXPO_YN ='Y' AND B1.CPN_ID IS NOT NULL AND SYSDATE BETWEEN B1.SPRC_APPLY_START_DT AND B1.SPRC_APPLY_END_DT )N WHERE 1=1 AND A.BRAND_ID = B.BRAND_ID AND B.CATALOG_ID = C.CATALOG_ID AND C.CATALOG_ID = D.CATALOG_ID AND D.MENU_ID = E.MENU_ID AND D.MENU_ID = F.MENU_ID AND D.MENU_ID = #{menuId} /*INPUT 메뉴ID*/ AND B.CATALOG_ID = G.CATALOG_ID AND G.PROD_ID = H.PROD_ID AND H.PART_PROD_ID = I.PROD_ID AND I.PROD_ID = J.PROD_ID AND I.PROD_ID = K.PROD_ID AND I.PROD_ID = L.PROD_ID AND B.CATALOG_ID = PI.PROD_ID AND PI.IMG_CD=#{imageCd}
AND I.PROD_ID = M.PROD_ID(+)
AND K.TENANT_ID = M.TENANT_ID AND I.PROD_ID = M.PROD_ID
AND I.PROD_ID = N.PROD_ID(+) AND H.PROD_RSHP_CD ='DP010802' /*채널-에피소드 상품 관계*/ AND C.LANG_CD= #{langCd} /* ko : 한국어 */ AND F.LANG_CD= #{langCd} /* ko : 한국어 */ AND D.BASE_YN='Y' /* Y : 기본(기본여부) */ AND D.USE_YN ='Y' /* Y : 기본(기본여부) */ AND E.USE_YN='Y' /* Y : 기본(기본여부) */ AND G.BASE_YN='Y' /* Y : 기본(기본여부) */ AND G.USE_YN ='Y' /* Y : 기본(기본여부) */ AND NVL(I.B2B_PROD_YN, 'N') != 'Y' AND K.PROD_STATUS_CD = 'PD000403' /* PD000403 : 판매중 */ AND K.EXPO_YN = 'Y' /* Y : 노출(노출여부) */ AND SYSDATE BETWEEN L.APPLY_START_DT AND L.APPLY_END_DT AND K.TENANT_ID = #{tenantId} /* INPUT 테넌트ID */
AND J.PROD_CHRG_YN =#{prodCharge}
AND J.PROD_GRD_CD =#{prodGradeCd}
AND NOT EXISTS ( SELECT 1 FROM TB_DP_SPRT_DEVICE DV ,TB_CM_DEVICE CM WHERE 1=1 AND DV.DEVICE_MODEL_CD = CM.DEVICE_MODEL_CD AND CM.SCL_SHPG_SPRT_YN = 'Y' /*쇼핑 상품 단말 지원 여부*/ AND DV.DEVICE_MODEL_CD = #{deviceModelCd} AND PROD_ID = G.PROD_ID ) ) WHERE NO =1 )
ORDER BY REG_DT DESC, CATALOG_NM ASC
ORDER BY EXPO_ORD ASC, CATALOG_NM ASC
) WHERE RNUM BETWEEN #{offset} AND #{count}

/* ShoppingMapper.xml.getSpecialSalesList,기획전 상품 조회, 김형식/SK플래닛 , 2014-02-03 */ SELECT * FROM ( SELECT KK.* ,COUNT(planId) OVER() AS TOTALCOUNT ,ROWNUM AS RNUM FROM( SELECT PLAN_ID as planId ,PLAN_NM as PlanName ,SUB_TITL_NM as subTitleName ,LINK_URL as linkUrl ,PLAN_START_DT as planStartDt ,PLAN_END_DT as planEndDt ,PRZWNER_ANNO_DT przwnerAnnoDt ,PLAN_GIFT_NM as planGiftName ,FN_GETIMAGE(PLAN_ID, #{imageCd}) filePos FROM TB_DP_PLAN_INFO_MANG WHERE TO_CHAR(SYSDATE, 'YYYYMMDD') BETWEEN PLAN_START_DT AND PLAN_END_DT AND PLAN_STATUS_CD = 'DP006501' AND DP_YN = 'Y'
order by REG_DT DESC , PLAN_NM
order by PLAN_END_DT , PLAN_NM
)KK ) WHERE RNUM BETWEEN #{offset} AND #{count}

/* ShoppingMapper.xml.getBrandshopMainList,브랜드샵 - 메인 리스트 조회, 김형식/SK플래닛 , 2014-01-30 */ SELECT * FROM ( SELECT KK.* ,COUNT(1) OVER() AS TOTALCOUNT ,ROWNUM AS RNUM FROM( SELECT BRAND_ID AS brandId , BRAND_NM AS brandNm , MENU_ID AS menuId
, '쇼핑 HOT' AS menuName
, MENU_NM AS menuName
, TOP_MENU_ID as upMenuId ,(SELECT MENU_NM FROM TB_DP_MENU_CATEGORY_DESC WHERE MENU_ID = TOP_MENU_ID AND LANG_CD = #{langCd} /* ko : 한국어 */ AND ROWNUM = 1 ) AS upMenuName ,(SELECT FILE_PATH||FILE_NM FROM TB_DP_PROD_IMG WHERE PROD_ID = BRAND_ID AND IMG_CD =#{imageCd} ) as filePos FROM ( SELECT * FROM ( SELECT SB.BRAND_ID ,SB.BRAND_NM ,MC.MENU_ID ,MCD.MENU_NM ,MC.TOP_MENU_ID ,(SELECT COUNT(CT.CATALOG_ID) FROM TB_DP_SHPG_CATALOG CT WHERE CT.BRAND_ID = SB.BRAND_ID AND NVL(CT.CHNL_CNT, 0) > 0) AS CNT FROM TB_DP_SHPG_BRAND SB ,TB_DP_MENU_CATEGORY MC ,TB_DP_MENU_CATEGORY_DESC MCD
,TB_DP_RECOM_PROD_MANG PM
WHERE SB.MENU_ID = MC.MENU_ID AND SB.MENU_ID = MCD.MENU_ID
AND SB.BRAND_ID = PM.BRAND_ID
AND MC.USE_YN ='Y'
AND MC.MENU_ID = #{menuId}
AND MCD.LANG_CD =#{langCd} )WHERE CNT>0 ORDER BY MENU_ID , BRAND_NM ) )KK )WHERE RNUM BETWEEN #{offset} AND #{count}
