,(SELECT B1.PROD_ID , B1.SPRC_DC_RATE , B1.SPRC_PRICE FROM TB_DP_PROD_RSHP A1 ,TB_DP_SPRC_PROD B1 WHERE 1=1 AND A1.PART_PROD_ID = B1.PROD_ID AND B1.EXPO_YN ='Y' AND B1.CPN_ID IS NOT NULL AND SYSDATE BETWEEN B1.SPRC_APPLY_START_DT AND B1.SPRC_APPLY_END_DT )N WHERE 1=1 AND A.BRAND_ID = B.BRAND_ID AND B.CATALOG_ID = C.CATALOG_ID AND C.CATALOG_ID = D.CATALOG_ID AND D.MENU_ID = E.MENU_ID AND D.MENU_ID = F.MENU_ID AND A.BRAND_ID = #{req.brandId} /*INPUT 브랜드ID*/ AND B.CATALOG_ID = G.CATALOG_ID AND C.CATALOG_ID <> #{req.exceptProdId} /*INPUT 제외상품ID*/ AND G.PROD_ID = H.PROD_ID AND H.PART_PROD_ID = I.PROD_ID AND I.PROD_ID = J.PROD_ID AND I.PROD_ID = K.PROD_ID AND I.PROD_ID = L.PROD_ID AND K.TENANT_ID = L.TENANT_ID
AND K.TENANT_ID = M.TENANT_ID AND B.CATALOG_ID = M.PROD_ID
AND I.PROD_ID = N.PROD_ID(+) AND H.PROD_RSHP_CD =#{prodRshpCd} /*채널-에피소드 상품 관계*/ AND NVL(B.CHNL_CNT, 0) > 0 AND C.LANG_CD= #{tenantHeader.langCd} /* ko : 한국어 */ AND F.LANG_CD= #{tenantHeader.langCd} /* ko : 한국어 */ AND D.BASE_YN='Y' /* Y : 기본(기본여부) */ AND D.USE_YN ='Y' /* Y : 기본(기본여부) */ AND E.USE_YN='Y' /* Y : 기본(기본여부) */ AND G.BASE_YN='Y' /* Y : 기본(기본여부) */ AND G.USE_YN ='Y' /* Y : 기본(기본여부) */ AND NVL(I.B2B_PROD_YN, 'N') <> 'Y' AND K.PROD_STATUS_CD = #{prodStatusCd} /* PD000403 : 판매중 */ AND K.EXPO_YN = 'Y' /* Y : 노출(노출여부) */ AND SYSDATE BETWEEN L.APPLY_START_DT AND L.APPLY_END_DT AND K.TENANT_ID = #{tenantHeader.tenantId} /* INPUT 테넌트ID */
AND J.PROD_CHRG_YN =#{req.prodCharge}
AND J.PROD_GRD_CD IN
#{req.prodGradeCd}
AND EXISTS ( SELECT 1 FROM TB_DP_SPRT_DEVICE DV ,TB_CM_DEVICE CM WHERE 1=1 AND DV.DEVICE_MODEL_CD = CM.DEVICE_MODEL_CD AND CM.SCL_SHPG_SPRT_YN = 'Y' /*쇼핑 상품 단말 지원 여부*/ AND (DV.DEVICE_MODEL_CD = #{req.deviceModelCd} OR DV.DEVICE_MODEL_CD = #{req.virtualDeviceModelNo}) AND PROD_ID = I.PROD_ID ) ) WHERE NO =1
ORDER BY REG_DT DESC, CATALOG_NM ASC
ORDER BY EXPO_ORD ASC, CATALOG_NM ASC
) ) WHERE RNUM BETWEEN #{req.offset} AND #{req.count}

/* ShoppingMapper.xml.getShoppingChannelDetail,쇼핑상세(채널) 조회, 김형식/SK플래닛 , 2014-02-04 */ SELECT * FROM ( SELECT B.CATALOG_ID
