/* ShoppingMapper.xml.getFeatureProductList,쇼핑/인기 추천 상품 리스트 조회, 김형식/SK플래닛 , 2014-01-07 */ SELECT * FROM ( SELECT rownum AS RNUM ,COUNT(CATALOG_ID) OVER() AS TOTALCOUNT ,CATALOG_ID AS catalogId ,BRAND_ID AS brandId ,MENU_ID AS menuId ,TENANT_ID AS tenentId ,PROD_ID as prodId ,PART_PROD_ID as partProdId ,EXPO_ORD as expoOrd ,#{channelContentTypeCd} as contentsTypeCd FROM ( SELECT * FROM ( SELECT B.CATALOG_ID ,C.CATALOG_NM ,A.BRAND_ID ,D.MENU_ID ,ROW_NUMBER() OVER(PARTITION BY B.CATALOG_ID ORDER BY NVL(N.SPRC_PRICE, L.PROD_AMT) ASC) as NO ,K.TENANT_ID ,H.PROD_ID ,H.PART_PROD_ID ,M.EXPO_ORD_ALWAYS_YN ,M.EXPO_ORD ,M.EXPO_ORD_SUB ,I.REG_DT FROM TB_DP_SHPG_BRAND A ,TB_DP_SHPG_CATALOG B ,TB_DP_SHPG_CATALOG_DESC C ,TB_DP_MENU_SHPG_MAPG D ,TB_DP_MENU_CATEGORY E ,TB_DP_MENU_CATEGORY_DESC F ,TB_DP_PROD_CATALOG_MAPG G ,TB_DP_PROD_RSHP H ,TB_DP_SHPG_PROD I ,TB_DP_PROD J ,TB_DP_TENANT_PROD K ,TB_DP_TENANT_PROD_PRICE L ,( SELECT TDLP.TENANT_ID , TDLP.LIST_ID , TDLP.MENU_ID , TDLP.PROD_ID , TDLP.EXPO_ORD_ALWAYS_YN , TDLP.EXPO_ORD , TDLP.EXPO_ORD_SUB , TDLP.PRCHS_QTY , TDLP.DWLD_QTY FROM TB_DP_LIST_PROD TDLP ,TB_DP_LIST TDL WHERE 1=1 AND TDLP.TENANT_ID = TDL.TENANT_ID AND TDLP.LIST_ID = TDL.LIST_ID AND TDLP.TENANT_ID = #{tenantHeader.tenantId} /* 테넌트ID */ AND TDLP.LIST_ID = #{req.listId} /* ADM000000014 : 쇼핑 추천, RNK000000006 쇼핑 인기 */ AND TDLP.EXPO_YN = 'Y' /* Y : 노출 (노출여부) */ AND TDL.EXPO_YN = 'Y' /* Y : 노출 (노출여부) */ AND TO_CHAR(TDLP.STD_DT, 'YYYYMMDDHH24MISS') = #{stdDt} AND SYSDATE BETWEEN TDLP.EXPO_START_DT AND TDLP.EXPO_END_DT ) M ,(SELECT /*+ ORDERED */ B1.PROD_ID , B1.SPRC_DC_RATE , B1.SPRC_PRICE FROM TB_DP_PROD_RSHP A1 ,TB_DP_SPRC_PROD B1 WHERE 1=1 AND A1.PART_PROD_ID = B1.PROD_ID AND B1.EXPO_YN ='Y' AND B1.CPN_ID IS NOT NULL AND SYSDATE BETWEEN B1.SPRC_APPLY_START_DT AND B1.SPRC_APPLY_END_DT )N WHERE 1=1 AND A.BRAND_ID = B.BRAND_ID AND B.CATALOG_ID = C.CATALOG_ID AND C.CATALOG_ID = D.CATALOG_ID AND D.MENU_ID = E.MENU_ID AND D.MENU_ID = F.MENU_ID AND B.CATALOG_ID = G.CATALOG_ID AND G.PROD_ID = H.PROD_ID AND H.PROD_ID <> H.PART_PROD_ID AND H.PART_PROD_ID = I.PROD_ID AND I.PROD_ID = J.PROD_ID AND I.PROD_ID = K.PROD_ID AND I.PROD_ID = L.PROD_ID AND K.TENANT_ID = M.TENANT_ID AND K.TENANT_ID = L.TENANT_ID AND B.CATALOG_ID = M.PROD_ID AND I.PROD_ID = N.PROD_ID(+) AND H.PROD_RSHP_CD =#{prodRshpCd} /*채널-에피소드 상품 관계*/ AND NVL(B.CHNL_CNT, 0) > 0 AND C.LANG_CD= #{tenantHeader.langCd} /* ko : 한국어 */ AND F.LANG_CD= #{tenantHeader.langCd} /* ko : 한국어 */ AND D.BASE_YN='Y' /* Y : 기본(기본여부) */ AND D.USE_YN ='Y' /* Y : 기본(기본여부) */ AND E.USE_YN='Y' /* Y : 기본(기본여부) */ AND G.BASE_YN='Y' /* Y : 기본(기본여부) */ AND G.USE_YN ='Y' /* Y : 기본(기본여부) */ AND NVL(I.B2B_PROD_YN, 'N') <> 'Y' AND K.PROD_STATUS_CD = #{prodStatusCd} /* PD000403 : 판매중 */ AND K.EXPO_YN = 'Y' /* Y : 노출(노출여부) */ AND SYSDATE BETWEEN L.APPLY_START_DT AND L.APPLY_END_DT AND K.TENANT_ID = #{tenantHeader.tenantId} /* INPUT 테넌트ID */
AND J.PROD_CHRG_YN =#{req.prodCharge}
AND J.PROD_GRD_CD IN
#{req.prodGradeCd}
AND EXISTS ( SELECT 1 FROM TB_DP_SPRT_DEVICE DV WHERE 1=1 AND (DV.DEVICE_MODEL_CD = #{deviceHeader.model} OR DV.DEVICE_MODEL_CD = #{req.virtualDeviceModelNo}) AND PROD_ID = I.PROD_ID ) ) WHERE NO =1 ORDER BY EXPO_ORD_ALWAYS_YN DESC , EXPO_ORD , EXPO_ORD_SUB , CATALOG_NM ASC ) ) WHERE RNUM BETWEEN #{req.offset} AND #{req.count}

/* ShoppingMapper.xml.getNewProductList,최신 상품 리스트 조회, 김형식/SK플래닛 , 2014-01-07 */ SELECT * FROM ( SELECT rownum AS RNUM ,COUNT(CATALOG_ID) OVER() AS TOTALCOUNT ,CATALOG_ID AS catalogId ,BRAND_ID AS brandId ,MENU_ID AS menuId ,TENANT_ID AS tenentId ,PROD_ID as prodId ,PART_PROD_ID as partProdId ,#{channelContentTypeCd} as contentsTypeCd FROM ( SELECT * FROM ( SELECT B.CATALOG_ID ,C.CATALOG_NM ,A.BRAND_ID ,D.MENU_ID ,ROW_NUMBER() OVER(PARTITION BY B.CATALOG_ID ORDER BY NVL(N.SPRC_PRICE, L.PROD_AMT) ASC) as NO ,K.TENANT_ID ,H.PROD_ID ,H.PART_PROD_ID ,I.REG_DT FROM TB_DP_SHPG_BRAND A ,TB_DP_SHPG_CATALOG B ,TB_DP_SHPG_CATALOG_DESC C ,TB_DP_MENU_SHPG_MAPG D ,TB_DP_MENU_CATEGORY E ,TB_DP_MENU_CATEGORY_DESC F ,TB_DP_PROD_CATALOG_MAPG G ,TB_DP_PROD_RSHP H ,TB_DP_SHPG_PROD I ,TB_DP_PROD J ,TB_DP_TENANT_PROD K ,TB_DP_TENANT_PROD_PRICE L ,(SELECT /*+ ORDERED */ B1.PROD_ID , B1.SPRC_DC_RATE , B1.SPRC_PRICE FROM TB_DP_PROD_RSHP A1 ,TB_DP_SPRC_PROD B1 WHERE 1=1 AND A1.PART_PROD_ID = B1.PROD_ID AND B1.EXPO_YN ='Y' AND B1.CPN_ID IS NOT NULL AND SYSDATE BETWEEN B1.SPRC_APPLY_START_DT AND B1.SPRC_APPLY_END_DT )N WHERE 1=1 AND A.BRAND_ID = B.BRAND_ID AND B.CATALOG_ID = C.CATALOG_ID AND C.CATALOG_ID = D.CATALOG_ID AND D.MENU_ID = E.MENU_ID AND D.MENU_ID = F.MENU_ID AND B.CATALOG_ID = G.CATALOG_ID AND G.PROD_ID = H.PROD_ID AND H.PROD_ID <> H.PART_PROD_ID AND H.PART_PROD_ID = I.PROD_ID AND I.PROD_ID = J.PROD_ID AND I.PROD_ID = K.PROD_ID AND I.PROD_ID = L.PROD_ID AND I.PROD_ID = N.PROD_ID(+) AND K.TENANT_ID = L.TENANT_ID
AND I.PROD_ID = #{req.partProdId}
AND H.PROD_RSHP_CD =#{prodRshpCd} /*채널-에피소드 상품 관계*/ AND NVL(B.CHNL_CNT, 0) > 0 AND C.LANG_CD= #{tenantHeader.langCd} /* ko : 한국어 */ AND F.LANG_CD= #{tenantHeader.langCd} /* ko : 한국어 */ AND D.BASE_YN='Y' /* Y : 기본(기본여부) */ AND D.USE_YN ='Y' /* Y : 기본(기본여부) */ AND E.USE_YN='Y' /* Y : 기본(기본여부) */ AND G.BASE_YN='Y' /* Y : 기본(기본여부) */ AND G.USE_YN ='Y' /* Y : 기본(기본여부) */ AND NVL(I.B2B_PROD_YN, 'N') <> 'Y' AND K.PROD_STATUS_CD = #{prodStatusCd} /* PD000403 : 판매중 */ AND K.EXPO_YN = 'Y' /* Y : 노출(노출여부) */ AND SYSDATE BETWEEN L.APPLY_START_DT AND L.APPLY_END_DT AND K.TENANT_ID = #{tenantHeader.tenantId} /* INPUT 테넌트ID */
AND J.PROD_CHRG_YN =#{req.prodCharge}
AND J.PROD_GRD_CD IN
#{req.prodGradeCd}
AND EXISTS ( SELECT 1 FROM TB_DP_SPRT_DEVICE DV WHERE 1=1 AND (DV.DEVICE_MODEL_CD = #{deviceHeader.model} OR DV.DEVICE_MODEL_CD = #{req.virtualDeviceModelNo}) AND PROD_ID = I.PROD_ID ) ) WHERE NO =1 ORDER BY REG_DT DESC, CATALOG_NM ASC ) ) WHERE RNUM BETWEEN #{req.offset} AND #{req.count}

/* ShoppingMapper.xml.getSubProductList,쇼핑 세부카테고리 상품 조회, 김형식/SK플래닛 , 2014-01-07 */ SELECT * FROM ( SELECT rownum AS RNUM ,COUNT(CATALOG_ID) OVER() AS TOTALCOUNT ,CATALOG_ID AS catalogId ,BRAND_ID AS brandId ,MENU_ID AS menuId ,TENANT_ID AS tenentId ,PROD_ID as prodId ,PART_PROD_ID as partProdId ,#{channelContentTypeCd} as contentsTypeCd FROM ( SELECT * FROM ( SELECT B.CATALOG_ID ,C.CATALOG_NM ,A.BRAND_ID ,D.MENU_ID ,ROW_NUMBER() OVER(PARTITION BY B.CATALOG_ID ORDER BY NVL(N.SPRC_PRICE, L.PROD_AMT) ASC) as NO ,K.TENANT_ID ,H.PROD_ID ,H.PART_PROD_ID ,I.REG_DT
,'1' EXPO_ORD
,M.EXPO_ORD_ALWAYS_YN ,M.EXPO_ORD ,M.EXPO_ORD_SUB
FROM TB_DP_SHPG_BRAND A ,TB_DP_SHPG_CATALOG B ,TB_DP_SHPG_CATALOG_DESC C ,TB_DP_MENU_SHPG_MAPG D ,TB_DP_MENU_CATEGORY E ,TB_DP_MENU_CATEGORY_DESC F ,TB_DP_PROD_CATALOG_MAPG G ,TB_DP_PROD_RSHP H ,TB_DP_SHPG_PROD I ,TB_DP_PROD J ,TB_DP_TENANT_PROD K ,TB_DP_TENANT_PROD_PRICE L
,( SELECT TDLP.TENANT_ID , TDLP.LIST_ID , TDLP.MENU_ID , TDLP.PROD_ID , TDLP.EXPO_ORD_ALWAYS_YN , TDLP.EXPO_ORD , TDLP.EXPO_ORD_SUB , TDLP.PRCHS_QTY , TDLP.DWLD_QTY FROM TB_DP_LIST_PROD TDLP ,TB_DP_LIST TDL WHERE 1=1 AND TDLP.TENANT_ID = TDL.TENANT_ID AND TDLP.LIST_ID = TDL.LIST_ID AND TDLP.TENANT_ID = #{tenantHeader.tenantId} /* 테넌트ID */ AND TDLP.LIST_ID = #{req.listId} /* RNK000000006 쇼핑 인기 */ AND TDLP.EXPO_YN = 'Y' /* Y : 노출 (노출여부) */ AND TDL.EXPO_YN = 'Y' /* Y : 노출 (노출여부) */ AND TO_CHAR(TDLP.STD_DT, 'YYYYMMDDHH24MISS') = #{stdDt} AND SYSDATE BETWEEN TDLP.EXPO_START_DT AND TDLP.EXPO_END_DT ) M
,(SELECT /*+ ORDERED */ B1.PROD_ID , B1.SPRC_DC_RATE , B1.SPRC_PRICE FROM TB_DP_PROD_RSHP A1 ,TB_DP_SPRC_PROD B1 WHERE 1=1 AND A1.PART_PROD_ID = B1.PROD_ID AND B1.EXPO_YN ='Y' AND B1.CPN_ID IS NOT NULL AND SYSDATE BETWEEN B1.SPRC_APPLY_START_DT AND B1.SPRC_APPLY_END_DT )N WHERE 1=1 AND A.BRAND_ID = B.BRAND_ID AND B.CATALOG_ID = C.CATALOG_ID AND C.CATALOG_ID = D.CATALOG_ID AND D.MENU_ID = E.MENU_ID AND D.MENU_ID = F.MENU_ID AND D.MENU_ID = #{req.menuId} /*INPUT 메뉴ID*/ AND B.CATALOG_ID = G.CATALOG_ID AND G.PROD_ID = H.PROD_ID AND H.PROD_ID <> H.PART_PROD_ID AND H.PART_PROD_ID = I.PROD_ID AND I.PROD_ID = J.PROD_ID AND I.PROD_ID = K.PROD_ID AND I.PROD_ID = L.PROD_ID AND K.TENANT_ID = L.TENANT_ID
AND K.TENANT_ID = M.TENANT_ID AND B.CATALOG_ID = M.PROD_ID
AND I.PROD_ID = N.PROD_ID(+) AND H.PROD_RSHP_CD =#{prodRshpCd} /*채널-에피소드 상품 관계*/ AND NVL(B.CHNL_CNT, 0) > 0 AND C.LANG_CD= #{tenantHeader.langCd} /* ko : 한국어 */ AND F.LANG_CD= #{tenantHeader.langCd} /* ko : 한국어 */ AND D.BASE_YN='Y' /* Y : 기본(기본여부) */ AND D.USE_YN ='Y' /* Y : 기본(기본여부) */ AND E.USE_YN='Y' /* Y : 기본(기본여부) */ AND G.BASE_YN='Y' /* Y : 기본(기본여부) */ AND G.USE_YN ='Y' /* Y : 기본(기본여부) */ AND NVL(I.B2B_PROD_YN, 'N') <> 'Y' AND K.PROD_STATUS_CD = #{prodStatusCd} /* PD000403 : 판매중 */ AND K.EXPO_YN = 'Y' /* Y : 노출(노출여부) */ AND SYSDATE BETWEEN L.APPLY_START_DT AND L.APPLY_END_DT AND K.TENANT_ID = #{tenantHeader.tenantId} /* INPUT 테넌트ID */
AND J.PROD_CHRG_YN =#{req.prodCharge}
AND J.PROD_GRD_CD IN
#{req.prodGradeCd}
AND EXISTS ( SELECT 1 FROM TB_DP_SPRT_DEVICE DV WHERE 1=1 AND (DV.DEVICE_MODEL_CD = #{deviceHeader.model} OR DV.DEVICE_MODEL_CD = #{req.virtualDeviceModelNo}) AND PROD_ID = I.PROD_ID ) ) WHERE NO =1
ORDER BY REG_DT DESC , CATALOG_NM ASC
ORDER BY EXPO_ORD_ALWAYS_YN DESC , EXPO_ORD , EXPO_ORD_SUB , CATALOG_NM ASC
) ) WHERE RNUM BETWEEN #{req.offset} AND #{req.count}

/* ShoppingMapper.xml.getSpecialPriceProductList,특가 상품 리스트 조회, 김형식/SK플래닛 , 2014-02-04 */ SELECT * FROM ( SELECT ROW_NUMBER()OVER(ORDER BY REG_DT DESC, CATALOG_NM ) AS RNUM ,COUNT(CATALOG_ID) OVER() AS TOTALCOUNT ,CATALOG_ID ,PROD_NET_AMT ,PROD_AMT ,DC_RATE ,DC_AMT ,DLV_PROD_YN ,BRAND_ID ,BRAND_NM ,MENU_ID ,MENU_NM ,TOP_MENU_ID ,META_CLSF_CD ,(SELECT MENU_NM FROM TB_DP_MENU_CATEGORY_DESC WHERE MENU_ID = TOP_MENU_ID AND LANG_CD = #{langCd} /* ko : 한국어 */ AND ROWNUM = 1 ) AS TOP_MENU_NM ,NO AS no ,NEW_YN ,REG_DT ,TENANT_ID ,PROD_ID ,PART_PROD_ID ,PROD_NM AS catalogNm ,PRCHS_CNT ,PROD_GRD_CD as prodGrdCd ,TO_CHAR(SPRC_APPLY_START_DT,'YYYYMMDDHH24MISS') as applyStartDt ,TO_CHAR(SPRC_APPLY_END_DT,'YYYYMMDDHH24MISS') as applyEndDt ,PROD_CASE_CD ,CONCAT(FILE_PATH,FILE_NM) AS FILE_PATH ,'Y' AS specialSale ,(CASE WHEN ( ( MTH_PRCHS_CNT >= MM_PROD_MAX_SALE_QTY ) OR ( DLY_PRCHS_CNT >= DLY_PROD_MAX_SALE_QTY ) ) THEN 'Y' ELSE 'N' END ) AS soldOut ,CONTENTS_TYPE_CD FROM ( SELECT * FROM ( SELECT B.CATALOG_ID ,C.CATALOG_NM ,NVL(L.PROD_NET_AMT, 0) AS PROD_NET_AMT ,NVL(N.SPRC_PRICE, NVL(L.PROD_AMT, 0)) AS PROD_AMT ,NVL(N.SPRC_DC_RATE, NVL(L.DC_RATE,0)) AS DC_RATE ,NVL(N.SPRC_DC_PRICE, NVL(L.DC_AMT, 0)) AS DC_AMT ,A.BRAND_ID ,A.BRAND_NM ,D.MENU_ID ,F.MENU_NM ,ROW_NUMBER() OVER(PARTITION BY B.CATALOG_ID ORDER BY NVL(N.SPRC_PRICE, L.PROD_AMT) ASC) as NO ,CASE WHEN (SYSDATE - 7) <= I.REG_DT THEN 'Y' ELSE 'N' END AS NEW_YN ,I.REG_DT ,K.TENANT_ID ,H.PROD_ID ,H.PART_PROD_ID ,I.DLV_PROD_YN ,CASE WHEN NVL(M.PRCHS_CNT, 0) < 0 THEN 0 ELSE NVL(M.PRCHS_CNT, 0) END AS PRCHS_CNT ,J.PROD_GRD_CD ,L.APPLY_START_DT ,L.APPLY_END_DT ,(CASE WHEN I.PROD_CASE_CD='DP006301' THEN 'giftcard' WHEN I.PROD_CASE_CD='DP006302' THEN 'exchange' WHEN I.PROD_CASE_CD='DP006303' THEN 'delivery' END) AS PROD_CASE_CD ,E.TOP_MENU_ID ,J.META_CLSF_CD ,PI.FILE_PATH ,PI.FILE_NM , NVL( ( SELECT SUM( TSPP.PRCHS_CNT ) FROM TB_DP_SPRC_PROD_PRCHS_MANG TSPP WHERE TO_CHAR(TSPP.PRCHS_DT,'YYYYMMDD') BETWEEN CONCAT( TO_CHAR( SYSDATE, 'YYYYMM' ), '01' ) AND TO_CHAR( LAST_DAY( SYSDATE ),'YYYYMMDD') AND TSPP.PROD_ID = H.PART_PROD_ID ) , 0 ) AS MTH_PRCHS_CNT , NVL( ( SELECT TSPP.PRCHS_CNT FROM TB_DP_SPRC_PROD_PRCHS_MANG TSPP WHERE TO_CHAR(TSPP.PRCHS_DT,'YYYYMMDD') = TO_CHAR( SYSDATE, 'YYYYMMDD' ) AND TSPP.PROD_ID = H.PART_PROD_ID ) , 0 ) AS DLY_PRCHS_CNT ,N.MM_PROD_MAX_SALE_QTY ,N.DLY_PROD_MAX_SALE_QTY ,N.SPRC_APPLY_START_DT ,N.SPRC_APPLY_END_DT ,N.EXPO_ORD ,O.PROD_NM ,J.CONTENTS_TYPE_CD FROM TB_DP_SHPG_BRAND A ,TB_DP_SHPG_CATALOG B ,TB_DP_SHPG_CATALOG_DESC C ,TB_DP_MENU_SHPG_MAPG D ,TB_DP_MENU_CATEGORY E ,TB_DP_MENU_CATEGORY_DESC F ,TB_DP_PROD_CATALOG_MAPG G ,TB_DP_PROD_RSHP H ,TB_DP_SHPG_PROD I ,TB_DP_PROD J ,TB_DP_TENANT_PROD K ,TB_DP_TENANT_PROD_PRICE L ,TB_DP_PROD_IMG PI ,(SELECT PROD_ID ,PRCHS_CNT FROM TB_DP_TENANT_PROD_STATS WHERE TENANT_ID = #{tenantId} /* INPUT 테넌트ID */ ) M ,TB_DP_SPRC_PROD N ,TB_DP_PROD_DESC O WHERE 1=1 AND A.BRAND_ID = B.BRAND_ID AND B.CATALOG_ID = C.CATALOG_ID AND C.CATALOG_ID = D.CATALOG_ID AND D.MENU_ID = E.MENU_ID AND D.MENU_ID = F.MENU_ID AND B.CATALOG_ID = G.CATALOG_ID AND G.PROD_ID = H.PROD_ID AND H.PROD_ID <> H.PART_PROD_ID AND H.PART_PROD_ID = I.PROD_ID AND I.PROD_ID = J.PROD_ID AND I.PROD_ID = K.PROD_ID AND I.PROD_ID = L.PROD_ID AND I.PROD_ID = M.PROD_ID(+) AND I.PROD_ID = N.PROD_ID AND I.PROD_ID = O.PROD_ID AND K.TENANT_ID = L.TENANT_ID AND N.EXPO_YN ='Y' AND N.CPN_ID IS NOT NULL
AND B.CATALOG_ID = PI.PROD_ID AND H.PROD_RSHP_CD ='DP010802' /*채널-에피소드 상품 관계*/ AND NVL(B.CHNL_CNT, 0) > 0 AND C.LANG_CD= #{langCd} /* ko : 한국어 */ AND O.LANG_CD= #{langCd} /* ko : 한국어 */ AND F.LANG_CD= #{langCd} /* ko : 한국어 */ AND D.BASE_YN='Y' /* Y : 기본(기본여부) */ AND D.USE_YN ='Y' /* Y : 기본(기본여부) */ AND E.USE_YN='Y' /* Y : 기본(기본여부) */ AND G.BASE_YN='Y' /* Y : 기본(기본여부) */ AND G.USE_YN ='Y' /* Y : 기본(기본여부) */ AND NVL(I.B2B_PROD_YN, 'N') <> 'Y' AND K.PROD_STATUS_CD = 'PD000403' /* PD000403 : 판매중 */ AND K.EXPO_YN = 'Y' /* Y : 노출(노출여부) */ AND SYSDATE BETWEEN N.SPRC_APPLY_START_DT AND N.SPRC_APPLY_END_DT AND SYSDATE BETWEEN L.APPLY_START_DT AND L.APPLY_END_DT AND K.TENANT_ID = #{tenantId} /* INPUT 테넌트ID */
AND J.PROD_CHRG_YN =#{prodCharge}
AND J.PROD_GRD_CD IN
#{prodGradeCd}
AND EXISTS ( SELECT 1 FROM TB_DP_SPRT_DEVICE DV WHERE 1=1 AND (DV.DEVICE_MODEL_CD = #{deviceModelCd} OR DV.DEVICE_MODEL_CD = #{virtualDeviceModelNo}) AND PROD_ID = I.PROD_ID ) ) WHERE NO =1 ORDER BY EXPO_ORD , PROD_NM ) ) WHERE RNUM BETWEEN #{offset} AND #{count}

/* ShoppingMapper.xml.getSpecialSalesList,기획전 상품 조회, 김형식/SK플래닛 , 2014-02-03 */ SELECT * FROM ( SELECT KK.* ,COUNT(PLAN_ID) OVER() AS TOTALCOUNT ,ROWNUM AS RNUM FROM( SELECT PLAN_ID ,PLAN_NM as prodNm ,SUB_TITL_NM ,LINK_URL ,TO_CHAR(PLAN_START_DT,'YYYYMMDDHH24MISS') AS PLAN_START_DT ,TO_CHAR(PLAN_END_DT,'YYYYMMDDHH24MISS') AS PLAN_END_DT ,PRZWNER_ANNO_DT ,PLAN_GIFT_NM ,(SELECT CONCAT(FILE_PATH,FILE_NM) FROM TB_DP_PROD_IMG WHERE PROD_ID = PLAN_ID AND IMG_CD =#{bannerImgCd} )bannerFilePath ,(SELECT CONCAT(FILE_PATH,FILE_NM) FROM TB_DP_PROD_IMG WHERE PROD_ID = PLAN_ID AND IMG_CD =#{promotionImgCd} )FILE_PATH FROM TB_DP_PLAN_INFO_MANG WHERE SYSDATE BETWEEN PLAN_START_DT AND PLAN_END_DT AND PLAN_STATUS_CD = 'DP006501' AND DP_YN = 'Y'
AND PLAN_ID = #{planId}
order by REG_DT DESC , PLAN_NM
order by PLAN_END_DT , PLAN_NM
