,H.PROD_ID ,H.PART_PROD_ID ,C.CATALOG_DESC ,A.BRAND_ID ,A.BRAND_NM ,D.MENU_ID ,F.MENU_NM ,J.TOP_MENU_ID ,(SELECT MENU_NM FROM TB_DP_MENU_CATEGORY_DESC WHERE MENU_ID = J.TOP_MENU_ID AND LANG_CD = #{lang} AND ROWNUM = 1 ) AS TOP_MENU_NM ,F.MENU_DESC ,ROW_NUMBER() OVER(PARTITION BY B.CATALOG_ID ORDER BY L.PROD_AMT ASC) as NO ,CASE WHEN NVL(M.PRCHS_CNT, 0) < 0 THEN 0 ELSE NVL(M.PRCHS_CNT, 0) END AS PRCHS_CNT ,(CASE WHEN I.PROD_CASE_CD='DP006301' THEN 'giftcard' WHEN I.PROD_CASE_CD='DP006302' THEN 'exchange' WHEN I.PROD_CASE_CD='DP006303' THEN 'delivery' END) AS PROD_CASE_CD ,CONCAT(PI.FILE_PATH,PI.FILE_NM) as FILE_PATH ,J.CONTENTS_TYPE_CD ,(SELECT SRC_CONTENT_ID FROM TB_DP_SHPG_PROD WHERE prod_id=H.PROD_ID ) AS SRC_CONTENT_ID ,K.PROD_STATUS_CD ,C.CATALOG_DTL_DESC AS PROD_DTL_DESC FROM TB_DP_SHPG_BRAND A ,TB_DP_SHPG_CATALOG B ,TB_DP_SHPG_CATALOG_DESC C ,TB_DP_MENU_SHPG_MAPG D ,TB_DP_MENU_CATEGORY E ,TB_DP_MENU_CATEGORY_DESC F ,TB_DP_PROD_CATALOG_MAPG G ,TB_DP_PROD_RSHP H ,TB_DP_SHPG_PROD I ,TB_DP_PROD J ,TB_DP_TENANT_PROD K ,TB_DP_TENANT_PROD_PRICE L ,TB_DP_PROD_IMG PI ,TB_DP_PROD_DESC O ,(SELECT PROD_ID ,PRCHS_CNT FROM TB_DP_TENANT_PROD_STATS WHERE TENANT_ID = #{tenantHeader.tenantId} /* INPUT 테넌트ID */ ) M WHERE 1=1 AND A.BRAND_ID = B.BRAND_ID AND B.CATALOG_ID = C.CATALOG_ID AND C.CATALOG_ID = D.CATALOG_ID AND D.MENU_ID = E.MENU_ID AND D.MENU_ID = F.MENU_ID AND B.CATALOG_ID = G.CATALOG_ID AND G.PROD_ID = H.PROD_ID AND H.PROD_ID <> H.PART_PROD_ID AND H.PART_PROD_ID = I.PROD_ID AND I.PROD_ID = J.PROD_ID AND I.PROD_ID = K.PROD_ID AND I.PROD_ID = L.PROD_ID AND I.PROD_ID = O.PROD_ID AND K.TENANT_ID = L.TENANT_ID AND C.CATALOG_ID = PI.PROD_ID AND PI.IMG_CD = #{imageCd} AND I.PROD_ID = M.PROD_ID(+) AND H.PROD_RSHP_CD =#{prodRshpCd} /*채널-에피소드 상품 관계*/ AND NVL(B.CHNL_CNT, 0) > 0 AND C.LANG_CD= #{lang} /* ko : 한국어 */ AND F.LANG_CD= #{lang} /* ko : 한국어 */ AND D.BASE_YN='Y' /* Y : 기본(기본여부) */ AND D.USE_YN ='Y' /* Y : 기본(기본여부) */ AND E.USE_YN='Y' /* Y : 기본(기본여부) */ AND G.BASE_YN='Y' /* Y : 기본(기본여부) */ AND G.USE_YN ='Y' /* Y : 기본(기본여부) */ AND NVL(I.B2B_PROD_YN, 'N') <> 'Y' AND K.EXPO_YN = 'Y' /* Y : 노출(노출여부) */ AND SYSDATE BETWEEN L.APPLY_START_DT AND L.APPLY_END_DT AND K.TENANT_ID = #{tenantHeader.tenantId} /* INPUT 테넌트ID */ AND B.CATALOG_ID = #{req.productId}
AND I.PROD_ID = #{req.specialProdId}
AND EXISTS ( SELECT 1 FROM TB_DP_SPRT_DEVICE DV WHERE 1=1 AND (DV.DEVICE_MODEL_CD = #{deviceHeader.model} OR DV.DEVICE_MODEL_CD = #{req.virtualDeviceModelNo}) AND PROD_ID = I.PROD_ID ) ) WHERE NO =1

/* ShoppingMapper.xml.getShoppingEpisodeDetail,쇼핑상세(에피소드) 조회, 김형식/SK플래닛 , 2014-02-04 */ SELECT KK.* ,(CASE WHEN KK.SPECIAL_SALE_YN ='Y' THEN ( CASE WHEN ( ( KK.MTH_PRCHS_CNT >= KK.MTH_MAX_CNT ) OR ( KK.DLY_PRCHS_CNT >= KK.DLY_MAX_CNT ) ) THEN 'Y' ELSE 'N' END ) ELSE 'N' END ) AS soldOut ,KK.SPECIAL_SALE_YN AS specialSale ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') AS SYS_DATE FROM ( SELECT D.MENU_ID ,J.TOP_MENU_ID ,H.PROD_ID ,H.PART_PROD_ID ,J.SELLER_MBR_NO ,NVL(L.PROD_NET_AMT, 0) AS PROD_NET_AMT ,NVL(N.SPRC_PRICE, NVL(L.PROD_AMT, 0)) AS PROD_AMT ,NVL(N.SPRC_DC_RATE, NVL(L.DC_RATE,0)) AS DC_RATE ,NVL(N.SPRC_DC_PRICE, NVL(L.DC_AMT, 0)) AS DC_AMT ,J.PROD_GRD_CD ,J.USE_PERIOD_UNIT_CD ,J.USE_PERIOD ,(CASE WHEN I.PROD_CASE_CD='DP006301' THEN 'giftcard' WHEN I.PROD_CASE_CD='DP006302' THEN 'exchange' WHEN I.PROD_CASE_CD='DP006303' THEN 'delivery' END) AS PROD_CASE_CD ,TO_CHAR(NVL( N.SPRC_APPLY_START_DT, L.APPLY_START_DT),'YYYYMMDDHH24MISS') AS APPLY_START_DT ,TO_CHAR(NVL( N.SPRC_APPLY_END_DT , L.APPLY_END_DT ),'YYYYMMDDHH24MISS') AS APPLY_END_DT ,I.SALE_CNT ,NVL(N.MM_PROD_MAX_SALE_QTY, I.MM_MAX_SALE_QTY ) AS MTH_MAX_CNT /* 월_최대_판매_수량 */ ,NVL(N.DLY_PROD_MAX_SALE_QTY, I.DAY_MAX_SALE_QTY ) AS DLY_MAX_CNT /* 일_최대_판매_수량 */ ,NVL(N.PERMAN_MM_MAX_PRCHS_QTY, I.MM_MBR_MAX_PRCHS_QTY ) AS MTH_USR_MAX_CNT /* 월_회원_최대_구매_수량 */ ,NVL(N.PERMAN_DAY_MAX_PRCHS_QTY, I.DAY_MBR_MAX_PRCHS_QTY ) AS DLY_USR_MAX_CNT /* 일_회원_최대_구매_수량 */ ,NVL(N.ONCE_MAX_PRCHS_POSB_CNT, I.FIRST_MAX_PRCHS_QTY ) AS EACH_MAX_CNT /* 1차_최대_구매_수량 */ ,I.USE_PLAC /* 사용_장소 */ ,I.USE_LIMT_DESC /* 사용_제한_설명 */ ,I.NOTICE_MATT /* 공지_사항 */ ,I.PRCHS_CANCEL_DRBK_REASON /* 구매_취소_환불_사유 */ ,I.B2B_PROD_YN /* B2B_상품_여부 */ ,I.DLV_PROD_YN /* 배송_상품_여부 */ ,CASE WHEN N.PROD_ID IS NOT NULL THEN 'Y' ELSE 'N'END AS SPECIAL_SALE_YN ,CASE WHEN N.PROD_ID IS NOT NULL THEN N.CPN_ID ELSE '' END AS SPECIAL_COUPON_ID
,(SELECT ( CASE WHEN COUNT( PD.NOTI_SEQ )=0 THEN 'feedback' END ) FROM TB_DP_PROD_NOTI PD WHERE PD.PROD_ID = H.PART_PROD_ID
AND PD.MBR_NO = #{req.userKey}
AND PD.NOTI_TYPE_CD = 'DP000401' AND PD.DEL_YN = 'N' AND PD.TENANT_ID = #{tenantHeader.tenantId} /* INPUT 테넌트ID */ )AS ALLOW
,NVL( ( SELECT SUM( TSPP.PRCHS_CNT ) FROM TB_DP_SPRC_PROD_PRCHS_MANG TSPP WHERE TO_CHAR(TSPP.PRCHS_DT,'YYYYMMDD') BETWEEN CONCAT( TO_CHAR( SYSDATE, 'YYYYMM' ), '01' ) AND TO_CHAR( LAST_DAY( SYSDATE ),'YYYYMMDD') AND TSPP.PROD_ID = H.PART_PROD_ID ) , 0 ) AS MTH_PRCHS_CNT ,NVL( ( SELECT TSPP.PRCHS_CNT FROM TB_DP_SPRC_PROD_PRCHS_MANG TSPP WHERE TO_CHAR(TSPP.PRCHS_DT,'YYYYMMDD') = TO_CHAR( SYSDATE, 'YYYYMMDD' ) AND TSPP.PROD_ID = H.PART_PROD_ID ) , 0 ) AS DLY_PRCHS_CNT ,ROW_NUMBER() OVER(PARTITION BY B.CATALOG_ID ORDER BY NVL(N.SPRC_PRICE, L.PROD_AMT) ASC) as NO ,(SELECT SRC_CONTENT_ID FROM TB_DP_SHPG_PROD WHERE prod_id=H.PROD_ID ) AS couponCode ,I.SRC_CONTENT_ID AS itemCode ,(CASE WHEN (SELECT EPSD_CNT FROM TB_DP_SHPG_PROD WHERE PROD_ID = H.PROD_ID)>0 THEN (SELECT PROD_STATUS_CD FROM TB_DP_TENANT_PROD WHERE TENANT_ID=#{tenantHeader.tenantId} AND PROD_ID=H.PROD_ID) ELSE 'PD000404' END ) PROD_STATUS_CD ,O.PROD_NM as catalogNm FROM TB_DP_SHPG_BRAND A ,TB_DP_SHPG_CATALOG B ,TB_DP_SHPG_CATALOG_DESC C ,TB_DP_MENU_SHPG_MAPG D ,TB_DP_MENU_CATEGORY E ,TB_DP_MENU_CATEGORY_DESC F ,TB_DP_PROD_CATALOG_MAPG G ,TB_DP_PROD_RSHP H ,TB_DP_SHPG_PROD I ,TB_DP_PROD J ,TB_DP_TENANT_PROD K ,TB_DP_TENANT_PROD_PRICE L ,TB_DP_PROD_DESC O ,(SELECT /*+ ORDERED */ B1.PROD_ID , B1.SPRC_DC_RATE , B1.SPRC_PRICE , B1.SPRC_DC_PRICE , B1.SPRC_APPLY_START_DT , B1.SPRC_APPLY_END_DT , B1.MM_PROD_MAX_SALE_QTY , B1.DLY_PROD_MAX_SALE_QTY , B1.PERMAN_MM_MAX_PRCHS_QTY , B1.PERMAN_DAY_MAX_PRCHS_QTY , B1.ONCE_MAX_PRCHS_POSB_CNT , B1.CPN_ID FROM TB_DP_PROD_RSHP A1 ,TB_DP_SPRC_PROD B1 WHERE 1=1 AND A1.PART_PROD_ID = B1.PROD_ID
AND B1.PROD_ID = #{req.specialProdId}
AND B1.EXPO_YN ='Y' AND B1.CPN_ID IS NOT NULL AND SYSDATE BETWEEN B1.SPRC_APPLY_START_DT AND B1.SPRC_APPLY_END_DT )N WHERE 1=1 AND A.BRAND_ID = B.BRAND_ID AND B.CATALOG_ID = C.CATALOG_ID AND C.CATALOG_ID = D.CATALOG_ID AND D.MENU_ID = E.MENU_ID AND D.MENU_ID = F.MENU_ID AND B.CATALOG_ID = G.CATALOG_ID AND G.PROD_ID = H.PROD_ID AND H.PROD_ID <> H.PART_PROD_ID AND H.PART_PROD_ID = I.PROD_ID AND I.PROD_ID = J.PROD_ID AND I.PROD_ID = K.PROD_ID AND I.PROD_ID = L.PROD_ID AND I.PROD_ID = O.PROD_ID AND K.TENANT_ID = L.TENANT_ID AND I.PROD_ID = N.PROD_ID(+) AND H.PROD_RSHP_CD =#{prodRshpCd} /*채널-에피소드 상품 관계*/ AND NVL(B.CHNL_CNT, 0) > 0 AND C.LANG_CD= #{lang} /* ko : 한국어 */ AND F.LANG_CD= #{lang} /* ko : 한국어 */ AND O.LANG_CD= #{lang} /* ko : 한국어 */ AND D.BASE_YN='Y' /* Y : 기본(기본여부) */ AND D.USE_YN ='Y' /* Y : 기본(기본여부) */ AND E.USE_YN='Y' /* Y : 기본(기본여부) */ AND G.BASE_YN='Y' /* Y : 기본(기본여부) */ AND G.USE_YN ='Y' /* Y : 기본(기본여부) */ AND NVL(I.B2B_PROD_YN, 'N') <> 'Y' AND K.EXPO_YN = 'Y' /* Y : 노출(노출여부) */ AND SYSDATE BETWEEN L.APPLY_START_DT AND L.APPLY_END_DT AND K.TENANT_ID = #{tenantHeader.tenantId} /* INPUT 테넌트ID */ AND B.CATALOG_ID = #{req.productId}
AND I.PROD_ID = #{req.specialProdId}
AND EXISTS ( SELECT 1 FROM TB_DP_SPRT_DEVICE DV WHERE 1=1 AND (DV.DEVICE_MODEL_CD = #{deviceHeader.model} OR DV.DEVICE_MODEL_CD = #{req.virtualDeviceModelNo}) AND PROD_ID = I.PROD_ID )
) KK
WHERE NO <= #{endRow}

/* ShoppingMapper.xml.getShoppingImgDetailList,쇼핑상세(이미지) 조회, 김형식/SK플래닛  , 2014-02-04 */
   SELECT 
            CONCAT(FILE_PATH,FILE_NM) FILE_PATH 
          , EXPO_ORD
     FROM TB_DP_PROD_IMG
    WHERE PROD_ID = #{req.productId} 
      AND IMG_CD =#{cutDetailImageCd}
      AND LANG_CD= #{lang}          /* ko : 한국어 */
    ORDER BY EXPO_ORD
