mapper.select:
SELECT /* VodDetailMapper.xml, selectVodChannel, SAC : 임근대 , 2014-01-28 */ P.TENANT_ID, P.LANG_CD , P.MENU_ID, P.MENU_NM, P2.TOP_MENU_ID, (SELECT MENU_NM FROM TB_DP_MENU_CATEGORY_DESC WHERE MENU_ID = P2.TOP_MENU_ID AND LANG_CD = P.LANG_CD AND ROWNUM = 1) AS TOP_MENU_NM , P.EPSD_PLAY_TIME AS RUNNING_TIME , P2.META_CLSF_CD, P2.CID, TP.PROD_STATUS_CD, P2.PROD_GRD_CD , P2.PROD_ID, P2.REG_DT, P2.SVC_GRP_CD, MP.GENRE_CD , PD.PROD_NM, PD.PROD_BASE_DESC, PD.PROD_DTL_DESC, PD.PROD_INTR_DSCR, PD.ARTIST1_NM, PD.ARTIST2_NM , ( CASE WHEN ( MP.EPSD_CNT > 0 AND TPP.CHNL_UNLMT_AMT >= 0 ) THEN TPP.CHNL_UNLMT_AMT END ) AS STORE_AMT , ( CASE WHEN ( MP.EPSD_CNT > 0 AND TPP.PROD_AMT >= 0 ) THEN TPP.PROD_AMT END ) AS PLAY_AMT , MP.DWLD_AREA_LIMT_YN, MP.SVC_START_DT, MP.CHNL_COMP_NM, MP.AGENCY_NM , P2.SELLER_MBR_NO, P2.EXPO_SELLER_NM, P2.EXPO_SELLER_TELNO, P2.EXPO_SELLER_EMAIL , MP.BRDC_COMP_CD, (SELECT CD_NM FROM TB_CM_CD WHERE CD_ID = MP.BRDC_COMP_CD) AS BRDC_COMP_CD_NM /* 방송사 */ , MP.HDV_YN, MP.HDCP_YN, MP.DOLBY_SPRT_YN , MP.SAMPL_URL, MP.SC_SAMPL_URL , P2.SELLER_MBR_NO, P2.EXPO_SELLER_NM, P2.EXPO_SELLER_TELNO, P2.EXPO_SELLER_EMAIL , PI.FILE_PATH AS IMG_PATH, PI.FILE_NM AS IMG_NM, PI.FILE_SIZE AS IMG_SIZE , NVL(STAT.PATICPERS_CNT, 0) AS PATICPERS_CNT, NVL(STAT.PRCHS_CNT, 0) AS PRCHS_CNT
, ( CASE WHEN ( MP.EPSD_CNT > 0 AND MP.STRM_NETWORK_CD = 'DP004402' ) THEN 'restrict' END ) AS STORE_DWLD_NET , ( CASE WHEN ( MP.EPSD_CNT > 0 AND MP.DWLD_NETWORK_CD = 'DP004402' ) THEN 'restrict' END ) AS PLAY_DWLD_NET , ( CASE WHEN ( MP.EPSD_CNT > 0 AND NVL( P.STORE_DRM, 0 ) > 0 ) THEN 'drm' ELSE NULL END ) AS STORE_DRM , ( CASE WHEN ( MP.EPSD_CNT > 0 AND NVL( P.PLAY_DRM, 0 ) > 0 ) THEN 'drm' ELSE NULL END ) AS PLAY_DRM , NVL((CASE WHEN (STAT.AVG_EVLU_SCORE > 0 AND STAT.AVG_EVLU_SCORE < 1) THEN 1 WHEN STAT.AVG_EVLU_SCORE > 5 THEN 5 ELSE ROUND(STAT.AVG_EVLU_SCORE, 1) END), 0) AS AVG_EVLU_SCORE
FROM ( SELECT RSHP.PROD_ID AS CHNL_PROD_ID, G.TENANT_ID, C.LANG_CD , MAX( D.USE_PERIOD_UNIT_CD ) AS USE_PERIOD_UNIT_CD , MAX( A.MENU_ID ) AS MENU_ID, MAX( C.MENU_NM ) AS MENU_NM , MAX( D.USE_PERIOD ) AS USE_TERM , SUM( DECODE( NVL ( CASE WHEN D.USE_PERIOD_UNIT_CD = 'PD00310' THEN D.DRM_YN END , 'N' ), 'Y', 1, 0 ) ) AS STORE_DRM , SUM( DECODE( NVL ( CASE WHEN D.USE_PERIOD_UNIT_CD != 'PD00310' THEN D.DRM_YN END , 'N' ), 'Y', 1, 0 ) ) AS PLAY_DRM , MAX( SC.EPSD_PLAY_TM ) AS EPSD_PLAY_TIME , MAX( L.DOLBY_SPRT_YN ) AS DOLBY_SPRT_YN FROM TB_DP_MENU_CATEGORY_PROD_MAPG A INNER JOIN TB_DP_MENU_CATEGORY B ON A.MENU_ID = B.MENU_ID INNER JOIN TB_DP_MENU_CATEGORY_DESC C ON A.MENU_ID = C.MENU_ID INNER JOIN TB_DP_PROD_RSHP RSHP ON RSHP.PROD_ID = A.PROD_ID INNER JOIN TB_DP_PROD D ON RSHP.PART_PROD_ID = D.PROD_ID INNER JOIN TB_DP_MULTIMDA_PROD E ON D.PROD_ID = E.PROD_ID INNER JOIN TB_DP_TENANT_PROD G ON D.PROD_ID = G.PROD_ID INNER JOIN TB_DP_TENANT_PROD_PRICE O ON G.PROD_ID = O.PROD_ID AND O.TENANT_ID = G.TENANT_ID INNER JOIN TB_DP_SUB_CONTENTS SC ON D.PROD_ID = SC.PROD_ID INNER JOIN TB_DP_SPRT_DEVICE K ON D.PROD_ID = K.PROD_ID INNER JOIN TB_CM_DEVICE L ON K.DEVICE_MODEL_CD = L.DEVICE_MODEL_CD WHERE 1=1 AND G.TENANT_ID = #{tenantId} /* tenant ID */ AND C.LANG_CD = #{langCd} /* 언어 */ AND RSHP.PROD_ID = #{channelId} /* channel ID */ AND G.PROD_STATUS_CD IN ('PD000402', 'PD000403', 'PD000404') /* PD000402 : 판매대기 , PD000403 : 판매중, PD000404 : 판매중지 */ AND G.EXPO_YN = 'Y' /* Y : 노출 (노출여부) */ AND B.USE_YN = 'Y' /* Y : 사용 (사용여부) */ AND D.SVC_GRP_CD = 'DP000203' /* DP000201 : 멀티미디어 */ AND D.CONTENTS_TYPE_CD = 'PD002502' /* PD002502 : */ AND D.TOP_MENU_ID IN ('DP17', 'DP18') /* 영화,TV방송 */ AND SC.SALE_YN ='Y' AND SYSDATE BETWEEN O.APPLY_START_DT AND O.APPLY_END_DT GROUP BY RSHP.PROD_ID, G.TENANT_ID, C.LANG_CD ) P INNER JOIN TB_DP_PROD P2 ON P2.PROD_ID = P.CHNL_PROD_ID INNER JOIN TB_DP_MULTIMDA_PROD MP ON P2.PROD_ID = MP.PROD_ID INNER JOIN TB_DP_TENANT_PROD TP ON P2.PROD_ID = TP.PROD_ID AND P.TENANT_ID = TP.TENANT_ID INNER JOIN TB_DP_TENANT_PROD_PRICE TPP ON P2.PROD_ID = TPP.PROD_ID AND P.TENANT_ID = TPP.TENANT_ID INNER JOIN TB_DP_PROD_DESC PD ON P2.PROD_ID = PD.PROD_ID AND P.LANG_CD = PD.LANG_CD INNER JOIN TB_DP_PROD_IMG PI ON PI.PROD_ID = P.CHNL_PROD_ID INNER JOIN TB_DP_SPRT_DEVICE SPDV ON P2.PROD_ID = SPDV.PROD_ID INNER JOIN TB_CM_DEVICE DV ON SPDV.DEVICE_MODEL_CD = DV.DEVICE_MODEL_CD LEFT OUTER JOIN TB_DP_TENANT_PROD_STATS STAT ON STAT.PROD_ID = P2.PROD_ID AND P.TENANT_ID = STAT.TENANT_ID WHERE 1=1 AND PI.EXPO_ORD = '1' AND PI.IMG_CD = #{imgCd} AND SYSDATE BETWEEN TPP.APPLY_START_DT AND TPP.APPLY_END_DT AND DV.DEVICE_MODEL_CD = #{deviceModel}
