mapper.select:
SELECT /* MemberCommonMapper.getClauseDetail, 약관상세조회, 강신완/부르칸, 2014-02-12 */ clause_id AS clauseId, clause_item_cd AS clauseItemCd, start_day AS startDay, end_day AS endDay, file_path AS filePath, file_nm AS fileNm, clause_ver AS clauseVer, up_clause_item_cd AS upClauseItemCd, dp_yn AS dpYn, reg_id AS regId, TO_CHAR(reg_dt, 'YYYYMMDDHH24MISS') AS regDt, upd_id AS updId, TO_CHAR(upd_dt, 'YYYYMMDDHH24MISS') AS updDt FROM TB_CM_CLAUSE WHERE dp_yn='Y'
AND clause_id = #{clauseId}
ORDER BY clause_id ASC

SELECT /* MemberCommonMapper.getPhoneInfo, 폰정보 조회, 심대진/다모아, 2014-01-08 */ DEVICE_MODEL_CD, MODEL_NM, ENG_MODEL_NM, REP_DEVICE_YN, UA_CD, LIST_IMG_PATH, DTL_IMG_PATH, CMNT_COMP_CD, CPU_TYPE_CD, VM_VER_CD, LCD_SIZE_CD, COLOR_CD, AUDIO_TYPE_CD, AUDIO_POLY_CD, GIGA_VER_CD, DRM_VER_CD, V4_SPRT_TYPE_CD, DEVICE_TYPE_CD, PRCHS_POSB_TYPE_CD, INNER_MEM_SIZE, NETWORK_TYPE, USE_YN, DP_YN, FEATURE_PHONE_YN, CAMERA_SPRT_YN, EXTRR_MEM_YN, TOUCH_SCREEN_YN, BLUETOOTH_SPRT_YN, GPS_SPRT_YN, ITOPP_V4_SPRT_YN, SOUND_3D_SPRT_YN, VIRTUAL_KEY_YN, T_PLAYER_SPRT_YN, VIDEO_DRM_SPRT_YN, CMF_SPRT_YN, EMBEDDED_YN, WSL_SPRT_YN, SD_VIDEO_SPRT_YN, FR_DVC_YN, VERIFY_DVC_YN, EBOOK_SPRT_YN, COMIC_SPRT_YN, HDV_SPRT_YN, KPAD_SPRT_YN, KWAC_SPRT_YN, FLASH_SPRT_YN, HDMI_SPRT_YN, HDCP_SPRT_YN, GIFT_SPRT_YN, SCL_SHPG_SPRT_YN, MAGAZINE_SPRT_YN, MUSIC_SPRT_YN, AOM_SPRT_YN, SKT_DRM_SPRT_YN, STRM_SPRT_YN, WGTP_SPRT_YN, ARM_SPRT_YN, VOD_FIXISTT_SPRT_YN, DOLBY_SPRT_YN, WEBTOON_SPRT_YN, REG_ID, REG_DT, UPD_ID, UPD_DT, (SELECT CD_NM FROM TB_CM_CD WHERE CD_ID = MNFT_COMP_CD AND LANG_CD = 'ko') AS MNFT_COMP_CD, (SELECT CD_NM FROM TB_CM_CD WHERE CD_ID = VM_TYPE_CD AND LANG_CD = 'ko') AS VM_TYPE_CD FROM TB_CM_DEVICE WHERE REP_DEVICE_YN = 'Y'
AND DEVICE_MODEL_CD = #{deviceModelCd}
AND UA_CD = #{uaCd}
