mapper.select:
SELECT  /* MiscellaneousMapper.searchPhoneAuthInfo, 휴대폰 SMS 인증 정보 확인 (인증 코드,Signature,만료시간), 김다슬/인크로스, 2014-01-16 */
        auth_seq
      , auth_sign
      , auth_value
        
      , TO_CHAR(auth_value_create_dt - (SYSDATE - (#{timeToLive}/24/60))) AS curr_dt  /* 인증코드 생성일자-(현재-생존시간) */
        
      , TO_CHAR(auth_value_create_dt, 'yyyyMMdd hh24:mi:ss') AS auth_value_create_dt 
      , auth_compt_yn
      , NVL(auth_cnt, 0) auth_cnt
  FROM  tb_cm_svc_auth
 WHERE  auth_type_cd = #{authTypeCd}
   AND  auth_sign = #{authSign}
   AND  auth_value = #{authValue}
   AND  auth_mdn = #{authMdn}

    SELECT /* MiscellaneousMapper.searchPhoneAuthCnt, 휴대폰 SMS 인증 카운트, 김영균/지티소프트, 2014-08-27 */ 
           auth_cnt
      FROM(SELECT NVL(auth_cnt, 0) auth_cnt,
                  ROW_NUMBER () OVER (ORDER BY auth_seq DESC) rnum
		    FROM tb_cm_svc_auth
		    WHERE  tenant_id = #{tenantId}
		      AND  system_id = #{systemId}
		      AND  auth_mdn = #{authMdn}
		      AND  auth_type_cd = #{authTypeCd}  /* 인증 타입 (CM010901:SMS, CM010902:Email) */ 
		      AND  auth_compt_yn = 'N'       
		      AND auth_value_create_dt >= (SYSDATE - (#{timeToLive} / 24 / 60))        /* 3분이내 */
      )WHERE rnum = 1

mapper.insert:
INSERT /* MiscellaneousMapper.createServiceAuthCode, 휴대폰 SMS 및 이메일 인증 코드 정보 저장, 김다슬/인크로스, 2014-01-21 */ INTO tb_cm_svc_auth ( auth_seq
, mbr_no
, tenant_id
, system_id
, auth_mdn /* SMS 인증 대상 번호 */
, auth_type_cd /* 인증 타입 (CM010901:SMS, CM010902:Email, CM010903:EmailAuthUrl) */
, auth_sign /* 인증 Signature */
, auth_value /* 인증 코드 */
, auth_email /* Email 인증일 경우 수신자 Email 주소 포함 */
, auth_value_create_dt /* 인증 코드 생성 일자 */ , auth_compt_yn /* 인증 여부 - 초기화 */ , reg_id , reg_dt , upd_id , upd_dt ) VALUES ( sq_cm_svc_auth.NEXTVAL
, #{mbrNo}
, #{tenantId}
, #{systemId}
, #{authMdn}
, #{authTypeCd}
, #{authSign}
, #{authValue}
, #{authEmail}
, SYSDATE , 'N' , 'admin' , SYSDATE , 'admin' , SYSDATE )

mapper.update:
UPDATE  /* MiscellaneousMapper.updateServiceAuthCode, 이메일 인증 코드 업데이트, 김다슬/인크로스, 2014-04-15 */
        tb_cm_svc_auth
   SET  auth_value = #{authValue}
      , auth_email = #{authEmail}
      , auth_value_create_dt = SYSDATE
      , upd_id = 'admin'
      , upd_dt = SYSDATE
 WHERE  auth_seq = #{authSeq}

mapper.select:
SELECT /* MiscellaneousMapper.searchEmailAuthInfo, 이메일 인증코드 정보 확인, 김다슬/인크로스, 2014-01-23 */ auth_seq , mbr_no , auth_email
, TO_CHAR(auth_value_create_dt -(SYSDATE -#{timeToLive})) AS curr_dt
, TO_CHAR(auth_value_create_dt, 'yyyyMMdd hh24:mi:ss') AS auth_value_create_dt , auth_compt_yn FROM tb_cm_svc_auth WHERE auth_type_cd = #{authTypeCd} /* 인증 타입 (CM010901:SMS, CM010902:Email, CM010903:EmailAuthUrl) */ AND auth_value = #{authValue}

SELECT /* MiscellaneousMapper.xml, searchEmailAuthUrlInfo, SAC(SAC), 김경복, 2015-05-27 */ auth_seq , mbr_no , auth_email
, TO_CHAR(auth_value_create_dt -(SYSDATE - ${timeToLive}/24)) AS curr_dt
, TO_CHAR(auth_value_create_dt, 'yyyyMMdd hh24:mi:ss') AS auth_value_create_dt , auth_compt_yn FROM tb_cm_svc_auth WHERE auth_type_cd = #{authTypeCd} /* 인증 타입 (CM010901:SMS, CM010902:Email, CM010903:EmailAuthUrl) */ AND auth_value = #{authValue}

 SELECT /* MiscellaneousMapper.searchPhoneAuthCheck, SAC (SAC), 윤보영, 2015-03-05 */
        auth_mdn,
        auth_compt_yn,
        TO_CHAR (reg_dt, 'YYYYMMDDHH24MISS') AS reg_dt,
        CASE
          WHEN (auth_compt_yn = 'N' AND auth_cnt IS NULL) THEN NULL -- 인증번호만 요청한 상태
          ELSE TO_CHAR (upd_dt, 'YYYYMMDDHH24MISS')
        END
          upd_dt
 FROM tb_cm_svc_auth
WHERE tenant_id = #{tenantId}
  AND system_id = #{systemId}
  AND auth_type_cd = #{authTypeCd}
  AND auth_mdn = #{authMdn}
  AND auth_sign = #{authSign}
