mapper.select:
	   /* MiscellaneousMapper.xml, getPhoneAuthYn, SAC : 김다슬 , 2014-01-16 */
       SELECT 
           AUTH_SEQ,
           AUTH_SIGN,
           AUTH_VALUE,
           TO_CHAR(AUTH_VALUE_CREATE_DT, 'yyyymmddhh24miss') AS AUTH_VALUE_CREATE_DT,
           AUTH_COMPT_YN
       FROM
           TB_CM_SVC_AUTH
       WHERE
           AUTH_TYPE_CD = #{authTypeCd}
           and AUTH_SIGN = #{authSign}
           and AUTH_VALUE = #{authValue}
