mapper.select:
SELECT  /* FeedbackMapper.getProdNotiWDCount, 탈퇴회원 사용후기 기등록 여부, 김현일/SK플래닛, 2014-02-04 */
        COUNT(*)
  FROM  tb_dp_prod_noti_wd
 WHERE  tenant_id = #{tenantId}
   AND  noti_seq = #{notiSeq}

SELECT /* FeedbackMapper.getProdNotiList, 사용후기 전체 목록 조회, 김현일/SK플래닛, 2014-02-04 */ a.noti_seq /* 후기 SEQ */ , a.mbr_no /* 회원 번호 */ , a.seller_mbr_no /* 판매자 회원 번호 */ , a.title /* 제목 */ , a.noti_dscr /* 후기 내용 */ , NVL( a.noti_score, 0 ) AS noti_score /* 후기 점수(추천) */ , a.mbr_telno /* 회원 전화번호 */ , a.reg_id /* 등록 ID */ , TO_CHAR(a.reg_dt,'YYYYMMDDHH24MISS') AS reg_dt /* 등록 일시 */ , a.seller_resp_title /* 판매자 응답 제목 */ , a.seller_resp_opin /* 판매자 응답 내용 */ , NVL2( a.seller_resp_opin, TO_CHAR( a.seller_resp_reg_dt, 'YYYYMMDDHH24MISS' ), NULL ) AS seller_resp_reg_dt /* 판매자 응답 일시 */ , a.cid /* CID */ , DECODE( a.mbr_no, #{mbrNo}, 'Y', 'N' ) AS self_yn /* 본인 여부 */ , DECODE( a.mbr_no, a.seller_mbr_no, 'Y','N' ) AS sale_yn /* 판매자 여부 */ , DECODE( a.mbr_no, a.seller_mbr_no, 'seller', #{mbrNo}, 'mine', '' ) AS whose /* 판매자 본인 식별 */ , ( CASE WHEN ( SELECT COUNT(*) /* 상품 후기 선호 갯수 */ FROM tb_dp_prod_notigood l /* 상품 후기 선호 */ WHERE l.tenant_id = a.tenant_id /* 테넌트 ID */ AND l.mbr_no = #{mbrNo} /* 회원 번호 */ AND l.noti_seq = a.noti_seq /* 후기 SEQ */ ) > 0 THEN 'Y' /* 선호 갯수가 0보다 큰것 */ ELSE 'N' END ) AS noti_yn /* 본인 추천여부 ID */ , ( SELECT avg_score /* 평균 점수 */ FROM tb_dp_mbr_avg /* 상품 평점 */ WHERE tenant_id = a.tenant_id /* 테넌트 ID */ AND prod_id = a.prod_id /* 상품 ID */ AND mbr_no = a.mbr_no /* 회원 번호 */ ) AS avg_score /* 평균 점수 */ , a.fb_post_yn /* 페이스북 POST 여부 */ , a.prod_id /* 상품 ID */ , a.expo_seller_nm /* 노출 판매자 명 */ , a.svc_grp_cd /* 서비스 그룹 코드 */ FROM ( SELECT ia.* FROM ( SELECT ia.* , ROWNUM AS rnum FROM ( SELECT
/*+ INDEX_DESC(N IDX_DP_PROD_NOTI_05) */
/*+ INDEX_DESC(N IDX_DP_PROD_NOTI_01) */
/*+ INDEX_DESC(N IDX_DP_PROD_NOTI_06) */
/*+ INDEX_DESC(N IDX_DP_PROD_NOTI_05) */
ia.tenant_id /* 테넌트 ID */ , ia.noti_seq /* 후기 SEQ */ , ia.title /* 제목 */ , ia.noti_dscr /* 후기 내용 */ , ia.reg_dt /* 등록 일시 */ , ia.mbr_no /* 회원 번호 */ , ia.reg_id /* 등록 ID */ , ia.mbr_telno /* 회원 전화번호 */ , ia.noti_score /* 후기 점수(추천) */ , ia.seller_resp_title /* 판매자 응답 제목 */ , ia.seller_resp_opin /* 판매자 응답 의견 */ , ia.seller_resp_reg_dt /* 판매자 응답 일시 */ , ia.prod_id /* 상품 ID */ , ia.fb_post_yn /* 페이스북 POST 여부 */ , ib.seller_mbr_no /* 판매자 회원 번호 */ , ib.cid /* CID */ , ib.svc_grp_cd /* 서비스 그룹 코드 */ , ib.expo_seller_nm /* 노출 판매자 명 */ FROM tb_dp_prod_noti ia /* 상품 후기 */ , tb_dp_prod ib /* 상품 */ WHERE ia.tenant_id = #{tenantId} /* 테넌트 ID */ AND ib.prod_id = ia.prod_id /* 상품 ID */
AND ia.noti_seq = #{notiSeq} /* 후기 SEQ */
AND ia.catalog_id = #{prodId} /* 카탈로그 ID */
AND ia.prod_id = #{chnlId} /* 상품 ID */
AND ia.prod_id = #{prodId} /* 상품 ID */
AND ia.noti_type_cd = 'DP000401' /* 후기 타입 코드 */ AND ia.del_yn = 'N' /* 삭제 여부 */ AND ia.bad_noti_yn IN ( 'N', 'R' ) /* 불량 후기 여부 */ AND ia.spam_yn = 'N' /* 스펨 여부 */
ORDER BY ia.noti_score DESC , ia.reg_dt DESC
ORDER BY ia.reg_dt DESC
) ia WHERE ROWNUM <= #{endRow} ) ia WHERE ia.rnum BETWEEN #{startRow} AND #{endRow} ) a

SELECT /* FeedbackMapper.getProdNotiList, 자기가작성한 사용후기, 김현일/SK플래닛, 2014-02-04 */ a.noti_seq /* 후기 SEQ */ , a.mbr_no /* 회원 번호 */ , a.seller_mbr_no /* 판매자 회원 번호 */ , NVL2( a.title, a.title, NVL2( a.noti_dscr, NULL, default_msg ) ) AS title /* 제목 */ , NVL2( a.noti_dscr, a.noti_dscr, default_msg ) AS noti_dscr /* 후기 내용 */ , NVL( a.noti_score, 0 ) AS noti_score /* 후기 점수(추천) */ , a.mbr_telno /* 회원 전화번호 */ , a.reg_id /* 등록 ID */ , TO_CHAR(a.reg_dt,'YYYYMMDDHH24MISS') AS reg_dt /* 등록일시 */ , a.seller_resp_title /* 판매자 응답 제목 */ , a.seller_resp_opin /* 판매자 응답 의견 */ , NVL2( a.seller_resp_opin, TO_CHAR(a.seller_resp_reg_dt,'YYYYMMDDHH24MISS'), NULL ) AS seller_resp_reg_dt /* 판매자 응답 일시 */ , a.cid /* CID */ , 'mine' AS WHOSE /* 본인 노출 */ , ( CASE WHEN ( SELECT COUNT(*) /* 상품 후기 선호 갯수 */ FROM tb_dp_prod_notigood l /* 상품 후기 선호 */ WHERE l.tenant_id = a.tenant_id /* 테넌트 ID */ AND l.mbr_no = #{mbrNo} /* 회원 번호 */ AND l.noti_seq = a.noti_seq /* 후기 SEQ */ ) > 0 THEN 'Y' /* 후기 선호 갯수가 0보다 큰것 */ ELSE 'N' END ) AS noti_yn /* 본인 추천 여부 */ , a.avg_score /* 평균 점수 */ , a.fb_post_yn /* 페이스북 POST 여부 */ , a.prod_id /* 상품 ID */ , a.expo_seller_nm /* 노출 판매자 명 */ , a.svc_grp_cd /* 서비스 그룹 코드 */ FROM ( SELECT ia.* , DECODE ( ia.avg_score , 1, #{defaultMsg1} /* 평균 점수가 1이면 1번 메시지(메시지는 소스상 상수로 선언) */ , 2, #{defaultMsg2} /* 평균 점수가 2이면 2번 메시지(메시지는 소스상 상수로 선언) */ , 3, #{defaultMsg3} /* 평균 점수가 3이면 3번 메시지(메시지는 소스상 상수로 선언) */ , 4, #{defaultMsg4} /* 평균 점수가 4이면 4번 메시지(메시지는 소스상 상수로 선언) */ , 5, #{defaultMsg5} /* 평균 점수가 5이면 5번 메시지(메시지는 소스상 상수로 선언) */ , NULL ) AS default_msg /* 기본 메시지 */ FROM ( SELECT ia.* , ROWNUM AS rnum FROM (
SELECT MAX( tenant_id ) AS tenant_id /* 테넌트 ID */ , MAX( noti_seq ) AS noti_seq /* 후기 SEQ */ , MAX( title ) AS title /* 제목 */ , MAX( noti_dscr ) AS noti_dscr /* 후기 내용 */ , MAX( reg_dt ) AS reg_dt /* 등록 일시 */ , MAX( mbr_no ) AS mbr_no /* 회원 번호 */ , MAX( reg_id ) AS reg_id /* 등록 ID */ , MAX( mbr_telno ) AS mbr_telno /* 회원 전화번호 */ , MAX( noti_score ) AS noti_score /* 후기 점수(추천) */ , MAX( seller_resp_title ) AS seller_resp_title /* 판매자 응답 제목 */ , MAX( seller_resp_opin ) AS seller_resp_opin /* 판매자 응답 의견 */ , MAX( seller_resp_reg_dt ) AS seller_resp_reg_dt /* 판매자 응답 일시 */ , MAX( fb_post_yn ) AS fb_post_yn /* 페이스북 POST 여부 */ , MAX( prod_id ) AS prod_id /* 상품 ID */ , MAX( seller_mbr_no ) AS seller_mbr_no /* 판매자 회원 번호 */ , MAX( cid ) AS cid /* CID */ , MAX( svc_grp_cd ) AS svc_grp_cd /* 서비스 그룹 코드 */ , MAX( avg_score ) AS avg_score /* 평균 점수 */ , MAX( expo_seller_nm ) AS expo_seller_nm /* 노출 판매자 명 */ FROM ( SELECT * FROM ( SELECT ia.tenant_id /* 테넌트 ID */ , ia.noti_seq /* 후기 SEQ */ , ia.title /* 제목 */ , ia.noti_dscr /* 후기 내용 */ , ia.reg_dt /* 등록 일시 */ , ia.mbr_no /* 회원 번호 */ , ia.reg_id /* 등록 ID */ , ia.mbr_telno /* 회원 전화번호 */ , ia.noti_score /* 후기 점수(추천) */ , ia.seller_resp_title /* 판매자 응답 제목 */ , ia.seller_resp_opin /* 판매자 응답 의견 */ , ia.seller_resp_reg_dt /* 판매자 응답 일시 */ , ia.fb_post_yn /* 페이스북 POST 여부 */ , ia.prod_id /* 상품 ID */ , ib.seller_mbr_no /* 판매자 회원 번호 */ , ib.cid /* CID */ , ib.svc_grp_cd /* 서비스 그룹 코드 */ , ib.expo_seller_nm /* 노출 판매자 명 */ , NULL avg_score /* 평균 점수 */
, ROW_NUMBER() OVER (PARTITION BY ia.prod_id ORDER BY ia.reg_dt DESC) AS ranking /* 여러건수중 최신후기 한건*/ FROM tb_dp_prod_noti ia /* 상품 후기 */ , tb_dp_prod ib /* 상품 */ WHERE ia.tenant_id = #{tenantId} /* 테넌트 ID */ AND ib.prod_id = ia.prod_id /* 상품 ID */ AND ia.mbr_no = #{mbrNo} /* 회원 번호 */
AND ia.catalog_id IN
#{prodId}
AND ia.prod_id = #{chnlId}
AND ia.prod_id IN
#{prodId}
AND ia.noti_type_cd = 'DP000401' /* 후기 타입 코드 */ AND ia.del_yn = 'N' /* 삭제 여부 */ AND ia.bad_noti_yn IN ( 'N', 'R' ) /* 불량 후기 여부 */ AND ia.spam_yn = 'N' /* 스펨 여부 */ ) WHERE ranking = 1
UNION ALL SELECT NULL AS tenant_id , NULL AS noti_seq , NULL AS title , NULL AS noti_dscr , ic.reg_dt AS reg_dt /* 등록 일시 */ , ic.mbr_no AS mbr_no /* 회원 번호 */ , NULL AS reg_id , NULL AS mbr_telno , NULL AS noti_score , NULL AS seller_resp_title , NULL AS seller_resp_opin , NULL AS seller_resp_reg_dt , NULL AS fb_post_yn , ic.prod_id /* 상품 ID */ , NULL AS seller_mbr_no , NULL AS cid , NULL AS svc_grp_cd , NULL AS expo_seller_nm , ic.avg_score /* 평균 점수 */ , NULL AS ranking FROM tb_dp_mbr_avg ic /* 상품 평점 */ WHERE ic.tenant_id = #{tenantId} /* 테넌트 ID */ AND ic.mbr_no = #{mbrNo} /* 회원 번호 */
AND ic.prod_id IN
#{prodId}
) GROUP BY prod_id ORDER BY reg_dt DESC ) ia WHERE ROWNUM <= #{endRow} ) ia WHERE ia.rnum BETWEEN #{startRow} AND #{endRow} ) a

SELECT /* FeedbackMapper.getMyProdNotiCount, 자기가 작성한 사용후기 갯수, 김현일/SK플래닛, 2014-02-04 */ COUNT(*) AS total_count /* 후기 갯수 */ FROM ( SELECT prod_id AS prod_id FROM ( SELECT * FROM ( SELECT la.prod_id /* 상품 ID */ , ROW_NUMBER() OVER (PARTITION BY la.prod_id ORDER BY la.reg_dt DESC) AS ranking /* 여러건수중 최신후기 한건*/ FROM tb_dp_prod_noti la /* 상품 후기 */ , tb_dp_prod lb /* 상품 */ WHERE la.tenant_id = #{tenantId} /* 테넌트 ID */ AND lb.prod_id = la.prod_id /* 상품 ID */ AND la.mbr_no = #{mbrNo} /* 회원 번호 */
AND la.catalog_id IN
#{prodId}
AND la.prod_id = #{chnlId}
AND la.prod_id IN
#{prodId}
AND la.noti_type_cd = 'DP000401' /* 후기 타입 코드 */ AND la.del_yn = 'N' /* 삭제 여부 */ AND la.bad_noti_yn IN ( 'N', 'R' ) /* 불량 후기 여부 */ AND la.spam_yn = 'N' /* 스펨 여부 */ ) WHERE ranking = 1
UNION ALL SELECT lc.prod_id /* 상품 ID */ , NULL AS ranking FROM tb_dp_mbr_avg lc /* 상품 평점 */ WHERE lc.tenant_id = #{tenantId} /* 테넌트 ID */ AND lc.mbr_no = #{mbrNo} /* 회원 번호 */
AND lc.prod_id IN
#{prodId}
) GROUP BY prod_id )

mapper.update:
UPDATE  /* FeedbackMapper.updateSellerRespWD, 탈퇴회원 사용후기 판매자 댓글, 남궁석호/SK플래닛, 2014-02-10 */
        tb_dp_prod_noti_wd
   SET  seller_resp_opin = #{sellerRespOpin}
      , seller_resp_title = #{sellerRespTitle}
      , seller_resp_reg_dt = SYSDATE
 WHERE  tenant_id = #{tenantId}
   AND  noti_seq = #{notiSeq}
