mapper.select:
SELECT /* FeedbackMapper.xml, getRegMbrAvg, SAC : 김현일 , 2014-02-04 */
       AVG_SCORE
  FROM TB_DP_MBR_AVG
 WHERE TENANT_ID = #{tenantId} 
   AND MBR_NO = #{mbrNo}
   AND PROD_ID = #{prodId}

mapper.insert:
/* FeedbackMapper.xml, mergeMbrAvg, SAC : 김현일 , 2014-02-04 */
MERGE INTO TB_DP_MBR_AVG
USING DUAL
   ON ( TENANT_ID = #{tenantId} AND PROD_ID = #{prodId} AND MBR_NO = #{mbrNo} )
 WHEN NOT MATCHED THEN
    INSERT ( TENANT_ID
           , MBR_NO
           , PROD_ID
           , AVG_SCORE
           , REG_ID
           , REG_DT )
    VALUES ( #{tenantId}
           , #{mbrNo}
           , #{prodId}
           , #{avgScore}
           , #{regId}
           , SYSDATE )
 WHEN MATCHED THEN
    UPDATE
       SET AVG_SCORE = #{avgScore}
         , REG_DT = SYSDATE

mapper.delete:
DELETE /* FeedbackMapper.xml, deleteMbrAvg, SAC : 김현일 , 2014-02-04 */ 
  FROM TB_DP_MBR_AVG
 WHERE TENANT_ID = #{tenantId}
   AND PROD_ID = #{prodId}
   AND MBR_NO = #{mbrNo}

mapper.update:
	    /* FeedbackMapper.xml, updateTenantProdStats, SAC : 김현일 , 2014-02-04 */
        UPDATE TB_DP_TENANT_PROD_STATS
	       SET TOT_EVLU_SCORE = NVL(TOT_EVLU_SCORE, 0) + #{avgEvluScore} - #{preAvgScore}
	           , UPD_ID = #{updId}
	           , UPD_DT = SYSDATE
	           
	           , AVG_EVLU_SCORE = ( ( NVL(TOT_EVLU_SCORE, 0) + #{avgEvluScore} - #{preAvgScore} ) / NVL(PATICPERS_CNT, 0) )
	           
	           
	           , PATICPERS_CNT = PATICPERS_CNT - 1
	           , AVG_EVLU_SCORE = ( ( NVL(TOT_EVLU_SCORE, 0) + #{avgEvluScore} - #{preAvgScore} ) / (NVL(PATICPERS_CNT, 0) - 1) )
	           
	     WHERE TENANT_ID = #{tenantId}
	       AND PROD_ID = #{prodId}

mapper.insert:
/* FeedbackMapper.xml, mergeTenantProdStats, SAC : 김현일 , 2014-02-04 */
MERGE INTO TB_DP_TENANT_PROD_STATS
USING DUAL
   ON ( TENANT_ID = #{tenantId} AND PROD_ID = #{prodId} )
WHEN NOT MATCHED THEN
    INSERT ( TENANT_ID
           , PROD_ID
           , AVG_EVLU_SCORE
           , TOT_EVLU_SCORE
           , PATICPERS_CNT
           , REG_ID
           , REG_DT )
    VALUES ( #{tenantId}
           , #{prodId}
           , #{avgEvluScore}
           , #{avgEvluScore}
           , 1
           , #{regId}
           , SYSDATE )
WHEN MATCHED THEN
    UPDATE
       SET AVG_EVLU_SCORE = ((NVL(TOT_EVLU_SCORE, 0) + #{avgEvluScore}) / (NVL(PATICPERS_CNT, 0) + 1))
            , TOT_EVLU_SCORE = NVL(TOT_EVLU_SCORE, 0) + #{avgEvluScore}
            , PATICPERS_CNT = NVL(PATICPERS_CNT, 0) + 1
         , UPD_ID = #{updId}
         , UPD_DT = SYSDATE

