mapper.select:
SELECT  /* FeedbackMapper.xml, getProdNotiCount, SAC : 김현일 , 2014-02-04 */
   SUM( TOTAL_COUNT ) TOTAL_COUNT
FROM (
   SELECT COUNT(*) AS TOTAL_COUNT
     FROM TB_DP_PROD_NOTI N
    WHERE N.TENANT_ID = #{tenantId}
      AND N.DEL_YN = 'N'
      AND N.BAD_NOTI_YN IN ( 'N', 'R' )
      AND N.SPAM_YN = 'N'
      AND N.NOTI_TYPE_CD = 'DP000401'
     
      AND N.NOTI_SEQ = #{notiSeq}
     
     
     
      AND N.CATALOG_ID = #{prodId}
      
      AND N.PROD_ID = #{chnlId}
      
     
     
     
      AND N.PROD_ID = #{prodId}
     
    UNION ALL
   SELECT COUNT(*) AS TOTAL_COUNT
     FROM TB_DP_PROD_NOTI_WD N
    WHERE N.TENANT_ID = #{tenantId}
      AND N.DEL_YN = 'N'
      AND N.BAD_NOTI_YN IN ( 'N', 'R' )
      AND N.SPAM_YN = 'N'
      AND N.NOTI_TYPE_CD = 'DP000401'
     
      AND N.NOTI_SEQ = #{notiSeq}
     
     
     
      AND N.CATALOG_ID = #{prodId}
      
      AND N.PROD_ID = #{chnlId}
      
     
     
     
      AND N.PROD_ID = #{prodId}
     
   )

SELECT /* FeedbackMapper.xml, getProdNotiList, SAC : 김현일 , 2014-02-04 */ N.NOTI_SEQ , N.MBR_NO , N.SELLER_MBR_NO , N.TITLE , N.NOTI_DSCR , NVL( N.NOTI_SCORE, 0 ) AS NOTI_SCORE , N.MBR_TELNO , N.REG_ID , TO_CHAR(N.REG_DT,'YYYYMMDDHH24MISS') AS REG_DT , N.SELLER_RESP_TITLE , N.SELLER_RESP_OPIN , NVL2( N.SELLER_RESP_OPIN, TO_CHAR( N.SELLER_RESP_REG_DT, 'YYYYMMDDHH24MISS' ), NULL ) AS SELLER_RESP_REG_DT , N.CID , DECODE( N.MBR_NO, #{mbrNo}, 'Y', 'N' ) AS SELF_YN , DECODE( N.MBR_NO, N.SELLER_MBR_NO, 'Y','N' ) AS SALE_YN , DECODE( N.MBR_NO, N.SELLER_MBR_NO, 'seller', #{mbrNo}, 'mine', '' ) AS WHOSE , ( CASE WHEN ( SELECT COUNT(*) FROM TB_DP_PROD_NOTIGOOD NG WHERE NG.TENANT_ID = N.TENANT_ID AND NG.MBR_NO = #{mbrNo} AND NG.NOTI_SEQ = N.NOTI_SEQ ) > 0 THEN 'Y' ELSE 'N' END ) AS NOTI_YN , ( SELECT AVG_SCORE FROM TB_DP_MBR_AVG WHERE TENANT_ID = N.TENANT_ID AND PROD_ID = N.PROD_ID AND MBR_NO = N.MBR_NO ) AS AVG_SCORE , N.FB_POST_YN , N.PROD_ID , N.EXPO_SELLER_NM , N.SVC_GRP_CD FROM ( SELECT A.* FROM ( SELECT NOTI.* , ROWNUM AS RNUM FROM ( SELECT N.* FROM ( SELECT * FROM ( SELECT N.TENANT_ID , N.NOTI_SEQ , N.TITLE , N.NOTI_DSCR , N.REG_DT , N.MBR_NO , N.REG_ID , N.MBR_TELNO , N.NOTI_SCORE , N.SELLER_RESP_TITLE , N.SELLER_RESP_OPIN , N.SELLER_RESP_REG_DT , N.PROD_ID , N.FB_POST_YN , P.SELLER_MBR_NO AS SELLER_MBR_NO , P.CID , P.SVC_GRP_CD , P.EXPO_SELLER_NM FROM TB_DP_PROD_NOTI N , TB_DP_PROD P WHERE N.TENANT_ID = #{tenantId} AND P.PROD_ID = N.PROD_ID
AND N.NOTI_SEQ = #{notiSeq}
AND N.CATALOG_ID = #{prodId}
AND N.PROD_ID = #{chnlId}
AND N.PROD_ID = #{prodId}
AND N.NOTI_TYPE_CD = 'DP000401' AND N.DEL_YN = 'N' AND N.BAD_NOTI_YN IN ( 'N', 'R' ) AND N.SPAM_YN = 'N'
ORDER BY N.NOTI_SCORE DESC , N.REG_DT DESC
ORDER BY N.REG_DT DESC
) M WHERE ROWNUM <= #{endRow} UNION ALL SELECT * FROM ( SELECT N.TENANT_ID , N.NOTI_SEQ , N.TITLE , N.NOTI_DSCR , N.REG_DT , N.MBR_NO , N.REG_ID , N.MBR_TELNO , N.NOTI_SCORE , N.SELLER_RESP_TITLE , N.SELLER_RESP_OPIN , N.SELLER_RESP_REG_DT , N.PROD_ID , N.FB_POST_YN , P.SELLER_MBR_NO AS SELLER_MBR_NO , P.CID , P.SVC_GRP_CD , P.EXPO_SELLER_NM FROM TB_DP_PROD_NOTI_WD N , TB_DP_PROD P WHERE N.TENANT_ID = #{tenantId} AND P.PROD_ID = N.PROD_ID
AND N.NOTI_SEQ = #{notiSeq}
AND N.CATALOG_ID = #{prodId}
AND N.PROD_ID = #{chnlId}
AND N.PROD_ID = #{prodId}
AND N.NOTI_TYPE_CD = 'DP000401' AND N.DEL_YN = 'N' AND N.BAD_NOTI_YN IN ( 'N', 'R' ) AND N.SPAM_YN = 'N'
ORDER BY N.NOTI_SCORE DESC , N.REG_DT DESC
ORDER BY N.REG_DT DESC
) M WHERE ROWNUM <= #{endRow} ) N
ORDER BY N.NOTI_SCORE DESC , N.REG_DT DESC
ORDER BY N.REG_DT DESC
) NOTI WHERE ROWNUM <= #{endRow} ) A WHERE A.RNUM BETWEEN #{startRow} AND #{endRow} ) N

SELECT /* FeedbackMapper.xml, getProdNotiList, SAC : 김현일 , 2014-02-04 */ N.NOTI_SEQ , N.MBR_NO , N.SELLER_MBR_NO , NVL2( N.TITLE, N.TITLE, NVL2( N.NOTI_DSCR, NULL, DEFAULT_MSG ) ) AS TITLE , NVL2( N.NOTI_DSCR, N.NOTI_DSCR, DEFAULT_MSG ) AS NOTI_DSCR , NVL( N.NOTI_SCORE, 0 ) AS NOTI_SCORE , N.MBR_TELNO , N.REG_ID , TO_CHAR(N.REG_DT,'YYYYMMDDHH24MISS') AS REG_DT , N.SELLER_RESP_TITLE , N.SELLER_RESP_OPIN , NVL2( N.SELLER_RESP_OPIN, TO_CHAR(N.SELLER_RESP_REG_DT,'YYYYMMDDHH24MISS'), NULL ) AS SELLER_RESP_REG_DT , N.CID , 'mine' AS WHOSE , ( CASE WHEN ( SELECT COUNT(*) FROM TB_DP_PROD_NOTIGOOD NG WHERE NG.TENANT_ID = N.TENANT_ID AND NG.MBR_NO = #{mbrNo} AND NG.NOTI_SEQ = N.NOTI_SEQ ) > 0 THEN 'Y' ELSE 'N' END ) AS NOTI_YN , N.AVG_SCORE , N.FB_POST_YN , N.PROD_ID , N.EXPO_SELLER_NM , N.SVC_GRP_CD FROM ( SELECT N.* , DECODE( N.AVG_SCORE , 1, #{defaultMsg1} , 2, #{defaultMsg2} , 3, #{defaultMsg3} , 4, #{defaultMsg4} , 5, #{defaultMsg5} , NULL ) AS DEFAULT_MSG FROM ( SELECT NOTI.* , ROWNUM AS RNUM FROM ( SELECT MAX( TENANT_ID ) AS TENANT_ID , MAX( NOTI_SEQ ) AS NOTI_SEQ , MAX( TITLE ) AS TITLE , MAX( NOTI_DSCR ) AS NOTI_DSCR , MAX( REG_DT ) AS REG_DT , MAX( MBR_NO ) AS MBR_NO , MAX( REG_ID ) AS REG_ID , MAX( MBR_TELNO ) AS MBR_TELNO , MAX( NOTI_SCORE ) AS NOTI_SCORE , MAX( SELLER_RESP_TITLE ) AS SELLER_RESP_TITLE , MAX( SELLER_RESP_OPIN ) AS SELLER_RESP_OPIN , MAX( SELLER_RESP_REG_DT ) AS SELLER_RESP_REG_DT , MAX( FB_POST_YN ) AS FB_POST_YN , MAX( PROD_ID ) AS PROD_ID , MAX( SELLER_MBR_NO ) AS SELLER_MBR_NO , MAX( CID ) AS CID , MAX( SVC_GRP_CD ) AS SVC_GRP_CD , MAX( AVG_SCORE ) AS AVG_SCORE , MAX( EXPO_SELLER_NM ) AS EXPO_SELLER_NM FROM ( SELECT N.TENANT_ID , N.NOTI_SEQ , N.TITLE , N.NOTI_DSCR , N.REG_DT , N.MBR_NO , N.REG_ID , N.MBR_TELNO , N.NOTI_SCORE , N.SELLER_RESP_TITLE , N.SELLER_RESP_OPIN , N.SELLER_RESP_REG_DT , N.FB_POST_YN , N.PROD_ID , P.SELLER_MBR_NO AS SELLER_MBR_NO , P.CID , P.SVC_GRP_CD , P.EXPO_SELLER_NM , NULL AVG_SCORE FROM TB_DP_PROD_NOTI N , TB_DP_PROD P WHERE N.TENANT_ID = #{tenantId} AND P.PROD_ID = N.PROD_ID AND N.MBR_NO = #{mbrNo}
AND N.CATALOG_ID IN
#{prodId}
AND N.PROD_ID = #{chnlId}
AND N.PROD_ID IN
#{prodId}
AND N.NOTI_TYPE_CD = 'DP000401' AND N.DEL_YN = 'N' AND N.BAD_NOTI_YN IN ( 'N', 'R' ) AND N.SPAM_YN = 'N'
UNION ALL SELECT MA.TENANT_ID AS TENANT_ID , NULL AS NOTI_SEQ , NULL AS TITLE , NULL AS NOTI_DSCR , MA.REG_DT AS REG_DT , MA.MBR_NO AS MBR_NO , NULL AS REG_ID , NULL AS MBR_TELNO , NULL AS NOTI_SCORE , NULL AS SELLER_RESP_TITLE , NULL AS SELLER_RESP_OPIN , NULL AS SELLER_RESP_REG_DT , NULL AS FB_POST_YN , MA.PROD_ID , NULL AS SELLER_MBR_NO , NULL AS CID , NULL AS SVC_GRP_CD , NULL AS EXPO_SELLER_NM , MA.AVG_SCORE FROM TB_DP_MBR_AVG MA WHERE MA.TENANT_ID = #{tenantId} AND MA.MBR_NO = #{mbrNo}
AND MA.PROD_ID IN
#{prodId}
) GROUP BY PROD_ID ORDER BY REG_DT DESC ) NOTI WHERE ROWNUM <= #{endRow} ) N WHERE N.RNUM BETWEEN #{startRow} AND #{endRow} ) N

/* FeedbackMapper.xml, getMyProdNotiCount, SAC : 김현일 , 2014-02-04 */
SELECT COUNT(*) AS TOTAL_COUNT
  FROM
     (
     SELECT MAX( PROD_ID ) AS PROD_ID
       FROM
          (
          SELECT N.PROD_ID
            FROM TB_DP_PROD_NOTI  N
               , TB_DP_PROD  P
           WHERE N.TENANT_ID = #{tenantId}
             AND P.PROD_ID = N.PROD_ID
             AND N.MBR_NO = #{mbrNo}
      
      
          
             AND N.CATALOG_ID IN
             
                 #{prodId}
             
          
         
              AND N.PROD_ID = #{chnlId}
         
      
      
      
          
             AND N.PROD_ID IN
             
                 #{prodId}
             
          
      
             AND N.NOTI_TYPE_CD = 'DP000401'
             AND N.DEL_YN = 'N'
             AND N.BAD_NOTI_YN IN ( 'N', 'R' )
             AND N.SPAM_YN = 'N'
      
      
          UNION ALL
          SELECT MA.PROD_ID
          FROM TB_DP_MBR_AVG MA
         WHERE MA.TENANT_ID = #{tenantId} 
           AND MA.MBR_NO = #{mbrNo} 
          
              
                 AND MA.PROD_ID IN
                 
                     #{prodId}
                 
              
          
        
          )
      GROUP BY PROD_ID
     ) NOTI

