mapper.select:
SELECT /* SearchKeywordMapper.xml, getSearchKeyword, SAC-전시기타/심대진/다모아솔루션, 2015-09-04 */ TOTAL_COUNT , OPERATION_DT , SEARCH_NM , SEARCH_DESC , SEARCH_CLSF_CD , ROWNUM AS RANK , RANK_VARIATION , RANK_VARIATION_CD , KEYWORD FROM ( SELECT COUNT(1) OVER () AS TOTAL_COUNT , TO_CHAR(B.OPERATION_DT, 'YYYYMMDDHH24MISS') AS OPERATION_DT , A.SEARCH_NM , A.SEARCH_DESC , A.SEARCH_CLSF_CD , B.RANK , B.RANK_VARIATION , B.RANK_VARIATION_CD , B.KEYWORD , B.UPD_DT FROM TB_DP_SEARCH A, TB_DP_SEARCH_KEYWORD B WHERE A.TENANT_ID = B.TENANT_ID AND A.SEARCH_ID = B.SEARCH_ID AND A.STD_DT = B.STD_DT AND A.TENANT_ID = #{tenantId}
AND B.SEARCH_ID = #{searchId}
AND A.SEARCH_CLSF_CD = #{searchClsfCd}
AND A.EXPO_YN = 'Y' AND B.EXPO_YN = 'Y' ORDER BY B.RANK ASC, B.UPD_DT DESC ) WHERE ROWNUM <= #{count}
