1.PROD_ID AND F1.PROD_ID = G1.PROD_ID AND A1.TENANT_ID = G1.TENANT_ID AND C1.PROD_ID = G1.PROD_ID AND C1.PROD_ID = J1.PROD_ID AND A1.TENANT_ID = #{tenantId} /* INPUT 테넌트ID */ AND A1.UP_MENU_ID ='MN26' /* MN26 : 웹툰 */ AND A1.SYSTEM_ID = #{systemId} /* INPUT 시스템ID */ AND A1.USE_YN = 'Y' /* Y : 사용(시용여부) */ AND B1.USE_YN = 'Y' /* Y : 사용(시용여부) */ AND C1.BASE_YN = 'Y' /* Y : 기본(기본여부) */ AND C1.USE_YN = 'Y' /* Y : 사용(시용여부) */ AND D1.SVC_GRP_CD = 'DP000203' /* DP000203 : 멀티미디어 */ AND F1.LANG_CD = 'ko' /* ko : 한국어 */ AND G1.PROD_STATUS_CD = 'PD000403' /* PD000403 : 판매중 */ AND G1.EXPO_YN = 'Y' /* Y : 노출(노출여부) */
AND D1.PROD_CHRG_YN =#{prodCharge} /* N 인경우 무료 */
AND D1.PROD_GRD_CD =#{prodGradeCd} /* PD004401:전체이용가,PD004402:12세이용가,PD004403:15세이용가,PD004404:청소년이용불가 */
AND D1.DRM_YN =#{drm} /* DRM 지원 구분 Y : 지원, N : 미지원*/
)WHERE NO =1 ) K ,TB_DP_LIST_PROD L ,TB_DP_LIST M WHERE A.TENANT_ID = B.TENANT_ID AND A.MENU_ID = B.MENU_ID AND A.SYSTEM_ID = B.SYSTEM_ID AND B.CATEGORY_NO = C.CATEGORY_NO AND C.PROD_ID = D.PROD_ID AND D.PROD_ID = E.PROD_ID AND E.PROD_ID = F.PROD_ID AND F.PROD_ID = G.PROD_ID AND A.TENANT_ID = G.TENANT_ID AND G.PROD_ID = H.PROD_ID AND G.TENANT_ID = H.TENANT_ID AND G.PROD_ID = I.PROD_ID AND G.TENANT_ID = I.TENANT_ID AND G.PROD_ID = J.PART_PROD_ID AND C.PROD_ID = L.PROD_ID AND A.TENANT_ID = L.TENANT_ID AND M.TENANT_ID = L.TENANT_ID AND M.LIST_ID = L.LIST_ID AND L.LIST_ID = #{listId} /* ADM000000001 : 운영자 추천 웹툰 */ AND G.PROD_ID = K.PART_PROD_ID(+) AND K.PART_PROD_ID IS NOT NULL AND A.TENANT_ID = #{tenantId} /* INPUT 테넌트ID */ AND A.SYSTEM_ID = #{systemId} /* INPUT 시스템ID */
AND A.MENU_ID = #{menuId} /* MN26001 : 웹툰>코믹,MN26002:순정,MN26003:드라마,MN26004:스릴러,MN26005:판타지,MN26006:액션 */
AND A.USE_YN = 'Y' /* Y : 사용(시용여부) */ AND B.USE_YN = 'Y' /* Y : 사용(시용여부) */ AND C.BASE_YN = 'Y' /* Y : 기본(기본여부) */ AND C.USE_YN = 'Y' /* Y : 사용(시용여부) */ AND D.SVC_GRP_CD = 'DP000203' /* DP000203 : 멀티미디어 */ AND F.LANG_CD = 'ko' /* ko : 한국어 */ AND G.PROD_STATUS_CD = 'PD000403' /* PD000403 : 판매중 */ AND G.EXPO_YN = 'Y' /* Y : 노출(노출여부) */ AND SYSDATE BETWEEN I.APPLY_START_DT AND I.APPLY_END_DT
AND D.PROD_CHRG_YN =#{prodCharge} /* N 인경우 무료 */
AND D.PROD_GRD_CD =#{prodGradeCd} /* PD004401:전체이용가,PD004402:12세이용가,PD004403:15세이용가,PD004404:청소년이용불가 */
